style.css:
@-moz-document domain("expressen.se") {
/* Expressen Dark Theme V0.6 */

/* Global colors */
:root {
	--bg-darker-color: #13131d;

	--bg-mid-dark-color: #20202b;

	--bg-base-color: #23232d;

	--bg-brighter-color: #272732;


	--fg-darker-color: #cfcfcf;

	--fg-base-color: #dfdfdf;

	--fg-brighter-color: #efefef;

	--fg-brighter-red-color: #fdd;
}

/* --------------------------------------------- */
* {
	background-color: var( --bg-base-color);
	color: var( --fg-base-color);	
}
/* --------------------------------------------- */

i.icon--tip-text
{
	color: green !important;
	background-color: white !important;
}

.factbox__wrapper,
.factbox__content,
.factbox__content h2.factbox__content-headline,
.factbox__content div.factbox__text,
.factbox__content div.factbox__text p
{
    background-color: var( --bg-brighter-color ) !important;
}

.body-text h2,
.standard-article,
.body-text,
main,
header,
html,
body,
aside.cc_cursor,
blockqoute,
#header,

.text-header__wrapper,
.body-text blockquote,
.body-text>p,
.scroll__load-more-button,
.top-section,
.cc_cursor .mosaic,
.site-menu ul,
.main,
.row,
.footer,
.footer__top,
.footer,
.footer__top,
.footer__info,
.right_column,
.article section,
.article-section-content,
.article,
.site-header__wrapper,
.main-header__top
{
	background-color: var( --bg-base-color) !important;
}

.main-header__top,
.scroll__load-more-button,
.page-footer,
html,
body,
header,
.main,
.right-column {
	border-color: var( --bg-brighter-color) !important;
}

.teaser .row__cell .teaser__hero,
.teaser {
	background-color: var( --bg-brighter-color) !important;
	color: var( --fg-base-color) !important;	
}

/* Page footer */
.footer-menu__link span,
.footer-menu__link,
.footer-meta__people,
.footer-meta__people strong {
	color: var( --fg-base-color) !important;
}

.footer-meta__people a {
	color: var( --fg-brighter-color) !important;
}

.scroll__section-title,
.right-column--aside,
.right-column--aside .right-column,
.footer-menu,
.page-footer {
	background-color: var( --bg-brighter-color) !important;
	color: var( --fg-base-color) !important;
}

/* Fact Box */
.factbox__expand,
.factbox__show-less,
.factbox__show-more {
	background-color: #444 !important;
}

i.icon {
	color: #dfdfdf !important;
}

.article__body-text mark {
	background-color: #43434d;
}

.poll__bar::after {
	background-color: red !important;
}

strong {
	color: var( --fg-brighter-red-color) !important;
}

span.bb--red {
	color: var( --fg-base-color) !important;
}

h2 span,
p,

.article__clipText,
.article__meta span.cc_cursor,
.article_header,
.article__body-text h2,
.article__header h1,

.article__body-text h3,
.article__body-text strong,

.poll__percentage,
.poll__answer,
.poll__questionnaire label span,

.factbox__show-less,
.factbox__show-more,

.brodtext1 strong,
.a-till-o__letter h2,
.page__header h1,
.page__header h2,
.html-widget strong,
.section-title,

.site-footer__copyright,
.site-footer__affiliates h4,
.site-footer__column h4,
.site-footer__column li,

.social-panel h3,

.teaser h1,
.teaser h2
{
	color: #dfdfdf !important;
}

.poll-submit-button.expressen-show-result,
.html-widget .votes button.poll-submit-button {
	background-color: var( --bg-brighter-color) !important;
	color: var( --fg-brighter-color) !important;
}

.html-widget .votes .cc_cursor,
.html-widget .votes .poll-answer span,
.html-widget .votes {
	background-color: var( --bg-darker-color) !important;
	color: var( --fg-base-color) !important;
}

.th-border--main {
	border: none;
}

.top-menu {
	background-color: #33333d;
}

.site-header__wrapper {
	background-color: #43434d;
	color: var( --fg-base-color) !important;
}

.page-list {
	background-color: var( --bg-mid-dark-color) !important;
}

.vignette {
	background-color: var( --bg-darker-color) !important;
	color: var( --fg-base-color) !important;
}

time {
	color: #cfcfcf !important;
}

.page-list__time-item__content div {
	color: #bfbfbf !important;
}

.page-list__more {
	color: #bfcfbf !important;
}

.breaking-news__item {
	background-color: #13131d;
	color: #efefef;
}

.breaking-news__label {
	background-color: #43434d;
	color: #efefef;
}

.slider__item h2 {
	color: var( --fg-base-color) !important;
}

.top-menu__wrapper::after {
	display: none !important;
}

.site-wrapper {
	border: none !important;
}

a {
	color: white;
}

.th-text {
	color: var( --fg-base-color) !important;
}

.bb--red {
	color: #efefef !important;
}

.icon--premium-text {
	color: white !important;
	background-color: gray !important;
}

.th-box--main {
	background-color: #23232d;
	color: var( --fg-base-color) !important;
}

.latest-post__author {
	color: #898989;
}

button {
	background-color: #23232d;
	color: var( --fg-base-color) !important;
}

.tag {
	background-color: #23232d;
	color: var( --fg-base-color) !important;
}

.columnist-byline > picture,
.section-title {
	border-color: #666;
}

.subscribe-button {
	background-color: #43434d;
	border-color: #53535d;
	color: var( --fg-base-color) !important;
}

.rotator {
	opacity: .8 !important;
}

blockquote {
	color: var( --fg-base-color) !important;
}

blockquote::before {
	color: #afafaf !important;
}

/* Video Player */
.video-pill {
	background-color: #43434d !important;
}

#play path {
	fill: #afafaf;
}

#play circle {
	fill: #1f1f1f;
}

.video-pill span {
	color: var( --fg-base-color) !important;
}

h1.editorial-featured-list__title,
div.mosaic__headline
{
	color: var( --fg-base-color) !important;
}

ul.mosaic figcaption,
.mosaic__info .mosaic__headline,
.editorial-featured__headline,
.editorial-featured-list,
.editorial-featured__info,
article.editorial-featured .editorial-featured__linkImage
{
	background-color: transparent !important;
}

/* FG Stuff */
blockquote,
main,
header,
html,
a,
em,
article,
aside.cc_cursor,
article.editorial-featured,
span.native-ad-banner__company-name b,
span.native-ad-banner__company-name,
span.native-ad-banner__ad-text,
li.bredcrumb-menu__menu-item a.breadcrumb-menu__menu-link,

#header,

.main-header__right .share-button,
.body-text p,
.site-menu ul li,
.site-menu li span,
.main,
.right_column,
.article section,
.article-section-content,
.row,
.site-header__wrapper,
.native-ad-banner,
.breaking-news span,
.page-list__item__content,
.discount-ad__footer,
.evp__controls__current-time,
.evp__controls__duration,
.latest-post__author,
.latest-post__headline,
.site-header__text span,
.rich-image__credit b,
.rich-image__credit,
.rich-image__description,
.evp__topbar__title,
.article__complaints,
.byline__contact span,
.rich-text strong,
.rich-text__block p,
.rich-text__read-more {
	color: var( --fg-base-color) !important;
}

/* AD Stuff */
.quality-branding {
	border-color: #999 !important;
}

.premium-link::before {
	background-color: #484 !important;
	color: #dfdfdf !important;
}

.native-ad-banner span {
	background-color: var( --bg-brighter-color) !important;
	color: var( --fg-brighter-color) !important;
}

.native-ad-banner {
	background-color: var( --bg-brighter-color) !important;
	color: var( --fg-base-color) !important;
}

/* Search Stuff */
div.page-list__item__content h2,

.search-page__hit-counter span,
.search-page__hit-counter,
.search-form__row span,
.search-form__label {
	color: var( --fg-base-color) !important;	
}

div.page-list__item__content p em,
div.page-list__item__content h2 em
{
	color: var( --fg-brighter-color) !important;	
	background-color: #888 !important;
}

.icon--magnifying-glass {
	background-color: var( --bg-brighter-color) !important;
}

}

@-moz-document domain("datawrapper.dwcdn.net") {
/* Expressen Dark Theme V0.6 */
/* External Chart/Statistics Site */

/* Global colors */
:root {
	--bg-darker-color: #13131d;

	--bg-mid-dark-color: #20202b;

	--bg-base-color: #23232d;

	--bg-brighter-color: #272732;


	--fg-darker-color: #cfcfcf;

	--fg-base-color: #dfdfdf;

	--fg-brighter-color: #efefef;

	--fg-brighter-red-color: #fdd;
}

body
{
    background-color: #aaa;
    background-color: var( --bg-base-color) !important;
}

.source-caption,
.source,
span.inner,
.chart-intro,
.chart-title,
.dw-chart-notes,
.bc-split span,
.row-label span
{
    color: var( --fg-base-color) !important;
}


}
