style.css:
@-moz-document domain("wccftech.com") {
header {
    filter: invert(70) !important;
}
h1.logo:hover{
    filter:brightness(0.1);
}
body {
    background-color: #101010;
    color: #777777;
}
a.featured > div{
    color:#dddddd;
}
a.featured:hover > div{
    color:#ffffff;
    filter:brightness(1.1);
}
a.featured > div:hover{
    color:#00ffff;
}
a.featured > div > h2:hover{
    color:#00ffff;
}
article.post > div > a > img:hover{
    color:#00ffff;
    filter:brightness(1.1);
}
article.post > div > h2 > a:hover{
    color:#00ffff;
}
article.post > div > div > a:first-child:hover{
    color:#00cccc;
}
.builder-widget{
    word-wrap:break-word;
    max-height:0px;
    visibility:hidden;
}
.page-heading {
    color:#cccccc;
}
.body > h2:nth-child(n){
    color:#cccccc;
    padding:15px;
}
.meta-author{
    color:#555555;
}
span.facebook:nth-child(2){
    visibility:hidden;
    word-wrap:break-word;
    max-height:0px;
}
.above-the-fold {
    background: #101010;
}
.content {
    background: #171717;
    padding: 30px;
}
.post:hover {
    background: #252525;
}
.topics > a{
    color:#00cccc;
}
.topics a:hover {
    color: #00ffff;
}
.body > p > a{
    color:#00cccc;
}
.body > p > a:hover{
    color:#00ffff;
}
a {
    color: #cccccc;
}
.trending-posts li {
    border-bottom: 1px solid #252525;
}
.shares:hover,
.sohail-share {
    background: #1f2529;
}
h1.sub-head{
    visibility:hidden;
    word-wrap:break-word;
    max-height:0px;
}
.sticky-hardware h2,
.sticky-gaming h2,
.sticky-mobile h2 {
    text-align: center;
}
.sticky-hardware > h2 > a:hover{
    color:#00ffff;
}
.sticky-gaming > h2 > a:hover{
    color:#00ffff;
}
.sticky-mobile > h2 > a:hover{
    color:#00ffff;
}
html body.home.blog main.wrap.clearfix aside.sticky.clearfix div.sticky-wrap section.sticky-hardware ul li.first h3.clamp2 a:hover{
    color:#00ffff;
}
html body.home.blog main.wrap.clearfix aside.sticky.clearfix div.sticky-wrap section.sticky-gaming ul li.first h3.clamp2 a:hover{
    color:#00ffff;
}
html body.home.blog main.wrap.clearfix aside.sticky.clearfix div.sticky-wrap section.sticky-mobile ul li.first h3.clamp2 a:hover{
    color:#00ffff;
}
.sticky-hardware > ul > li > h3 > a:hover{
    color:#00ffff;
}
  /* to get userstyles to refresh */
  /*
  .sticky-hardware > ul > li > h3 > a:hover{
    color:#00ffff;
}
  */
.sticky-gaming > ul > li > h3 > a:hover{
    color:#00ffff;
}
.sticky-mobile > ul > li > h3 > a:hover{
    color:#00ffff;
}
.sticky-hardware > ul:nth-child(n) > li:nth-child(n) > a:nth-child(n) > div:nth-child(n):hover{
    filter:brightness(1.1);
    
}
.sticky-gaming > ul:nth-child(n) > li:nth-child(n) > a:nth-child(n) > div:nth-child(n):hover{
    filter:brightness(1.1);
    
}
.sticky-mobile > ul:nth-child(n) > li:nth-child(n) > a:nth-child(n) > div:nth-child(n):hover{
    filter:brightness(1.1);
    
}
.sticky h2 span {
    border-radius: 4px;
    font-weight: 500;
    background: #101010;
    padding: 2px 10px 0px 10px;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-hardware > h2,
body > aside.sticky.wrap > section.sticky-hardware > h2,
body > main > aside:nth-child(2) > div > section.sticky-hardware > h2 {
    border-color: #00eeee;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-gaming > h2,
body > aside.sticky.wrap > section.sticky-gaming > h2,
body > main > aside:nth-child(2) > div > section.sticky-gaming > h2 {
    border-color: #45d87c;
}
body > div.above-the-fold > aside.sticky.wrap > div > section.sticky-mobile > h2,
body > aside.sticky.wrap > section.sticky-mobile > h2,
body > main > aside:nth-child(2) > div > section.sticky-mobile > h2 {
    border-color: #0fa6f3;
}
.sidebar section h3 span {
    background: #464646;
    color: #ffffff;
}
.sidebar section h3 {
    border-bottom: 2px solid #464646;
}
.exclusives h2 span {
    background: #ffffff;
    color: #000000;
    font-size: 1.325em;
}
.exclusives h2 {
    border-color: #ffffff;
}
.highlights {
    background: #101010;
}
.highlights h2 span {
    background: #101010;
    color: #cccccc;
    position: absolute;
}
.highlights h2 {
    border-color: #464646;
}
.body p,
.body ul,
.body ol,
.body table {
    color: #aaa;
}
.meta a {
    color: #00ffff;
}
.meta a:hover {
    color: #ffffff;
}
.meta-author,
.meta-left {
    border-color: #404040;
}
.meta-share-fb,
.meta-share-tw,
.meta-share-rd {
    visibility:hidden;
    word-wrap:break-word;
    max-height:0px;
}
.meta-left {
    width: calc(100% - 75px);
}
.body h1,
.body h2,
.body h3,
.body h4 {
    color: #6d6d6d;
}
.wp-caption-text {
    color: #a0a0a0 !important;
    background: #222222;
}
.wp-gallery a:first-child {
    max-width: 840px;
}
.wp-gallery.clearfix {
    min-width: 840px;
    margin-left: -30px;
}
.wp-caption.clearfix {
    max-width: 840px;
}
.wp-gallery.clearfix > a {
    border-color: #353535;
}
.tablepress-table-name{
    text-align:center;
}
.tablepress tr td:nth-child(n){
    background-color: #464646;
}
.tablepress tr td:first-child{
    background-color: #262626;
}
.tablepress tbody td {
    background-color: #464646;
}
.tablepress tfoot th{
    background-color: #464646;
}
.tablepress tfoot th:nth-child(n){
    background-color: #464646;
}
.tablepress thead th:nth-child(n){
    background-color: #353535;
}
.tablepress thead th:first-child{
    background-color: #262626;
}
.tablepress tbody tr td:nth-child(n){
    border-color: #353535;
}
.tablepress tfoot th,
.tablepress thead th {
    background-color: #272727;
}
.tablepress .row-hover tr:hover td{
    background-color: #464646;
}
.tablepress .row-hover tr:hover td:not(:first-child){
    background-color: #262626;
}
.dem-poll-title {
    color: #cccccc;
    background-color: #353535;
}
.democracy {
    margin-top:30px;
    background-color: #262626;
}
.democracy a{
    color:#00cccc;
}
.democracy a:hover{
    color:#00ffff;
    background-color:#222222;
}
.dem-vote > li > label:hover{
    color:#cccccc;
}
.democracy div .dem-button{
    color: #00cccc;
    background-color: #222222;
}
.democracy div .dem-button:hover{
    color: #00ffff;
    background-color: #181818 !important;
}
.dem-bottom{
    background-color: #353535 !important;
    margin-bottom:-20px;
}
span.facebook:nth-child(1){
    visibility:hidden;
    word-wrap:break-word;
    max-height:0px;
    padding: 0px;
    color: #00ffff;
    background-color: #464646 !important;
}
span.twitter:nth-child(2){
    visibility:hidden;
    word-wrap:break-word;
    max-height:0px;
    padding: 0px;
    color: #00ffff;
    background-color: #464646 !important;
}
span.reddit:nth-child(3){
    visibility:hidden;
    word-wrap:break-word;
    max-height:0px;
    padding:0px;
    color: #00ffff;
    background-color: #464646 !important;
}
.thumbnails-a .trc_rbox_header,
.thumbnails-a .syndicatedItem .video-title {
    color: #999999;
}
.lg-backdrop {
    background-color: #101010;
}
.lg-toolbar {
    background-color: #222222;
}
.lg-sub-html {
    background-color: #222222;
    color: #999999;
}
#lg-counter > div {
    filter: invert();
}
body > aside.sticky.wrap > div > section.sticky-hardware > h2 {
    border-color: #00eeee;
}
body > aside.sticky.wrap > div > section.sticky-gaming > h2 {
    border-color: #45d87c;
}
body > aside.sticky.wrap > div > section.sticky-mobile > h2 {
    border-color: #0fa6f3;
}
.sticky li.first a {
    min-width: 340px;
}
.section-head span {
    background: #464646;
    color: #ffffff;
}
.section-head {
    border-color: #464646;
}
.review-verdict {
    border-color: #fed432;
    background: #101010;
}
.review-verdict h3 {
    color: #45d87c!important;
    border-bottom-color: #444444;
}
.review-cons h3 {
    color: #00ffff!important;
}
.logo {
    filter: saturate(2) contrast(0.9) hue-rotate(330deg);
}
blockquote {
    border-color: #464646;
}
#jp-relatedposts h3.jp-relatedposts-headline {
    margin: 0px 270px 0.5em!important;
    font-size: 2em!important;
}
.size-large,
.size-full {
    min-width: 100%;
}
main {
    background-color: #101010;
    padding: 100px 50px;
}
.featured-posts {
    background-color: #000000;
}
.post,
.youtube-channel {
    border-color: #222222;
}
.post .text {
    padding-top: 13px;
    padding-left: 250px;
    padding-right: 10px;
}
.lSSlideOuter {
    background-color: #252525;
}
.review-info h3 {
    border-color: #333333;
    background: #333333;
}
.review-info,
.review-info div,
.review-info .right {
    border-color: #333333;
}
.feedback-button {
    background: #171717;
}
.popular-posts li a {
    color: #eeeeee;
}
p.wp-caption-text {
    width: 680px;
}
.wccf-gallery {
    background: #252525;
}
blockquote {
    background: #232323;
    color: #5c5c5c;
}
.single .sidebar section {
    margin-bottom: 50px;
}
body > main > section > div.body.clearfix > div > a {
    color: #aaaaaa!important;
}
.wccf_related_post a:hover,
.wccf_related_post a:active {
    background: #232323;
}
p.wccf_related_post{
    margin-left:-30px;
    width: calc(100%+30px);
    background-color: #232323;
}
a.wccf_related_post > strong:nth-child(1){
    color:#cccccc;
}
a.wccf_related_post > strong:nth-child(1):hover{
    color:#ffffff;
}
.wccf-stock-table tr:nth-child(odd) {
    background-color: #202020;
}
.wccf_related_post a {
    color: #00cccc !important;
}
.wccf_related_post a:nth-child(1):hover{
    color: #00ffff !important;
}
a.wccf_related_post:hover > strong:nth-child(1){
    color:#ffffff;
}
article.post > div > h3 > a:hover{
    color:#00ffff;
}
div.clearfix:nth-child(1)
{
    margin-top: -120px;
}
div.sidebar-youtube-video:nth-child(1)
{
    background-color: #009999;
}
div.sidebar-youtube-video:nth-child(1):hover
{
    background-color: #00cccc;
}
.wccf-product > div:nth-child(n) > small:nth-child(n)
{
    color: #00dddd;
}
.wccf-product > div:nth-child(n){
    color: #cccccc;
}
.wccf-products > h4:nth-child(n) > span:nth-child(n)
{
    background-color: #171717;
    color: #ffffff;
}
.wccf-product:hover
{
    filter:brightness(1.1);
    background-color: #222222;
}
.wccf-product > div:nth-child(n):hover > small:nth-child(n)
{
    color: #00ffff;
}
.wccf-product > div:nth-child(n):hover
{
    color: #dddddd;
}
.amazon-disclosure
{
    height: 0px;
    visibility:hidden;
}
.related-posts{
    margin-top: -50px;
}
}
