@-moz-document url-prefix("https://www.binadarma.ac.id/") {
/* colors */
:root {
    --bg: #2f3640;
    --bger: #434758;
    --bgest: #2A3039;
    --fg: #f5f6fa;
    --gray: #7f8fa6;
    --blue: #37b0ff;
    --bidarblue: #2271a3;
}

/* root */
body {
    color: var(--fg);
}

/* background */
.u_row.u_full_row{
    background: var(--bg)!important;
}
.container-fluid {
    background-color: var(--bg)!important;
}
.main-color-2-bg {
    background: var(--bidarblue);
}


/* 0content */

.path-login .card-header.text-center{
    color: var(--bger);
}

.item-content a {
    color: var(--blue);
