style.css:
/* i really want this to be global */
 #ns_navigation {
    background-color: #045de9 !important;
    background-image: linear-gradient(315deg, #045de9 0%, #09c6f9 74%) !important;
}
.ns-active {
    background-color: #f8ceec;
    background-image: linear-gradient(315deg, #f8ceec 0%, #a88beb 74%) !important;
}
#ns_navigation > .ns-menu > .ns-menuitem.ns-menuitem-selected > a {
    background-color: #f8ceec;
    background-image: linear-gradient(315deg, #f8ceec 0%, #a88beb 74%) !important;
    border : 1px solid white !important;
}
#ns_navigation > .ns-menu > .ns-menuitem.ns-menuitem-selected {
    border: 1px solid white !important;
}
span.uir-label .smallgraytext, span.uir-label .smallgraytextnolink, span.uir-label .smallgraytextbold, span.uir-label .smalltextnolink, span.uir-label .smalltextul, div.uir-field-wrapper span.uir-field.inputreadonly span.input {
    color: #6f6f6f !important;
    text-transform: capitalize;
    font-variant: small-caps !important;
    letter-spacing: 0.5px !important;
    font-size: large !important;
}
body, td, select, textarea, input, button {
    font-family:'Quicksand' !important;
}
#body_div * {
    font-size: large !important;
    color: #6f6f6f !important;
    text-transform: capitalize;
    letter-spacing: 0.8px !important;
}
.uir-button input[type=submit], .uir-button input[type=button], .uir-multibutton input[type=submit], .uir-multibutton input[type=button] {
    letter-spacing: 1px !important;
}
#ns_navigation * {
    letter-spacing: 1.3px !important;
}
body * {
    font-family:'Quicksand' !important;
    letter-spacing: 1px !important;
}
#div__body {
    -webkit-text-stroke: thin !important;
}
.dropdownInput::selection , span::selection , p::selection{
    background: #a8d1ff !important;
}
.dropdownInput::-moz-selection , span::-moz-selection , p::-moz-selection{
    background: #a8d1ff !important;
}
