style.css:
@-moz-document url-prefix("https://archives.rigsofrods.net/repo/") {
/* single column file list */
.filemanager .data li {
    width: 100%!important;
}

/* show filename overflow, move filename up */
.filemanager .data li .name {
    overflow: visible!important;
    top: 20px!important;
}

/* move filesize up */
.filemanager .data li .details {
    top: 44px!important;
}

/* make each file shorter height-wise */
.filemanager .data li {
    height: 78px!important;
}

/* shrink the file icon*/
.icon.file {
    height: 2em!important;
    line-height: 2em!important;
    margin: 0.6em 1.2em 0.8em 1.3em!important;
}

/* shrink the folder icon */
.icon.folder.full::after {
    height: 1.15em!important;
}

/* make skinzips stand out more */
.icon.file.f-skinzip {
    box-shadow: 1.74em -2.1em 0 0 #255CB7 inset!important;
}

/* make the search box be always visible */
.filemanager .search input[type="search"] {
    display: inherit !important;
}


}
