style.css:
:root{
	/* change the color you want eg: --background: green; */
        --background: black;
        /* i really want this to be global */
}
Body{
        background-color: var(--background) !important;
        font-family:Georgia, Cambria, "Times New Roman", Times, serif;
        color:#ced7db;
        font-size:72.5%;
        font-weight:400;
        line-height:1
    }
    
    div.grid {
    background-color: var(--background);

    }
    
    .wrapper {
    border-top:1px solid var(--background);
    border-bottom:1px solid var(--background);
    background:var(--background);
    display: block;
    border: 1em solid var(--background);
    box-shadow: 0 0 5px rgba(17, 17, 17, .2),inset 0 0 0 1px #285867;
    }
    
	a.review figcaption {
 position:absolute;
 top:auto;
 bottom:0;
 left:0;
 right:0;
 -moz-transition:all 0.2s;
 -o-transition:all 0.2s;
 -webkit-transition:all 0.2s;
 transition:all 0.2s;
 padding:10px;
 background:#285867;
 background:rgb(41, 87, 102,.4);
 color: #ffff;
 
}

    div.grid.fixed-rail > section.main.pad.grid > form#filter.widget {
        background: var(--background);
        border: 2px solid #285867;
    }
 
    .fixed-rail .pad {
         width:100%;
        padding-right:330px
    }
    
	
	.review-list .slideWrap .slide ,
	.review-list .slideWrap .slide a {
		background-color: var(--background);
		
	}
	.review-list .slideWrap .slide a:nth-child(even) {
 		background:var(--background);
	}

	
	.review-list .slideWrap .slide a:hover,
	.review-list .slideWrap .slide a:focus {
	 	background-color:#285867;
		border: 5px solid var(--background);
	  	
	}
	
	
	
	.review-list .slideWrap .slide a h6 {
 		font-family:Georgia, Cambria, "Times New Roman", Times, serif;
 		font-style:normal;
 		font-weight:400;
 		-moz-transition:all 0.2s;
 		-o-transition:all 0.2s;
 		-webkit-transition:all 0.2s;
        transition:all 0.2s;
 		color:#285867;
 		margin:0
 
}
	.slideWrap .carousel-tabs {
 		text-align:center;
 		margin:0;
		padding:1em 0;
		background:var(--background);
		border:1px solid var(--background);
		border-bottom:0
}

	.card a:hover {
		background-color:#285867;
		border: 5px solid var(--background);
	}

	.card {
		 float:left;
		 width:31.3333333333333%;
		 font-size:0.85em;
		 padding-bottom:10px;
		 margin:3% 0 0 3%;
		 background-color: var(--background);
		 border: 1px solid #ebeeee;
	}
	
	.featured-cards {
 		padding:0 0 2em;
	  	border-bottom:20px solid var(--background); 
		}

	 section.main.pad section.featured-banner a img{
		
		border: 1px solid #285867
	}
	
	
	#home .main .featured {
 			-moz-box-shadow:inset 0 0 0 1px var(--background);
 			-webkit-box-shadow:inset 0 0 0 1px var(--background);
 			box-shadow:none;
 			background:rgb(41, 87, 102,.4);
 			margin:1em 0em 3em;
}

	
    div.grid.fixed-rail > section.main.pad > article.featured {
        background-color: var(--background);
		border: 1px solid #285867;
    }
	
	div.grid.fixed-rail > section.main.pad > article.featured > section.summary {
		background-color: var(--background);
		border: 0.1px solid #285867;

	}
	
    div.grid.fixed-rail > section.main.pad > article.featured > section.summary > p {
        
        background-color: var(--background);
		height: 72px
	}


    .details > ul:nth-child(3) > li:nth-child(2) {
    background-color: var(--background);
    }
    
    div.grid.content > section.main.fixed-rail > article.pad.entry > section.share{
    display: none;
    } 

    div.wrapper > h2.page-head {
        display: none;
    }
    
    a.memoriam {
        display: none;
    }

    div.grid.content > section.main.fixed-rail > aside.place {
        display: none;
    }
    
    footer.nocontent{
        display: none;
    }
    
    header > div.inner > nav > form#search > input {
        background-color: var(--background);
        border: 1px solid var(--background);
		border-radius: 1px;
    }

    header > div.inner > nav > form#search > input#q {
    
    border: 1px solid #285867;
	box-shadow: none;
    padding: 0;
    vertical-align: top;
    }
    
    div.grid.content > aside.channels-sidebar > a.rss{
        display: none;
    }
    
    div.grid.content > aside.movie-sidebar > section.details {
        background-color: var(--background);
        
    }
    
    div.grid.fixed-rail > aside.place > div.ebert_club_signup {
        display: none;
    }
    
    div.grid.fixed-rail > aside.place > section.mailing-list{
        display: none;
    }
        
    div.grid.content > aside.channels-sidebar > section.review-list{
        display: none;
    }
    
    div.grid.content > section.main.fixed-rail > article.pad.entry > div.whats-hot {
        display: none;
    }

	#review .movie-sidebar section.details ul li  {
 	margin-left:0;
 	
 	border: none;
 	background:var(--background);
	
	}
	#review .movie-sidebar h5 {
	 margin-bottom:0;
	 padding:5px 10px;
	 background:var(--background);
	 color:#729999;
	 border-left: 1px solid #ffff;
	 border-right: 1px solid #ffff;
	 border-top:1px solid #ffff;
	
	}
	

	
	.details > h4:nth-child(1) {
	 background: #1f7a99;
	 border: 23px solid var(--background);
	}
	#review .movie-sidebar h4 {
	 margin-bottom:0;
	 padding:5px 10px;
	 background:var(--background);
	 color:#1f7a99;
    }


	.movie-poster > img:nth-child(1) {
 	border: 1px solid #285867;
	}

	#review .more-details {
		 clear:left;
		 padding:1em 1em 0.5em;
		 background:var(--background);
		 color:#729999;
		 border: 1px solid #fff
	}
	
	
	#review .content figure figcaption  {
 		display: none;
		
	}
	#review .content figure{
		border: 1px solid #285867;
			
	}
 
    div.wrapper > div.grid.fixed-rail > section.main.pad.grid > form#filter > h4 {
		background-color: var(--background);
	}	
	
	div.wrapper > div.grid.fixed-rail > section.main.pad.grid > form#filter.widget > section#filters > fieldset.col-1-3 > div.controls > input {
  		background-color: rgb(15, 14, 14) !important;
		border: 1px solid #285867;
	}
	
	form#filter {
		 background:var(--background);
		 font-family:"Helvetica Neue", Helmet, Freesans, sans-serif;
		 font-style:normal;
		 font-weight:400;
		 padding:1em 0 1em 1em;
		 margin-top:0;
		 margin-bottom:20px;
		 width:100%;
		 text-align:left;
		 border:1px solid #285867;
}
