style.css:
@-moz-document domain("cronometronline.com.br") {
body {
    background: black;
}

#principal {
    background: #202023;
    border: 5px solid #2f2f2f;
}

#acoes #esc_button {
    background: #373737;
    float: right;
    margin: 0 70px 0 0;
    color: #dedede;
}

#acoes #stop_button {
    background: #5e1e1e;
    float: left;
    margin: 0 0 0 70px;
    color: #dedede;
}

.cronometro #difference {
    visibility: visible;
    font-size: 120px;
    color: #31849d;
    text-shadow: 1px 1px 1px #40c440;
}

#quemfaz {
    display: none;
}

#twitter {
    display: none;
}
}
