style.css:
@-moz-document url-prefix("https://context.reverso.net") {

body, input, button, input#entry, .wrapper-main, #footer, #container, .content-main, #wrapper, #results .languageBox.source, #bitexte .languageBox.source, #results .languageBox, #bitexte .languageBox, div.rating, .languageRow.row2, .languageRow.row1, .languageRow.selected, div#ui-tooltip-23, #footer .footerBlue, div#dialog-loading, #examples-content .example:hover, div.src, div.trg, div.options, .popup .body, #keyboardInputMaster {
  background: #222222 !important;
}

h1, h2, span, dd, i, li, p, div, ul.ul_grouptranslation li.menu entry, div.suggestion li.menu entry, #translation-box {
  color: #9A9A9A !important;
}

#header .title-head h2, #container h3, #container h2.title-h2, .join-reverso h4 {
  color: #4183C4;
}

th {
  color: #C2AE84;
}

a {
  color: #000000 !important;
}

b, a:visited, #top-results .detailed-view, #search-input .icon:hover {
  color: #ccc !important;
}

a:link {
  color: #ccc !important;
  font-weight: bold !important;
}

#top-content a:hover {
  color: #ccc !important;
  text-decoration: underline;
  text-shadow: none !important;
}

.icon.twitter, #top-menu .option .icon, #user-menu .drop-down .option .icon, #user-menu .drop-down .option, #subtop-content .icon.button, .reverso-logo .logo .reverso-logo-red, .reverso-logo .text .reverso-text, .reverso-logo .text .context-text, .reverso-logo, #examples-content, #search-links .icon {
  text-decoration: none !important;
  border: none !important;
}

#lang-selector .front, #add-example {
  background: #181818 !important;
  border: 1px solid #b7b7b7;
}

#top-content, #search-content, #latest-history-content, #suggested-searches-content, #corporate-banner, .selector .option, .selector .drop-down, .selector .front, .loading-pannel, #top-content .reverso-logo, #splitting-content .trgs .translation, .popup .header, .popup button, .ui-autocomplete, #translations-content .translation, #filtered-entry, .controls, .results .header .column.sortable, .noticed #notice-content {
  background: #181818 !important;
  border: none !important;
  box-shadow: none !important;
}

#latest-history-content .history-entry.even, .tooltip, .results .column.search, .results .column.details, .results .column.pair {
  background: #1c1c1c !important;
}

#latest-history-content .history-entry:hover, .selector .option:hover {
  background: #252525 !important;
}


#container .content-main a:hover {
  text-decoration: underline !important;
}

input, td, small, big, strong, ol, #keyboardInputMaster span, #grouptranslation li.menu, #examples-content .other-result, #load-more-examples {
  background-color: #181818 !important;
  border: 1px solid #222222 !important;
  color: #9A9A9A !important;
}

button, a:hover, a:hover span, #search-input .icon {
  color: #9A9A9A !important;
}

li:hover {
  color: #9A9A9A !important;
  background: none !important;
}

#search-input .icon.cancel:hover {
  color: #d13321;
}

#search-input input, #pair-selector .selector .option {
  border: 2px solid #1c1c1c !important;
  color: #a7a7a7;
}

#pair-selector .space-filler {
  width: 0;
}

#pair-selector .beta-flag {
  background: linear-gradient(90deg,transparent,#383838);
}

#pair-selector .swap-content .icons {
  background-color: #252525;
}

#search-button {
  background: #222222;
  color: #9a9a9a;
}

#search-button:hover {
  background: #252525;
}

#suggested-searches-content .separator, #suggested-searches-content .lang-title {
  border-bottom: 1px solid #9a9a9a;
}

div.src, div.trg {
    margin-bottom: 10px !important;
}

em {
  background: #222222 !important;
  text-shadow: 2px 2px 8px #F09420;
  text-decoration: underline;
  color: #9a9a9a !important;
}

div.badges, #top-menu.open .space-filler, #synonyms-promotional-bar, #extension-badge, #home-badges {
  display: none;
}

.selector .front {
  border-radius: 0;
}

#result-info p {
    opacity: 1;
}

#latest-history-content {
    width: 100%;
}

.half-width-wrapper.image-text-wrap > img {
    filter: invert(70%) grayscale(1);
}

#latest-history-content .encourage-content
{
    background-color: #181818;
}  

}
