style.css:
/* i really want this to be global */

:root {
  --button-default: rgba(9,30,66,.04);

  --custom-green: #5be7c4;
  --custom-purple: #7a57d1;
  --custom-blue: #4fc1e9;
  --custom-gray: #f5f7fa;
  --custom-red: #ff4870;
  --custom-yellow: #F9F871;
  --custom-light-red: #ff4870;
  --custom-dark: rgba(0,0,0,.65);

--theme-lime: #0094A4;
--theme-red: #ff4870;
--theme-yellow: #DB4FB7;
--theme-orange: #FF618F;
--theme-purple: #FF906A;
--theme-blue: #FF6E59;
--theme-pink: #00977B;
--theme-sky: #008EC9;
--theme-green: #7A57D1;
--theme-black: #215D6D;
--theme-default: #8888aa;
}

div#trello-root nav#header {
    background: #0071d5;
}

div#trello-root.body-board-view {
    background: #fff!important;
}
div#trello-root.body-board-view .list {
    background: #fff;
    border: 1px solid rgba(0,0,0,.1);
    box-shadow: 0 5px 5px rgba(0,0,0,.075);
    overflow: hidden;
}

div#trello-root.body-board-view .list-cards::-webkit-scrollbar-button,
div#trello-root.body-board-view .list-cards::-webkit-scrollbar-track,
div#trello-root.body-board-view .list-cards::-webkit-scrollbar {
  width: 2px;
  background: #fff!important;
  box-shadow: none;
}
div#trello-root.body-board-view .list-cards::-webkit-scrollbar-thumb {
  background: var(--custom-purple)!important;
}

div#trello-root.body-board-view .list-cards {
    margin: 0;
}
div#trello-root.body-board-view a.list-card {
    box-shadow: none;
    border-bottom: 1px solid #ddd;
}
div#trello-root.body-board-view div#header {
    background: #fff;
    border-bottom: 1px solid #ddd;
}
div#trello-root.body-board-view div#header * {
    color: #333!important;
}
div#trello-root.body-board-view input[data-test-id="header-search-input"] {
    border: 1px solid #ddd;
}
div#trello-root.body-board-view button[data-test-id="header-notifications-button"] {
    background: var(--custom-green);
}
div#trello-root.body-board-view #header > a > div > span:first-child {
    opacity: 1;
    z-index: 999;
    letter-spacing: 1px;
}
div#trello-root.body-board-view #header > a > div > span:first-child:before {
    content: 'Trello';
    color: #aaa!important;
    white-space: nowrap;
    top: -1px;
    position: relative;
}

div#trello-root.body-board-view .board-header {
    color: #333;
    background: #fff;
    margin-bottom: 6px;
    display: flex;
}
div#trello-root.body-board-view .board-header > * {
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
}
div#trello-root.body-board-view .board-header * {
    background: transparent;
    color: #333;
}
div#trello-root.body-board-view #header [data-test-id="header-boards-menu-button"] span:first-child {
    display: none;
}
div#trello-root.body-board-view #header [data-test-id="header-boards-menu-button"] span:last-child {
    font-weight: 400;
}

div#trello-root.body-board-view .board-header span.js-board-editing-target {
    color: var(--custom-purple)!important;
}
div#trello-root.body-board-view .board-header-btn-divider {
    background: #ddd;
    height: 1em;
}
div#trello-root.body-board-view .board-header .member-type.admin {
    background: var(--custom-red);
    border: 2px solid #fff;
    border-radius: 50%;
    transform: translate3d(2px,2px,0);
}

div#trello-root.body-board-view .badge.is-unread-notification {
    padding: 0;
    margin-top: 2px;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    text-align: center;
    background: var(--custom-red);
    border: none;
    color: #fff;
}
div#trello-root.body-board-view .badge.is-unread-notification .badge-icon {
    display: none;
}

div#trello-root.body-board-view .window {
    background: #fff;
    margin: 0;
    width: 80%;
    max-width: 1280px;
}

div#trello-root.body-board-view .window .window-main-col {
    width: calc( 100% - 250px );
}

div#trello-root.body-board-view .quick-card-editor {
  background: #7a57d1ee;
}
div#trello-root.body-board-view .quick-card-editor input[type="submit"] {
  background: var(--custom-green);
  color: var(--theme-black);
}
div#trello-root.body-board-view .quick-card-editor-buttons-item {
  background: transparent;
}

div#trello-root.body-board-view .button-link .on {
  background: var(--custom-green);
}
div#trello-root.body-board-view .button-link .on span {
  color: #333!important;
}
div#trello-root.body-board-view textarea.mod-list-name {
    font-weight: 900;
    text-transform: uppercase;
    letter-spacing: 1px;
    font-size: 85%;
}


div#trello-root.body-board-view  .card-label {
    background: transparent!important;
    border: 1px solid;
    color: var(--theme-default);
    border-radius: 0!important;
    text-align: center;
    min-width: auto;
    padding: 0;
    line-height: 0;
    display: flex;
    justify-content: center;
    align-items: center;
}
div#trello-root.body-board-view  .card-label .label-text {
    padding: 2px!important;
    margin:0!important;
    font-weight: 500!important;
    letter-spacing: .05rem!important;
}
div#trello-root.body-board-view  .card-label:last-child {
    margin-bottom: .75em;
}

div#trello-root.body-board-view  .card-label-green {
    color: var(--theme-green)!important;
}
div#trello-root.body-board-view  .card-label-yellow {
    color: var(--theme-yellow)!important;
}
div#trello-root.body-board-view  .card-label-orange {
    color: var(--theme-orange)!important;
}
div#trello-root.body-board-view  .card-label-red {
    color: var(--theme-red)!important;
}
div#trello-root.body-board-view  .card-label-purple {
    color: var(--theme-purple)!important;
}
div#trello-root.body-board-view  .card-label-blue {
    color: var(--theme-blue)!important;
}
div#trello-root.body-board-view  .card-label-pink {
    color: var(--theme-pink)!important;
}
div#trello-root.body-board-view  .card-label-sky {
    color: var(--theme-sky)!important;
}
div#trello-root.body-board-view  .card-label-lime {
    color: var(--theme-lime)!important;
}
div#trello-root.body-board-view  .card-label-black {
    color: var(--theme-black)!important;
}


div#trello-root.body-board-view  .card-label.active {
    background: var(--theme-default)!important;
}
div#trello-root.body-board-view  .card-label-green.active {
    background: var(--theme-green)!important;
}
div#trello-root.body-board-view  .card-label-yellow.active {
    background: var(--theme-yellow)!important;
}
div#trello-root.body-board-view  .card-label-orange.active {
    background: var(--theme-orange)!important;
}
div#trello-root.body-board-view  .card-label-red.active {
    background: var(--theme-red)!important;
}
div#trello-root.body-board-view  .card-label-purple.active {
    background: var(--theme-purple)!important;
}
div#trello-root.body-board-view  .card-label-blue.active {
    background: var(--theme-blue)!important;
}
div#trello-root.body-board-view  .card-label-pink.active {
    background: var(--theme-pink)!important;
}
div#trello-root.body-board-view  .card-label-sky.active {
    background: var(--theme-sky)!important;
}
div#trello-root.body-board-view  .card-label-lime.active {
    background: var(--theme-lime)!important;
}
div#trello-root.body-board-view  .card-label-black.active {
    background: var(--theme-black)!important;
}
div#trello-root.body-board-view  .card-label-black.active {
    background: var(--theme-black)!important;
}

div#trello-root.body-board-view .card-labels-hover .card-label,
div#trello-root.body-board-view .card-label.selected {
    box-shadow: none!important;
    margin-left: 0!important;
}

div#trello-root.body-board-view .card-label.active {
    box-shadow: none!important;
    margin-left: 0!important;
    color: #fff!important;
}



div#trello-root.body-board-view .badge {
    background-color: transparent;
    color: #333;
}
div#trello-root.body-board-view .badge.is-voted {
    background-color: var(--custom-green);
    border-radius: 2px;
    padding: 2px 7px;
}
div#trello-root.body-board-view .badge.is-voted span.badge-icon {
    display: none;
}
div#trello-root.body-board-view .badge.is-voted span {
    padding: 0;
    margin: 0;
    width: auto;
    font-weight: 500;
    color: var(--custom-dark);
}
div#trello-root.body-board-view .badge.is-voted span:before {
    content: '+';
}



div#trello-root.body-board-view .badge-icon {
    color: var(--custom-dark)!important;
}
div#trello-root.body-board-view .badge-icon.icon-subscribe {
    color: var(--custom-purple)!important;
    background: var(--custom-yellow);
    border-radius: 3px;
}
div#trello-root.body-board-view .js-due-date-badge {
    border-radius: 0;
    padding: 0!important;
    background: transparent; 
    display: block;
}
div#trello-root.body-board-view .js-due-date-badge:after {
    font-weight: 900;
    font-size: 80%;
    letter-spacing: .025rem;
    padding-left: 4px;
    position: relative;
    top: -1px;
}
div#trello-root.body-board-view .js-due-date-badge.is-due-soon {
    color: var(--theme-sky);
}
div#trello-root.body-board-view .js-due-date-badge.is-due-now {
    color: var(--theme-purple);  
}
div#trello-root.body-board-view .js-due-date-badge.is-due-past {
    color: var(--theme-red);
}
div#trello-root.body-board-view .js-due-date-badge.is-due-soon:after {
    content: 'GO TO GATE';
}
div#trello-root.body-board-view .js-due-date-badge.is-due-now:after {
    content: 'BOARDING';
}
div#trello-root.body-board-view .js-due-date-badge.is-due-past:after {
    content: 'PLANE TOOK OFF';
}
div#trello-root.body-board-view .badge-text {
    padding: 0;
}
div#trello-root.body-board-view .js-due-date-badge .badge-text {
    display: none;
}
div#trello-root.body-board-view .js-due-date-badge .badge-icon {
    color: inherit!important;
}
div#trello-root.body-board-view .card-detail-due-date-badge .card-detail-due-status-lozenge {
    padding: 1.5em;
    color: #fff;
    font-weight: 700;
}
div#trello-root.body-board-view .card-detail-due-date-badge.is-due-soon .card-detail-due-status-lozenge {
    background: var(--theme-sky);
}
div#trello-root.body-board-view .card-detail-due-date-badge.is-due-now .card-detail-due-status-lozenge {
    background: var(--theme-purple);
}
div#trello-root.body-board-view .card-detail-due-date-badge.is-due-past .card-detail-due-status-lozenge {
    background: var(--theme-red);
}





div#trello-root.body-board-view span.card-label {
    height: auto;
    width: auto;
    max-width: none;
    border-radius: 2px;
    overflow: hidden;
}
div#trello-root.body-board-view span.card-label .label-text {
    display: block;
    font-size: 10px;
    line-height: 1;
    padding: 5px;
    background: transparent;
    text-transform: uppercase;
    letter-spacing: .04rem;
    font-weight: 500;
}
div#trello-root.body-board-view .pop-over form.dpicker-widget button:not(.negate) {
    background-color: transparent;
    color: #333;
}
div#trello-root.body-board-view .pop-over button:not(.negate) {
    background-color: var(--button-default);
    color: #333;  
}
div#trello-root.body-board-view button.primary, 
div#trello-root.body-board-view button.primary, 
div#trello-root.body-board-view input[type=button].primary, 
div#trello-root.body-board-view input[type=submit].primary {
    background-color: var(--custom-green);
    color: #333;
}

div#trello-root.body-board-view .pop-over button.negate,
div#trello-root.body-board-view button.negate, 
div#trello-root.body-board-view button.negate, 
div#trello-root.body-board-view input[type=button].negate, 
div#trello-root.body-board-view input[type=submit].negate {
    background-color: var(--custom-red);
    color: #fff;
}

div#trello-root.body-board-view .iframe-input.focus, 
div#trello-root.body-board-view input[type=email]:focus, 
div#trello-root.body-board-view input[type=password]:focus, 
div#trello-root.body-board-view input[type=search]:focus, 
div#trello-root.body-board-view input[type=text]:focus, 
div#trello-root.body-board-view textarea:focus {
    box-shadow: inset 0 0 0 2px var(--custom-purple);
}
div#trello-root.body-board-view .check-div input[type=checkbox]:checked+label:before {
    background-color: var(--custom-purple);
    box-shadow: inset 0 0 0 2px var(--custom-purple);
    text-indent: 0;
}
div#trello-root.body-board-view .list-card-quick-edit textarea,
div#trello-root.body-board-view .is-editing textarea {
    box-shadow: none;
}

div#trello-root.body-board-view .card-composer-container {
    opacity: .65;
}
div#trello-root.body-board-view .card-composer-container:hover {
    opacity: 1;
}

div#trello-root.body-board-view .pika-single .is-selected .pika-button {
    color: #fff!important;
    background: var(--custom-purple)!important;
}
div#trello-root.body-board-view .pika-single .is-today .pika-button {
    background: var(--button-default)!important;
    color: #333;
}
div#trello-root.body-board-view .pika-single .pika-table td {
    background: transparent!important;
}



div#trello-root.body-board-view .checklist-progress-bar-current,
div#trello-root.body-board-view .checklist-item-fade-out .checklist-item-checkbox,
div#trello-root.body-board-view .checklist-item-state-complete .checklist-item-checkbox,
div#trello-root.body-board-view .comment-controls .comment-subscribe.is-subscribed .comment-subscribe-box {
    background-color: var(--custom-purple);
    box-shadow: none;
}

div#trello-root .window-sidebar {
    float: left;
    padding-left: 56px;
}
