html {
    --scrollbarBG: #202225;
    --thumbBG: #66686D;
}

html::-webkit-scrollbar {
    width: 11px;
}

html {
    scrollbar-width: thin;
    scrollbar-color: var(--thumbBG) var(--scrollbarBG);
}

html::-webkit-scrollbar-track {
    background: var(--scrollbarBG);
}

html::-webkit-scrollbar-thumb {
    background-color: var(--thumbBG);
    border-radius: 6px;
    border: 3px solid var(--scrollbarBG);
}

textarea {
    width: 98.6%!important;
    height: auto;
}

/* Poll */
.pun .poll {
    margin-top: 2em;
    padding-bottom: 1em;
    background-color: #2A2A2A;
    border: 1px solid #857B63;
}

.poll legend {
    font-weight: normal;
}

.poll p {
    font-weight: bold;
}

.pun .poll ol {
    list-style: none outside none;
    padding: 0px;
}

.pun .poll ol label {
    padding: 0px;
    background-color: #F9F5D1;
    border: 0 none;
}

.pun .poll li {
    padding: 0;
    margin-bottom: 0.5em;
}

.pun .poll label {
    padding: 0px;
}

.poll .percent {
    font-size: 0.8em;
    margin-left: 10px;
}

.poll .progressbar {
    height: 10px;
    border: 1px solid #365925;
    width: 60%;
}

.poll .progressbar span {
    background-color: #87BF2C;
    width: 100%;
    height: 10px;
    display: block;
    font-size: 0;
    overflow: hidden;
}

.poll .progressbar span span {
    display: none;
}

.poll .total {
    color: #515151;
    margin-top: 0.5em;
    font-style: italic;
}

.poll .poss {
    color: #959595;
    font-style: italic;
}

.pun .poll_mess {
    font-weight: bold;
    text-align: center;
}

.pun .pollbut {
    font-size: 0.8em;
    padding: 7px 0 0 30px;
}

.pun .pollbut input {
    margin-right: 12px;
}

/* Growl messages */
#growlcontainer {
    position: fixed;
    right: 20px;
    top: 20px;
    width: 500px;
    font-size: 12px;
    z-index: 1000;
}

#growlcontainer p {
    font-family: Arial, sans-serif;
    margin: 0;
}

.growlstatus {
    position: relative;
    color: #000;
    min-height: 48px;
    /* icon height */
    padding: 10px;
    margin-bottom: 5px;
    background-color: #F1EDCA;
    border: 1px solid #888;
    opacity: 0.8;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
}

.growlstatus:hover {
    border-color: #777;
    opacity: 0.9;
}

.growlstatus .growltitle {}

.growlstatusclose {
    visibility: hidden;
    float: right;
    width: 16px;
    height: 16px;
    cursor: pointer;
    margin: 0 0 3px 5px;
}

.growlstatus:hover .growlstatusclose {
    display: block;
    visibility: visible;
}

.growlwithicon {
    padding-left: 68px;
    background-position: 10px 10px;
    background-repeat: no-repeat;
}

/* Spoiler_tag */
.spoiler_wrap {
    padding: 0px 5px !important;
    border: 1px solid #66686D !important;
    border-radius: 6px;
    background-color: rgba(114, 136, 217, 0.24);
    moz-box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
    -webkit-box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
    box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
}

.spoiler_head {
    cursor: pointer;
    color: rgba(255, 255, 255, 0.9);
    font-weight: bold;
}

.spoiler_head span {
    font-weight: normal;
}

.spoiler_text {
    padding-bottom: 5px !important;
    display: none;
    color: rgba(255, 255, 255, 0.800);
    cursor: auto;
}

/* Emoticons */
#post .txtarea textarea {
    line-height: 1.4em;
    /* x20 */
}

#smilies_box {
    width: 100%;
    float: bottom;
    margin-top: 30px;
    text-align: center;
    overflow-x: hidden;
    overflow-y: auto;
    height: 120px;
    /*+border-radius: 6;*/
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    -khtml-border-radius: 6px;
    border-radius: 6px;
    background-color: rgba(0, 0, 0, 0.480);
    border: 1px solid #66686D;
    padding-top: 6px;
}

#rules-block {
    font-size: medium;
    padding: 5px;
}

#smilies_box ul {
    font-size: medium;
    height: auto;
}

#smilies_box li {
    display: inline;
    cursor: pointer;
}

.modlist {
    display: none;
}

input[type=text],
input[type=password],
textarea {
    border: 1px solid #66686B;
    background-color: rgba(0, 0, 0, 0.480);
    border-radius: 6px;
    width: auto;
    color: #FFFFFF;
    height: auto;
    padding: 6px;
    margin-top: 5px;
}

form#cse-search-box input {
    color: #4d4d4d;
    font-weight: bold;
    padding: 6px;
}

.pun input[type=submit] {
    cursor: pointer;
    color: #FFFFFF;
    font-size: small;
    font-weight: bold;
    background-color: rgba(114, 136, 217, 0.8);
    border: 1px solid #66686D;
    border-radius: 6px;
    padding: 4px;
}

.pun input[type=submit]:hover {
    color: #FFFFFF;
    text-decoration: underline;
}

.pun .postheadline {
    font-size: large;
}

.pun .postheadline a {
    color: #FFFFFF;
}

.blockpost .username img {
    vertical-align: middle;
}

.blockpost .usertitle strong {
    font-weight: normal;
}

body > .pun {
    min-width: 960px;
    max-width: 1200px;
    margin: 12px auto;
}

.pun span.bbs {
    text-decoration: line-through;
}

.pun .postmsg {
    font: normal medium / 1.75em verdana, arial, sans-serif;
}

.pun .postsignature p {
    max-height: 7.5em;
    line-height: 1.5em;
    padding: 0;
    overflow: hidden;
    text-align: center;
}

.pun .postleft .email a,
.pun .postleft .website a,
.pun .postleft .pw a {
    display: inline-block;
    width: 16px;
    height: 16px;
    margin-right: 0.5em;
    background-position: 0 0;
    background-repeat: no-repeat;
}

#search_google {
    margin-bottom: 25px;
    background-color: #36393F;
    text-align: center;
    padding-top: 10px;
    padding-bottom: 15px;
    border: 1px solid #66686B;
    border-top: 0px solid black;
    border-bottom-right-radius: 12px;
    border-bottom-left-radius: 12px;
}

#cse-search-results {
    margin-top: 15px;
    text-align: center;
    overflow: hidden;
}

#cse-search-results iframe {
    /*+border-radius: 6;*/
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    -khtml-border-radius: 6px;
    border-radius: 6px;
    width: 90%;
    border: 1px solid #66686B;
}

#ansifoot {
    font-size: small;
    text-align: left;
    color: #FFFFFF;
}

/*****************************************************************
1. INITIAL SETTINGS
*****************************************************************/
/* Limited Reset
----------------------------------------------------------------*/
.pun table,
.pun div,
.pun form,
.pun p,
.pun h1,
.pun h2,
.pun h3,
.pun h4,
.pun h5,
.pun pre,
.pun blockquote,
.pun ul,
.pun ol,
.pun li,
.pun dl,
.pun dt,
.pun dd,
.pun th,
.pun td,
.pun fieldset,
.pun img,
.pun abbr,
.pun cite {
    margin: 0;
    padding: 0;
    border: 0;
}

.pun ul,
.pun ol {
    list-style: none;
}

/* Structural Settings
----------------------------------------------------------------*/
.pun .clearer,
.pun .nosize {
    height: 0;
    width: 0;
    line-height: 0;
    font-size: 0;
    overflow: hidden;
}

.pun .clearer,
.pun .clearb {
    clear: both;
}

.pun .nosize {
    position: absolute;
    left: -9999em;
    text-indent: -9999em;
    width: 0;
}

* html .inbox,
* html .inform,
* html .pun,
* html .tclcon,
* html .codebox {
    height: 1px;
}

.pun,
.pun .inbox,
.pun .inform,
.pun .tclcon,
.pun .codebox {}

.clearl {
    clear: left;
}

/* Hidden Elements
----------------------------------------------------------------*/
#brdfooter h2,
#brdstats h2,
#brdstats .conl dt,
#brdstats .conr dt,
#modcontrols dt,
#searchlinks dt,
div.postright h3,
.pun .required strong span {
    position: absolute;
    display: block;
    overflow: hidden;
    width: 0;
    left: -9999em;
    text-indent: -9999em;
}

/*****************************************************************
2. TEXT & CONTENT
*****************************************************************/
/* Text Defaults
----------------------------------------------------------------*/
.pun {
    font: 68.75% / 1.4545em Verdana, Helvetica, Arial, sans-serif;
}

.pun table,
.pun td,
.pun th,
.pun input,
.pun select,
.pun optgroup,
.pun textarea,
.pun small,
.pun samp,
.pun legend {
    font-size: medium;
    font-family: verdana, helvetica, arial, sans-serif;
}

.pun pre,
.pun code {
    font-size: medium;
    font-family: consolas, monaco, "bitstream vera sans mono", "courier new", courier, monospace;
}

.pun pre code {
    font-size: medium;
}

.pun strong {
    font-weight: bold;
    font-size: medium;
}

.pun em {
    font-style: italic;
}

.pun ins {
    color: #000;
}

/* Content Defaults
----------------------------------------------------------------*/
.pun p,
.pun ul,
.pun ol,
.pun dl {
    font-size: medium;
    padding: 3px 5px;
}

.pun h2 {
    font-size: x-large;
    border-radius: 6px 6px 0px 0px;
    background-color: rgba(114, 136, 217, 0.8);
    text-align: center;
    padding: 10px 6px;
    margin-top: 25px;
    moz-box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
    -webkit-box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
    box-shadow: inset 0 0 24px rgba(0, 0, 0, 0.350);
}

.pun .blocktable h2 {
    color: #FFFFFF;
    font-weight: bold;
    background-color: rgba(114, 136, 217, 0.750);
    font-size: x-large;
    padding-bottom: 10px;
    padding-top: 10px;
}

.pun div[id^=idx] > h2 {
    cursor: pointer;
}

.pun h3 {
    font-size: 1.091em;
    padding: 3px 0;
}

.pun table p,
.pun table h3 {
    padding: 0;
}

.pun span.warntext,
.pun p.warntext {
    font-weight: bold;
}

/* User Content (Announcements, Rules, Posts)
----------------------------------------------------------------*/
.pun .usercontent p,
.pun .postmsg p {
    padding: 0.75em 0;
}

.pun .usercontent ul,
.pun .postmsg ul {
    padding: 0.75em 1em 0.75em 2.5em;
    list-style: disc;
}

.pun .usercontent ol,
.pun .postmsg ol {
    padding: 0.75em 1em 0.75em 2.5em;
    list-style: decimal;
}

.pun .usercontent ol.alpha,
.pun .postmsg ol.alpha {
    list-style: lower-alpha;
}

.pun .usercontent li ol,
.pun .usercontent li ul,
.pun .postmsg li ol,
.pun .postmsg li ul {
    padding: 0.25em 1em 0.75em 2.5em;
}

.pun .usercontent li p,
.pun .postmsg li p {
    padding: 0;
}

.pun .usercontent h1 {
    font-size: 1.4em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

.pun .usercontent h2 {
    font-size: 1.2em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

.pun .usercontent h3 {
    font-size: 1.1em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

.pun .usercontent h4,
.pun .usercontent h5,
.pun .usercontent h6 {
    font-size: 1em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

.pun .quotebox cite {
    font-weight: bold;
    font-style: normal;
    padding: 0.75em 0.75em 0 0.75em;
}

.pun span.bbu {
    text-decoration: underline;
}

.pun span.bbs {
    text-decoration: line-through;
}

.pun span.bbs,
.pun del {
    text-decoration: line-through;
}

.pun del {
    color: #555;
}

.pun .postmsg ins,
#punhelp samp ins {
    text-decoration: none;
}

.pun div.postmsg h5,
#punhelp h5 {
    font-size: 1.1em;
    font-weight: bold;
    padding: 0.75em 0 0 0;
}

/*****************************************************************
3. COMMON STYLES
*****************************************************************/
/* Page Layout
----------------------------------------------------------------*/
html,
body {
    margin: 0;
    padding: 0;
}

.pun {
    margin: 12px 20px;
}

#punredirect,
#punmaint,
#puninstall,
#pundb_update {
    margin: 50px 20% 12px 20%;
}

/* Vertical Element Spacing
----------------------------------------------------------------*/
#brdheader {
    margin: 0 0 12px 0;
}

#announce,
#brdstats {
    margin: 1em 0 2em;
}

#announce-block {
    border: 1px solid #666;
}

.pun .blocktable,
.pun .block,
.pun .blockform,
.pun .block2col,
#postreview {
    margin-bottom: 12px;
}

#punindex .blocktable,
.pun .blockpost {
    margin-bottom: 6px;
}

#postreview .blockpost {
    margin-bottom: -1px;
}

.pun .block2col .blockform,
.pun .block2col .block {
    margin-bottom: 0px;
}

.pun .linkst,
.pun .linksb {
    margin-top: -12px;
}

.pun .postlinksb {
    margin-top: -6px;
}

/* External Borders
----------------------------------------------------------------*/
.pun .box {
    border-style: solid;
    border-width: 1px;
    background-color: rgba(114, 136, 217, 0.850);
}

#brdheader .box {
    border-radius: 6px;
    overflow: hidden;
}

/* Default Internal Spacing
----------------------------------------------------------------*/
.pun .block .inbox,
.pun .blockmenu .inbox {}

/*****************************************************************
4. COMMON BOARD ELEMENTS
*****************************************************************/
/* Board Header
----------------------------------------------------------------*/
#brdtitle h1 {
    font-size: 44px;
    font-weight: bold;
    line-height: 1em;
    padding: 5px 0 0;
    text-align: center;
}

#brddesc {
    padding: 3px 0 5px;
    text-align: center;
    font-size: small;
}

#brdmenu li {
    display: inline;
    margin-right: 12px;
}

#brdmenu a:link,
#brdmenu a:visited {
    text-decoration: none;
}

#brdmenu a:hover,
#brdmenu a:active {
    text-decoration: underline;
}

#brdwelcome .conl {
    text-align: left;
}

#brdwelcome .conr {
    float: right;
    text-align: right;
}

/* Breadcrumbs and Post Links
----------------------------------------------------------------*/
.pun .linkst {
    padding: 8px 6px 3px 6px;
}

.pun .linksb,
.pun .postlinksb {
    padding: 3px 6px 8px 6px;
}

.pun .crumbs {
    clear: both;
    width: 100%;
    overflow: hidden;
}

.pun .crumbs li {
    display: inline;
    white-space: nowrap;
    font-weight: bold;
}

.pun .pagelink {
    float: left;
    white-space: nowrap;
}

.pun .postlink {
    font-weight: bold;
    white-space: nowrap;
}

.pun .postlink,
.pun .modbuttons {
    float: right;
    text-align: right;
}

.pun .modbuttons {
    padding: 1px 0;
    white-space: nowrap;
}

.pun .modbuttons input {
    margin-left: 6px;
}

.pun .postlink a {
    display: inline-block;
    padding: 0 25px 0 40px;
    color: #FFFFFF;
    line-height: 23px;
    text-decoration: none;
    border: 1px solid #63656A;
    /*+border-radius: 6;*/
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    -khtml-border-radius: 6px;
    border-radius: 6px;
    background-color: #6678BB;
}

.pun .postlink a:hover {
    text-decoration: underline;
    color: #FFFFFF;
}

#punindex .subscribelink {
    margin-top: 6px;
}

/* Board Footer
----------------------------------------------------------------*/
#brdfooter .conl {
    float: left;
}

#brdfooter .conr {
    float: right;
    text-align: right;
}

#brdfooter #modcontrols {
    border-bottom-style: solid;
    border-bottom-width: 1px;
    text-align: center;
}

#brdfooter #modcontrols dd {
    display: inline;
    margin: 0 6px;
}

/* Board Stats
----------------------------------------------------------------*/
#brdstats .conl {
    float: left;
}

#brdstats .conr {
    float: right;
    text-align: right;
}

#onlinelist dd,
#onlinelist dt {
    display: inline;
}

/*****************************************************************
5. MAIN TABLES
*****************************************************************/
.pun table {
    width: 100%;
    border-collapse: collapse;
    border-spacing: 0;
    empty-cells: show;
}

.pun .blocktable table {
    table-layout: fixed;
}

.pun td,
.pun th {
    padding: 4px 6px;
    line-height: 1.273em;
    text-align: left;
    font-weight: normal;
}

.pun td,
.pun th {
    border-style: solid none none solid;
    border-width: 1px;
}

.pun .tcl {
    width: auto;
    border-left: 0;
}

.pun tr.topic > td {
    padding: 8px 6px;
}

.pun .tc2,
.pun .tc3,
.pun .tcmod {
    width: 10%;
    text-align: center;
    padding: 4px 0;
}

.pun .tcr {
    width: 30%;
}

.pun .tcl h3 {
    font-size: 13px;
    font-weight: bold;
