style.css:
@-moz-document domain("dyn.ithome.com") {
	body {
		background-color: #f9f9f9 !important;
	}
	#post_comment {
		border-bottom: 1px #f9f9f9 solid;
		color: #0d0d0d;
	}

	#post_comment a {
		display: none;
	}
	.add_comm input#btnComment {
		background: #cccccc;
		color: #FFFFFF;
		text-indent: 0px;
	}
	.list .entry .headerimage {
		height: 40px;
		width: 40px;
		margin: 15px 0px 15px 10px;
	}
	.post_comment {
		width: 810px;
	}

	.add_comm textarea {
		width: 790px;
		background: #f9f9f9;
		border-bottom: 1px #0d0d0d solid;
	}
	.add_comm {
		width: 810px;
		background: #f9f9f9;
		border: 1px #f9f9f9 solid;
	}

	.comm_list ul.list li.entry {
		width: 810px;
		border-top: 0px #fff solid;
		border-bottom: 0px #ddd dotted;
	}
	.comm_reply .s,
	.comm_reply .a {
		color: #606060;
		font-size: 13px;
	}
	.comm_list ul.list li.entry p {
		padding: 0px 0 0 60px;
	}
	.p_floor {
		display: none;
	}
	#HotList h3 {
		display: none;
	}
	.nick a {
		margin-right: 4px;
		color: #0d0d0d !important;
		margin-bottom: 2px;
		font-size: 14px;
		font-weight: 800;
		line-height: 1.8rem;
	}
	.nick {
		padding-right: 0px;
	}
	li .mobile {
		padding-right: 0px;
	}
	.nick a:hover {
		color: #0d0d0d !important;
	}
	.ywz-content,
	span.mobile a {
		display: none;
	}
	.comment_co,
	.comment_fo {
		color: #065fd4 !important;
		font-size: 14px;
	}
	.comm_list ul.list li.entry ul.reply .zheDie a {
		color: #065fd4;
	}
	.comm_reply a {
		margin-left: 45px;
	}
	.comm_list ul.list li.entry .info {
		padding: 10px 0 0 60px;
		font-size: 13px;
		color: #606060;
	}
	.comm_list ul.list li.entry .level,
	#post_comment span,
	.icon2,
	.refresh {
		display: none;
	}
	.comm_list ul.list li.entry .adiv {
		margin-right: 30px;
	}
	#allch3 {
		color: #0d0d0d;
		font-size: 16px;
		border-bottom: 1px #0000001a solid;
	}
	.comm_list ul.list li.entry ul.reply {
		border: 0px #fcbb90 solid;
		background: #f9f9f9;
	}
	.comm_list ul.list li.entry ul.reply li.gh {
		border-bottom: 0px dotted #fcbb90;
	}
	.comm_list .more_comm a {
		background: #e5e5e5;
		border-top: 0px #a20022 solid;
		color: #0d0d0d;
		text-align: center;
		padding-right: 20px;
		padding-left: 20px;
	}
	.comm_list .more_comm a:hover {
		background-color: #d9d9d9;
		border-color: #0d0d0d;
	}
	a:hover {
		color: #0d0d0d;
	}
	.add_comm .comm-con {
		bottom: 20px;
		width: 100%;
		right: 650px;
	}
	.add_comm .comm-con .l {
		width: 15px;
	}
	#commentMessage {

		display: none;
	}

	/*评论区登录*/
	.comm_login {
		width: 100%;
	}
	.comm_login span.p1 {
		padding: 0 10px 0 0;
	}
	.comm_login input.rm_smt {
		width: 80px;
		height: 35px;
		color: #0d0d0d;
		background: #e5e5e5;
	}
	.comm_login input.inpt {
		width: 200px;
		height: 35px;
		margin-right: 20px;
	}
	.comm_list ul.list li.entry .zhiChi {
		margin-right: 7px;
	}
	.comm_list ul.list li.entry ul.reply .zhiChi {
		margin-right: 0px;
	}

	.comm_login label {
		top: 11px!important;
	}
}

@-moz-document domain("www.ithome.com") {
body {
	background: #f9f9f9;
}

#con {
	background: #f9f9f9;
	width: 1190px;
}
#nav {
	width: 1190px;
}
#hd,
#nav a.sel {
	background: #FFFFFF;
}

#nav .an {
	color: #b61616;
}

#nav .an a.nav-item:after {
	background: #ffffff;
}

#nav .an a.nav-item:before,
#nav .an a.nav-item:after,
#nav .nav-item-5:after {
	display: none;
}

#nav .nav-item-5:after {
	background: #ffffff;
}

#nav a.nav-item,
.nl-con .bx h2.cate-title,
.all_cate a:link {
	color: #000000;
}

#nav a.nav-item:hover {
	background: #f2f2f2;
	color: #0d0d0d!important;
}
#hd a:hover {
	color: #0d0d0d!important;
}
.con-block .rt {
	margin-left: 0px;
}
#top5,
#tt50,
#side_func,
#crf1 {
	display: none;
}
.tb-item > a {
	display: none !important;
}
.tb-item a.item-link {
	color: #0d0d0d!important;
}
.rt .nlst .ct {
	width: 1190px;
}
.dm a.item-link:after {
	background: none;
	width: 0px;
	height: 0px;
}
.all_news,
.shareto,
.newsgrade0,
.lf,
.lk,
.focus_area,
.con_2,
.related_post,
#iframeu2575614_0,
.adblock,
.pti_comm,
#author_baidu,
#editor_baidu,
.dajia,
.hotcate,
.bx-recom {
	display: none;
}
.adsbygoogle {
	display: none !important;
}
.ct {
	width: 1190px;
}

.date_list {
	color: #0d0d0d;
}

.rt .nlst .lst {
	height: 100%;
	width: 1190px;
	padding-top: 20px;
}
.rt .nlst .new-list ul li a:visited {
	color: #909090;
}
#rm_avatar img {
	border: 1px solid #D7D7D7;
}
.newnewsmsg {
	background-color: #909090;
	color: #FFFFFF;
	width: 850px;
}

.page_nav {
	margin: 0 auto;
}

.post_content h2 {
	background: #FFFFFF;
	font-size: 26px;
}

.rt .nlst .ct ul li a {
	color: #0d0d0d;
}

.rt .nlst .ct ul li a.current {
	background: #e5e5e5;
	color: #0d0d0d;
}

.rt .nlst .new-list ul {
	width: 570px;
}
.rt .nlst .new-list ul li.top {
	background: left center no-repeat;
	padding-left: 30px;
}

li span.title {
	width: 400px;
	overflow: inherit;
}

li.new span.date {
	color: #0d0d0d;
}

.content {
	padding-left: 20px;
	width: 810px;
}
.content .post_content p a {
	color: #0d0d0d;
}
.content .post_content p a:hover {
	color: #909090 !important;
}
.content .current_nav {
	border-bottom: 4px solid #e0e0e0;
}
.rt .nlst .new-list ul li {
	padding: 10px 0px 10px 30px;
	background: none;
	float: unset;
	margin-bottom: 0px;
	border: 0px solid #fff;
}
a.rednews:visited {
	color: #ff0000!important;
}
.rt .nlst .new-list ul li a:hover {
	color: #909090 !important;
}
li.mnow {
	position: relative;
	z-index: 1001;
	padding-right: 0px!important;
	min-width: 321px!important;
	border-color: #fcdeb5!important;
	border-radius: 3px;
	background: #f9f9f9!important;
}
.date {
	display: block !important;
}
a:hover {
	color: #FFFFFF !important;
}
.page_nav .page_num a,
.page_nav .page_num span.current {
	color: #0d0d0d!important;
	background: #e5e5e5;
	border-radius: 50px;
}
.page_nav .page_num a.current:hover {
	background-position: -28px -28px;
	color: #0d0d0d!important;
}
.ct > ul > li > a:hover {
	color: #0d0d0d !important;
}

/*最热排行*/
.rt .nlst .hot-list .bx {
	margin: 0 10px 20px 20px;
	width: 560px;
}
.hot-list .bx ul {
	padding: 0 10px;
	background: none;
	font-size: 9pt;
}
.hot-list .bx h4 {
	padding: 15px 15px 5px;
	border-top: 2px solid #e8e8e8;
	color: #0d0d0d;
	font-size: 18px;
}
.bx > ul > li > a:hover {
	color: #909090 !important;
}
.bx > ul > li > a {
	color: #0d0d0d !important;
}
/*底部*/
#ft .if,
#ft .if .bx2,
#ft .if .bx2 a {
	background: #2c2f3a;
	color: #fff !important;
	;
	text-align: center;
}
#ft .if .bx1 {
	height: 43px;
	background: #2c2f3a;
	line-height: 43px;
}
.con_2 {
	width: 1190px;
}

/*详情页*/
#wrapper {
	clear: both;
	padding: 20px 0;
	background: #f9f9f9;
}
.current_nav > a:hover,
#source_baidu > a:hover {
	color: #909090 !important;
}
.sidebar .sb_list {
	margin-bottom: 2pt;
}
.prelogin a {
	color: #0d0d0d !important;
	;
}
}

@-moz-document url("https://www.ithome.com/block/topnews.htm") {
.right_con .bar {
	border-top: 4px #e0e0e0 solid;
	background: #f9f9f9;
}
.right_con .bar ul li.current {
	border-bottom: 1px #0d0d0d solid;
	background: #fff;
	color: #0d0d0d;
}
.right_con .list {
	position: relative;
	z-index: 1000;
