style.css:
@-moz-document url-prefix("https://github.com/") {
/*
	dark1 		#292d3e
	dark2		#252937



	caption1 	#cccccc
*/

/******** BASE ********/
body {
	font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Helvetica,Arial,sans-serif,Apple Color Emoji, "Montseratt Medium", Segoe UI Emoji, Noto Color Emoji;
    color: #fff;
    background-color: #292d3e;
}

.Header {
    background-color: #252937;
}

.dashboard-sidebar {
	background-color: #292d3e
}

a {
    color: #63a7f8;
}

.rule, hr {
    border-bottom-color: #404454;
}

/******** HEADER ********/
.header-nav-current-user .user-profile-link {
    color: #fff;
}

/******** HEADER SEARCH ********/
.jump-to-suggestions-results-container .navigation-item {
    border-bottom-color: #e1e4e8;
}

/******** BORDERS ********/
.border {
    border-color: #3f4353!important;
}
.border-top {
    border-top-color: #404454!important;
}
.border-bottom {
    border-bottom-color: #404454!important;
}
.border-right {
    border-right: 1px solid #404454!important;
}
.border-blue {
    border-color: #1d80f3!important;
}
.border-gray-light {
    border-color: #404454!important;
}

/******** COLORS ********/
.bg-white {
    background-color: #292d3e!important;
}
.text-gray {
    color: #bbbdc4!important;
}
.text-gray-dark {
    color: #d5dadf!important;
}
.text-gray-light {
    color: #969ca4!important;
}
.text-green {
    color: #30b84f!important;
}
.text-red {
    color: #ff5764!important;
}

.bg-gray {
    background-color: #292d3e!important;
}

.link-gray-dark {
    color: #fff!important;
}

.link-gray-dark:hover, .link-hover-blue:hover {
    color: #9bacf4!important;
}

.link-gray {
    color: #cfcece!important;
}
.link-gray:hover, .muted-link:hover {
    color: #fceded!important;
}

.muted-link {
    color: #b6b9c7!important;
}

/******** AVATAR STACK ********/
.AvatarStack-body {
    background: transparent;
}
.AvatarStack--right .avatar {
    border-color: #ffffff8c;
}
.AvatarStack-body .avatar {
    background-color: #fefefe38;
}

/******** BOX ********/
.Box {
    background-color: rgb(39, 42, 58);
    border-color: hsl(228, 14%, 29%);
}

.Box-header {
    background-color: #252937;
    border-color: #404454;
}

.Box-body {
    border-bottom-color: #404454;
}

.Box-row {
    border-top-color: #3f4353;
}

.Box-row--hover-blue:hover {
    background-color: #252937;
}

/******** NEWS ********/
.news div.message, .news li blockquote {
    color: #d1d1d1;
}


/******** BUTTONS ********/
.btn {
    color: #f1f1f1;
    background-color: #272a3a;
	border-color: #404454;
    background-image: none;
}
.btn.hover, .btn:hover {
    background-color: #404454;
    background-image: none;
    background-position: -.5em;
    border-color: #404454;
}

.btn-primary {
    color: #fff;
    background-color: #2585f5;
    background-image: none;
	border-color: #0b77f6;
}

.btn-primary.hover, .btn-primary:hover {
    background-color: #2585f5;
    background-image: none;
}

.btn-primary.selected, .btn-primary:active, .btn-primary[aria-selected=true], [open]>.btn-primary {
    background-color: #2067ba;
    background-image: none;
    border-color: transparent;
    box-shadow: none;
}

.btn-primary.focus, .btn-primary:focus {
    box-shadow: none;
}

.btn.selected, .btn:active, .btn[aria-selected=true], [open]>.btn {
    background-color: #383b49;
    background-image: none;
    border-color: rgb(64, 68, 84);
    box-shadow: none;
}

.btn-link {
    color: #5fa7f1;
}

/******** IMAGES ********/
.markdown-body img {
    background-color: transparent;
}

/******** HEADINGS ********/
.markdown-body h1, .markdown-body h2 {
    border-bottom-color: #404454;
}

/******** CODE ********/
.markdown-body .highlight pre, .markdown-body pre {
    background-color: #292d3e;
}

/******** SELECT MENU ********/
.SelectMenu-modal {
    background-color: #252937;
    border-color: #404454;
    box-shadow: 0 1px 5px #f6f8fa3d;
}
.SelectMenu-list {
    border-top-color: #252937;
}
.SelectMenu-item {
    background-color: #292d3e;
    border-bottom-color: #404454;
    color: #cccccc;
}
.SelectMenu-item[aria-checked=true] {
    color: #31c9dd;
}
.SelectMenu-item:hover, body:not(.intent-mouse) .SelectMenu-item:focus {
	background-color: #252937;
}
.SelectMenu-filter {
    border-top-color: #404454;
}
.SelectMenu-tab {
    border-color: #292d3e;
}
.SelectMenu-tab[aria-selected=true] {
    color: #f1f8ff;
    background-color: #292d3e;
	border-color: #404454;
}
.SelectMenu-tab:not([aria-checked=true]):active, .SelectMenu-tab:not([aria-checked=true]):hover {
    background-color: #292d3e;
}
.SelectMenu-tab:not([aria-selected=true]):hover {
    color: #f1f8ff;
}

.select-menu-modal {
    background-color: transparent;
	border-color: #404454;
}
.select-menu-divider, .select-menu-header {
    background: #252937;
    border-bottom-color: #404454;
}

.select-menu-divider, .select-menu-header .select-menu-title {
    color: #fff;
}
.select-menu-item {
	color: #99cfdf;
    background-color: #292d3e;
	border-bottom-color: #404454;
}
.select-menu-item, .select-menu-item-text .description {
    color: #b2b4bc;
}
.select-menu-item.selected, details-menu .select-menu-item[aria-checked=true], details-menu .select-menu-item[aria-selected=true] {
    color: #ffffff;
}
.select-menu-item.selected .description, details-menu .select-menu-item[aria-checked=true] .description, details-menu .select-menu-item[aria-selected=true] .description {
    color: #b2b4bc;
}
.select-menu-item:hover .select-menu-item-heading {
	color: #fff;
}
.select-menu-item.navigation-focus, .select-menu-item.navigation-focus .description-inline, .select-menu-item.navigation-focus.select-menu-action, .select-menu-item.navigation-focus.selected, .select-menu-item.navigation-focus[aria-checked=true], .select-menu-item:focus, .select-menu-item:focus .description-inline, .select-menu-item:focus.select-menu-action, .select-menu-item:focus.selected, .select-menu-item:hover, .select-menu-item:hover .description-inline, .select-menu-item:hover.select-menu-action, .select-menu-item:hover.selected, .select-menu-item[aria-checked=true]:focus, .select-menu-item[aria-checked=true]:hover, .select-menu-item[aria-selected=true] .description-inline, .select-menu-item[aria-selected=true].select-menu-action, .select-menu-item[aria-selected=true]:focus, .select-menu-item[aria-selected=true]:hover {
    background-color: #252937;
}

/* .SelectMenu-modal footer {} */

.Counter {
    background-color: #2585f5;
}


/******** TOPICS ********/
.topic-tag {
    background-color: #272a3a;
    border: 1px solid #404454;
}
.topic-tag-link:hover {
    background-color: #252937;
    color: #fff;
}

/******** REPOSITORY ********/
.overall-summary {
    border-color: #404454;
}
.numbers-summary li>.nolink, .numbers-summary li>a, .numbers-summary .octicon {
    color: #b2b4bc;
}
.numbers-summary li>a:hover {
    color: #63a7f8;
}
.repository-lang-stats-graph {
    border-color: #404454;
}

.file-navigation .get-repo-btn {
    border-top-color: #404454;
}

.file-wrap {
    border-color: #404454;
}
table.files {
    background: none;
}
table.files td {
    border-top-color: #404454;
}
table.files td.icon, table.files td.icon .octicon-file-directory {
    color: rgb(178, 180, 188);
}
table.files tr.navigation-focus td, table.files tr[aria-selected=true] td {
    background-color: #252937;
}


/******** TYPOGRAPHY ********/
.text-emphasized {
    color: #d4d6e1;
}

/******** TYPOGRAPHY BASE ********/
code, pre, tt {
    font-family: monospace;
}

/******** FORM CONTROL ********/

.form-control, .form-select {
    background-color: #333747;
    border-color: #ffffff0f;
    color: #bcf8ff;
}

.form-control.focus, .form-control:focus, .form-select.focus, .form-select:focus {
    border-color: #2e8bf6;
    outline: none;
    box-shadow: inset 0 1px 2px rgba(27,31,35,.075), 0 0 0 0.2em rgba(37, 133, 245, 0.19);
}

/******** DROPDOWN MENU ********/
.dropdown-menu {
    background-color: #292d3e;
	border-color: #3f4353;
}
.dropdown-menu:before {
    border: 8px solid transparent;
    border-bottom-color: #3f4353;
}
.dropdown-menu:after {
    border-bottom-color: #292d3e;
}
.dropdown-item {
    color: #fff;
}
.dropdown-divider {
    border-top-color: #3f4353;
}

/******** POPOVER ********/
.Popover-message--bottom-left:after, .Popover-message--bottom-right:after, .Popover-message--bottom:after {
    border-top-color: #272a3a;
}
.Popover-message--top-left:after {
    border-bottom-color: #272a3a;
}
.Popover-message--top-left:before {
    border-bottom-color: #404454;
}

.Popover-message--bottom-left:after {
    border-top-color: #272a3a;
}
.Popover-message--bottom-left:before {
    border-top-color: #404454;
}

/******** PAGE HEAD ********/
.pagehead {
    background-color: #252937!important;
	border-bottom-color: #404454;
}
.reponav-item {
    color: #b2b4bc;
}
.reponav-item .Counter {
    color: #252937;
}
.reponav-item:focus, .reponav-item:hover {
    color: #fff;
}
.reponav-item.selected {
    color: #fff;
    background-color: #292d3e;
    border-color: #1d80f3 #404454 transparent;
}
.reponav-item .octicon {
    color: #b2b4bc;
}

/******** COMMIT ********/
.commit-tease {
    background-color: #252937;
    border-color: #404454;
    color: #b2b4bc;
}
.AvatarStack-body .avatar {
    background-color: #252937;
}
.commit-tease-sha {
    font-family: monospace;
    color: #5aa6ff;
}

/******** MISC ********/
.social-count {
    color: #fff;
    background-color: #404454;
    border-color: rgb(64, 68, 84);
}
.social-count:hover {
    color: #81b7fc;
}

/******** MENTIONS ********/
.team-mention, .user-mention {
    color: #fff;
}

/******** BRANCH NAME ********/
.branch-name {
	color: #fff!important;
    background-color: #2585f5;
}

/******** AJAX PAGINATION ********/
.ajax-pagination-form .ajax-pagination-btn {
    color: #5ba7ec;
    background: #272a3a;
    border-color: #404454;
}
.ajax-pagination-form .ajax-pagination-btn:focus, .ajax-pagination-form .ajax-pagination-btn:hover {
    color: #f1f1f1;
    background-color: #404454;
}

/******** GITHUB LIGHT ********/
.pl-c1, .pl-s .pl-v {
    color: #238aff;
}
.pl-pds, .pl-s, .pl-s .pl-pse .pl-s1, .pl-sr, .pl-sr .pl-cce, .pl-sr .pl-sra, .pl-sr .pl-sre {
    color: #7fb8fa;
}
.pl-k {
    color: #f24555;
}

}
