style.css:
@-moz-document domain("boredpanda.com") {
html {
    --color-black: #000;
    --color-dark-grey: #121212;
    --color-medium-grey: #212121;
    --color-light-grey: #bfbfbf;
    --color-white: #FFF;
    --color-red: #d74b1f;
    --color-dark-red: #b33d19;
    --color-green: #008000;
}

.cover {
    display: none;
}

body,
.header,
.header .bp-container.variation .nav.top-level-navigation ul li a,
.header .bp-container.variation .nav.top-level-navigation ul li.more .more-categories,
.header .bp-container.variation .nav.top-level-navigation ul li.more .more-categories ul li,
.header .bp-container.variation .nav ul li,
.header .dropdown-menu.notifications-drop-down,
.login-modal .auth-dialog .modal-body .login-form-block .auth,
.single-post-author,
.narrow-post-700 .show-all-contributors,
.narrow-post-700 .show-all-contributors a,
.post-comments .native-comments .expand-comments,
.show-all-contributors,
#showAllContributors {
    background-color: var(--color-black) !important;
}

.post,
.modal-content,
.top-categories,
.top-categories.variation ul li,
.top-categories.variation ul li a,
.posts .footer .right .vote-panel .points,
.header .bp-container.variation .nav ul li a.login-url,
.header .bp-container.variation .nav ul li.user-menu a:hover,
.sidebar .social-box .social-buttons,
.sidebar .social-box .social-buttons .button.facebook,
.sidebar .social-box .social-buttons .button.gplus,
.sidebar .social-box .social-buttons .button.twitter,
.sidebar .social-box .social-text-line,
.posts .post .footer .left .fb-custom-share-mobile .share-count,
.footer .comments i,
.footer .submissions i,
.footer .votes .vote i,
.single-open-list-post.narrow-post .bp-container .post .post-header,
.single.wide-post .post .categories-list.in-post,
.single-open-list-post.single.wide-post .bp-container .post .post-header,
.single-open-list-post .open-list-post-description,
.single-open-list-post.single.wide-post .bp-container .post .open-list-block,
.single-open-list-post .comments i,
.single-open-list-post .submissions i,
.single-open-list-post .votes .vote i,
.single-open-list-post.narrow-post-700 .bp-container .post .post-header,
.single-open-list-post.narrow-post-700 .bp-container .post .open-list-post-description,
.single-open-list-post.narrow-post-700 .bp-container .post .open-list-item,
.single-open-list-post.narrow-post-700 .bp-container .post .post-submission-line.post-footer.post-footer-gnews-promotion,
.single-open-list-post.narrow-post-700 .bp-container .post .open-list-block-footer,
.single-open-list-post.narrow-post .bp-container .post .open-list-block-footer,
.single-open-list-post.narrow-post .bp-container .post .entry .open-list-item,
.single.narrow-post-700 .bp-container .post footer.footer,
.single-post-author .author-info-container .follow,
.single .post .entry .open-list-footer,
.single .post .entry .post-content-description.submission-description .bordered-description, 
.single .post .outbrain-widget,
.single .post .post-recommendations,
.posts .post .footer .right .comments-count a,
.posts .post .footer .right .submissions-count a,
.posts .footer .right .vote-panel .points,
.post-comments,
.post-submission-line.post-footer.post-footer-gnews-promotion,
.newsletter-variation,
.scroll-to-top,
.sharebar,
.sharebar .social-box,
.post-bottom-list .box,
.sidebar .trending-posts-feed .title,
.page-template-subscribe-newsletter .wrapper,
.author-stats-box tr:first-child,
.author-info-box,
.posts-block,
.post-content-wrapper,
.post-content-container textarea,
.submit-post-item .controls .control.active,
.submit-post-item .controls .control,
.submit-post-item .content-box,
.post .newsletter-form.post-footer,
.post-image,
.info-block-container,
.edit-image-modal .edit-image-content .image-heading,
.edit-image-modal .edit-image-content .image-heading .add-post-form-input,
.edit-image-modal .edit-image-content .image-description,
.edit-image-modal .edit-image-content .image-description .add-post-form-input,
.edit-image-modal .edit-image-content .image-source,
.edit-image-modal .edit-image-content .image-actions .action-item.right-border,
.edit-image-modal .edit-image-content .image-actions .action-item,
.bottom-fixed-submit,
/* Profile */
.custom-form,
.page-template-users-top .users-top,
.author-info-container .follow,
/* About pages */
.about-bored-panda, 
.page-template-contact .left-contact-form, 
.panda-office {
    background: var(--color-dark-grey) !important;
}

/* Selected in a dark list */
.page-template-users-top .users-top table tbody tr.current-user,
.header .nav .notifications .notification-count,
.single .post .entry .post-content-description.text-open-list .bordered-description {
    background: var(--color-medium-grey) !important;
}

.open-list-comments .comment-submit-block .post-comment {
    background: var(--color-light-grey) !important;
}

/* Subscribe button */
.post-subscribe-form .new-variation-2 .subscribe-block .button {
    background: var(--color-green) !important;
}

/* Follow button hover */
.author-info-container .follow:hover {
    background: var(--color-red) !important;
}

/* Add post button */
.header .bp-container.variation .nav ul li.add-post a,
.single-open-list-post .header .bp-container.variation .nav ul li.add-post .add-post-top-button {
    margin: 10px 20px 10px 10px !important;
    border: none !important;
    border-radius: 5px !important;
    color: var(--color-white) !important;
    padding: 0 17px 0 15px !important;
    height: 38px !important;
    line-height: 42px !important;
    text-transform: none !important;
    font-weight: 500 !important;
    width: auto !important;
    background: var(--color-red) !important;
    border-bottom: 2px solid var(--color-dark-red) !important;
}

.header .bp-container.variation .nav.top-level-navigation ul li.active a,
.top-categories.variation ul li:hover,
.header .bp-container.variation .nav.top-level-navigation ul li.more .more-categories ul li {
    color: var(--color-light-grey);
    background-color: var(--color-dark-grey);
}

.post-title,
.post h2 a,
.auth .h2,
.auth .break p,
.auth label,
.header .bp-container.variation .nav.top-level-navigation ul li a:hover,
.header .nav .notifications .notification-count,
.header .dropdown-menu.notifications-drop-down .notifications-header .title,
.header .dropdown-menu.notifications-drop-down .empty-list .text,
.header .bp-container.variation .nav.top-level-navigation ul li a,
.sidebar .trending-posts-feed .trending-post .rec-title a,
.sidebar .trending-posts-feed .title,
.single .post .entry p,
.single .post .entry ul,
.single .post .entry h3,
.single .post .entry .open-list-header h2,
.single .post .entry .open-list-header span,
.single .post .entry .post-author span,
.single .post .entry .post-content-description.text-open-list .bordered-description,
.single.wide-post .post .entry h2,
.single-open-list-post .post-info-block .left.post-info .views,
.single-open-list-post .post-info-block .left.post-info p span,
.single-open-list-post .post-info-block .left.post-info i,
.post-recommendations .title,
.post-recommendations .recommended-posts .box .box-title,
.post-comments .title,
.post-comments h3,
.native-comments .comment .comment-author,
.native-comments .comment .comment-block p,
.native-comments .comment .vote-panel .points,
.native-comments .comment .comment-reply,
.open-list-comments .comment .comment-author,
.open-list-comments .comment .comment-reply,
.open-list-comments .comment .vote-panel .points,
.open-list-comments .view-more-replies,
.open-list-comments .expand-comments,
.single .post .entry .open-list-full-list-block a,
a.load-more-replies,
.post-tags .title,
.post-bottom-list .box .box-title,
.post-bottom-title,
.newsletter-variation .title,
.page-template-subscribe-newsletter .masthead,
.page-template-subscribe-newsletter .wrapper .text,
.page-template-subscribe-newsletter .wrapper .text .bold,
.get-app .title,
.upload-area .upload-description,
.author-stats .stats-info,
.author-info-container h2,
.posts-block .block-info .author-info .author-name,
.block-heading,
.contact-author-span,
/* Adding a new post */
.submit-post-item .controls .control.active,
.drag-n-drop-area .description-area .heading,
.drag-n-drop-area .upload-photos-button,
.post-content-container .post-title textarea,
.post-content-container .post-description textarea,
.edit-image-modal .edit-image-content .image-heading .add-post-form-input,
.edit-image-modal .edit-image-content .image-description .add-post-form-input,
.edit-image-modal .edit-image-content .image-actions .action-item label,
.edit-image-modal .edit-image-content .image-actions .action-item .delete-image,
.post-content-container .post-images .post-image .image-description .image-description-input,
/* Profile */
.no-posts,
.form-content ul li .edit-picture-block h3,
.form-content ul li label,
.custom-form h2,
.toggle-boxes .toggle-info label,
.form-content ul li .edit-profile-image:hover,
.posts-block.follows .post-block .author-info h2,
/* Top users */
.page-template-users-top .users-top table thead tr td .title,
.page-template-users-top .users-top table tbody tr td.name .value,
.page-template-users-top .users-top table tbody tr td .value,
/* About pages */
.about-bored-panda h1, 
.page-template-contact .left-contact-form h1, 
.panda-office h1,
.about-bored-panda p, 
.panda-office p,
/* Others */
.tdlw * {
    color: var(--color-white) !important;
}

/* Menu for the profile */
.header .bp-container.variation .nav ul li.user-menu a span,
.header .bp-container.variation .nav ul li.user-menu a .icon-arrow-down-menu,
.header .bp-container.variation .nav ul li a,
.header .bp-container.variation .nav ul li.search .search-icon,
.login-modal .auth-dialog .modal-body .login-form-block .auth .sign-up-link a {
/* Not important, in order to activate other color on hover */
    color: var(--color-white);
}

/* Add post button hover */
.header .bp-container.variation .nav ul li.add-post a:hover,
.single-open-list-post .header .bp-container.variation .nav ul li.add-post .add-post-top-button:hover,
.single-open-list-post .header .bp-container.variation .nav ul li.add-post .add-post-top-button:hover i {
    color: var(--color-black) !important;
}

.top-categories.variation ul li a,
.posts .footer .right .vote-panel .points,
.header .bp-container.variation .nav ul li a.login-url,
.header .dropdown-menu.notifications-drop-down .empty-list .title,
.sidebar .social-box .social-text-line,
.single .post .entry .open-list-footer .vote-panel .points,
.post .intro .post-author span,
.posts .post .footer .left .fb-custom-share-mobile .share-count,
.footer .comments i,
.footer .submissions i,
.footer .votes .vote i,
.single-open-list-post .comments i,
.single-open-list-post .submissions i,
.single-open-list-post .votes .vote i,
.single-post-author .author-info-container .member-type,
.posts .post .footer .right .comments-count a,
.posts .post .footer .right .submissions-count a,
.posts .footer .right .vote-panel .points,
.single .post .post-shares-footer .right .votes .points,
.author-communication-line .title,
.sidebar .trending-posts-feed .trending-post .points-count,
.submit-post-item .controls .control,
a.show-hidden-comment {
    color: var(--color-light-grey);
}

/* Hovering color for text */
.header .bp-container.variation .nav ul li.user-menu a:hover,
.header .bp-container.variation .nav ul li.search .search-icon:hover,
.header .bp-container.variation .nav ul a:hover {
    color: var(--color-red) !important;
}

/* Icon for the header */
.header .desktop .logotype div a,
.header .desktop .logotype h1 a,
.header .desktop .logotype h2 a {
    background-image: url(https://assets.boredpanda.com/blog/wp-content/themes/boredpanda/images/sprite-sheet.png?ver=1581516101);
    background-position: -445px 0px;
    width: 30px;
    height: 33px;
    display: inline-block;
    text-indent: -9999px;
    margin-right: 0px;
    margin-top: 13px;
}

/* Icon for the sharebar on the left */
.sharebar-votes .panda.neutral {
    background-image: url(https://assets.boredpanda.com/blog/wp-content/themes/boredpanda/images/sprite-sheet.png?ver=1581516101);
    background-position: -445px 0px;
    width: 30px;
    height: 33px;
}

/* Delete icon */
.edit-image-modal .edit-image-content .image-actions .action-item .delete-image-icon {
    background-image: url(https://assets.boredpanda.com/blog/wp-content/themes/boredpanda/images/sprite-sheet.png?ver=1581516101);
    background-position: -130px -444px;
    width: 14px;
    height: 18px;
    margin: 35px auto 3px;
    display: block;
}

/* Close icon */
.close-box {
    background-image: url(https://assets.boredpanda.com/blog/wp-content/themes/boredpanda/images/sprite-sheet.png?ver=1581516101);
    background-position: -225px -445px;
}

/* Do not display logo */
.header .desktop .logotype.logo-category a {
    display: none;
}

/* Post margins */
.post .footer,
.posts .post .footer .right .comments-count,
.posts-block .post-block,
.info-block-container,
.login-modal .auth-dialog .modal-body .login-form-block .auth {
    border-style: none !important;
}

/* Sidebar with posts on the right, bottom delimiter */
.sidebar .trending-posts-feed .trending-post,
.tdlw {
    border-bottom: 2px solid var(--color-light-grey);
}

/* Notifications dropdown border */
.header .dropdown-menu.notifications-drop-down {
    border: 2px solid var(--color-light-grey);
}

/* Social buttons */
.sidebar .social-box .social-buttons .button.facebook .link,
.sidebar .social-box .social-buttons .button.gplus .link, 
.sidebar .social-box .social-buttons .button.twitter .link {
    border-radius: 100%;
}

/* Comments section title margin */
.single.wide-post .post-comments {
    margin-top: 0px;
}

/* SVG icons */
.author-communication-line i .svg-icon {
    fill: var(--color-light-grey);
}

/* Loading spinner (PNGs) */
.full-size-spinner,
.overlay .spinne,
#infscr-loading img {
    filter: invert(100%) !important;
}
}
