style.css:
@-moz-document domain("imap.vivaldi.net") {
/* Creator: Alkaris Â©2020

Theme based on GTK+ theme Arc Darker

CC BY-NC-SA - Creative Commons 4.0 Attribution-NonCommercial-ShareAlike
*/




body {
    font-size: 9px;
    color: #2e333e;
    background: #383c4a;
    background-color: #383c4a
}

#topnav {
	height: 28px;
	margin-bottom: 10px;
	width: 1910px;
	top: 0px;
	padding: 0px 0px 0px 10px;
	background: #2e333e;
}

.topright {
	position: relative;
	padding-right: 7px;
	float: right;
}

#taskbar a:focus {
	background-color: #383c4a;
	color: #d3dae3;
}

#taskbar a.button-selected {
	color: #5294e2;
	background-color: #383c4a;
	padding: 0px 48px 18px 4px;
	position: relative;
	top: 0px;
	object-fit: scale-down;
	width: 12px;
	height: 12px;
}

#taskbar a {
	padding: 0px 48px 18px 4px;
	position: relative;
	object-fit: scale-down;
	top: 0px;
	left: -50px;
	width: 12px;
	height: 12px;
}

#taskbar a.button-mail {
	position: absolute;
	padding-right: 50px;
	object-fit: scale-down;
	top: 0px;
	width: 18px;
	height: 18px;
	left: -350px;
	right: 10px;
}


#taskbar a.button-addressbook {
	position: absolute;
	padding-right: 65px;
	top: 0px;
	object-fit: scale-down;
	width: 18px;
	height: 12px;
	left: -280px;
}

#taskbar a.button-calendar {
	position: absolute;
	padding-right: 70px;
	object-fit: scale-down;
	top: 0px;
	width: 17px;
	height: 12px;
	left: -194px;
}

#taskbar a.button-settings {
	position: absolute;
	padding-right: 15px;
	object-fit: scale-down;
	top: 0px;
	width: 75px;
	height: 12px;
	left: -104px;
}

#taskbar .minmodetoggle {
	position: absolute;
	top: -5px;
	right: 5px;
	width: 15px;
	height: 32px;
}

#toplogo {
	position: absolute;
	padding-top: 0%;
	cursor: pointer;
	border: 0px;
	object-fit: scale-down;
	width: 130px;
	height: 40px;
	top: -5px;
	left: 5px;
}

.headers-table td {
	color: #d3dae3;
}

.headers-table td.header a, .ui-dialog-content.popup span.adr a {
	color: #5294e2;
}

.widescreen #messagelistheader {
    background: #2f343f;
    background-color: #2e333e;
    color: #d3dae3;
    top: -3px;
    left: -3px;
    right: -3px;
    height: 30px;
    border-radius: 2px 2px 0 0;
    white-space: nowrap;
    border-width: 1px;
    border-bottom-style: solid;
    border-color: #2f343f
}

.pagenav, .pagejumper, input[type="text"], textarea {
    background: #2f343f;
    background-color: #2f343f;
    color: #c4c4c4;
    padding: 1px 0;
	border: #444a58;
    border-radius: 2px;
}

.widescreen .messagelist td.subject span.date {
    color: #5294e2;
}

.widescreen #mailview-bottom {
	border: 1px solid #d3dae3;
}

.widescreen #mailview-bottom {
	border: 1px solid #2f343f;
}

a.menuselector {
	border: 1px solid #2f343f;
	background: #2f343f;
}

a.menuselector .handle {
	background: #2f343f;
	background-color: #444a58;
	color: #d3dae3;
	padding: 0 6px 0 6px;
}

.searchbox input, #quicksearchbar input {
	width: 150px;
	margin: 0;
	padding: 4px 30px 4px 34px;
	height: 18px;
	background: #434a57;
	border: 1px solid #2f343f;
	color: #d3dae3;
	font-weight: normal;
	font-size: 10px;
}

.listbox .listing li.selected>a {
	color: #d3dae3;
	background-color: #5294e2;
	font-weight: bold;
	border: 1px solid #383c4a;
}

.listing li a {
	color: #d3dae3;
	background-color: #404552;
	border: 1px solid #383c4a;
}

.listing li {
	border-bottom: 1px solid #2f343f;
}

ul.treelist li a:focus, ul.listing .listitem a:focus, ul.listing .listitem span:focus, ul.listing.focus .listitem.focused span {
	color: #d3dae3 !important;
	background-color: #383c4a;
	outline: 0;
}

.listbox .boxfooter{
	width: 100%;
	height: 40px;
	border-top: 1px solid #383c4a;
	background: #2f343f;
}

.listbox .listbutton {
	width: 44px;
	border-right: 1px solid #383c4a;
	background: #2f343f;
	padding: 0px 1px 0px 1px;
	margin-top: 1px;
}

.boxfooter .listbutton .inner {
	width: 41px;
	height: 28px;
	text-indent: -5000px;
	object-fit: scale-down;
}

.boxfooter .countdisplay {
	top: 10px;
	color: #d3dae3;
	padding: 3px 6px;
}

#quotadisplay {
	left: 6px;
	height: 18px;
	font-size: 11px;
	font-weight: bold;
	padding-left: 30px;
}

#message-objects div.notice {
	color: #f25056b3;
	border: 1px solid #bf972ab5;
	background-color: #f4ebd1a1;
	background-position: 3px -87px;
	padding: 3px 10px 3px 25px;
	white-space: transparent;
}

.folderlist li.mailbox .unreadcount {
	background: #f25056;
	color: #2e333e;
	font-weight: bold;
}

.folderlist li.mailbox.selected>a .unreadcount {
	background: #f25056;
	color: #2e333e;
	font-weight: bold;
}

.records-table tbody tr td:first-child, td.subject {
    padding-left: 2px;
    border-bottom: 1px solid #c4c4c4;
    background: #383c4a;
    background-color: #383c4a;
    color: #c4c4c4;
}

.widescreen .messagelist td.subject span.fromto {
    color: #c4c4c4;
}

.messagelist tbody td a {
    color: #c4c4c4;
    text-decoration: none;
    text-decoration-line: none;
    text-decoration-style: initial;
    text-decoration-color: initial;
}

.widescreen .messagelist td.flags {
    background-color: #383c4a;
    border-bottom: #c4c4c4;
}

#messagelistfooter {
    border-top: 1px solid #383c4a;
    background: #2e333e;
    padding: 4px 3px 1px;
}

.uibox {
    border: 1px solid #2f343f;
    border-radius: 2px;
    background: #383c4a;
}

a.button span.inner {
    color: #c4c4c4;
}

label.disabled, a.button.disabled {
    color: #c4c4c4;
}

.toolbar a.button {
	color: #d3dae3;
}

.toolbar a.button.disabled {
	color: #d3dae3;
	opacity: .4;
}

#mainscreen {
	position: absolute;
	top: 60px;
	left: 4px;
	right: 4px;
	bottom: 4px;
}

#topline {
	background-color: #2e333e;
	color: #d3dae3;
	height: 18px;
	padding: 0px 0px 0px 10px;
	border-bottom: 1px solid #2e333e;
}

ul.toolbarmenu li a {
	color: #d3dae3;
}

ul.toolbarmenu li a.icon {
	color: #d3dae3;
}

.googie_list td span, ul.toolbarmenu li a {
	background: #2f343f;
	background-color: #2e333e;
	color: #d3dae3;
	opacity: 100%;
}


/*

Email Compose section

*/

#composeheaders {
	border-radius: 4px 4px 0 0;
	padding-left: 19px;
}

#messageheader, #composeheaders {
	position: relative;
	padding: 1px 0px 0px 15px;
	background: #383c4a;
	color: #d3dae3;
	border-bottom: 1px solid #2e333e;
}

.moreheaderstoggle {
	background: #444a58;
	border-right: 1px solid #2e333e;
}

.compose-headers td textarea,
.compose-headers td input {
	width: 80%;
	resize: none;
	background-color: #404552;
	border: 1.5px solid #2e333e;
}

.compose-headers td.title {
	width: 1%;
	color: #d3dae3;
	white-space: nowrap;
}

.compose-headers td.editfield a.iconlink {
	color: #d3dae3;
}

a.iconlink {
	color: #d3dae3;
}

.composeoption {
	color: #d3dae3;
}

#compose-attachments {
	background: #404552;
	color: #d3dae3;
	border: 1px solid #2e333e;
	width: 240px;
}

#compose-attachments .hint {
	color: #d3dae3;
}

a.button, buttongroup {
	background: #b0b9c6;
	color: #404552;
	border: 1px solid #2e333e;
}

.uibox .boxtitle,
.uibox .listing
thread th,
.uibox .listing thread td {
	background: #2e333e;
	color: #d3dae3;
	border: 1px solid #2e333e;
	border-radius: 1px 1px 0 0;
}

.listsearchbox {
	background: #2e333e;
	padding: 3px;
}

a.iconbutton.searchicon {
	display: none;
	position: absolute;
	background-position: -2px -320px;
	height: 20px;
	width: 25px;
	top: 1px;
	bottom: 1px;
}


.listsearchbox {
	border: 1px solid #2e333e;
	padding: 1px;
}
.searchbox .iconbutton.reset {
	position: absolute;
	top: -2px;
	right: 8px;
}

#compose-contacts #directorylist {
	border-bottom: 1px solid #2e333e;
}


/*
Calendar section

*/

#calendars .treelist li.selected > div.calendar {
	background-color: #404552;
}

#calendars .treelist li span.calname {
	color: #d3dae3;
}

.cal-24917 .handle {
	color: #f25056;
}

li .cal-24917, #eventshow .cal-24917 {
	color: #f25056;
}

element.style {
	background-color: #f25056;
}

.ui-datepicker .ui-datepicker-header {
	background: #2e333e;
	background-color: #2e333e;
	border: 1px solid #2e333e;
	color: #d3dae3;
}

.ui-datepicker select.ui-datepicker-month, .ui-datepicker select.ui-datepicker-year {
	font-size: 12px;
	background: #2e333e;
	background-color: #2e333e;
}

/* scrollbar width */
::-webkit-scrollbar {
  background-color: #404552;
  width: 9px;
}

/* scrollbar Track */
::-webkit-scrollbar-track {
  box-shadow: inset 0 0 5px #404552; 
  border-radius: 9px;
}
 
/* scrollbar Handle */
::-webkit-scrollbar-thumb {
  background: #b0b9c6; 
  border-radius: 1px;
}

/* scrollbar Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #d3dae3; 
}

