    StScrollBar StButton#vhandle:hover, StScrollBar StButton#hhandle:hover {
      background-color: #676b78; }
    StScrollBar StButton#vhandle:active, StScrollBar StButton#hhandle:active {
      background-color: #5294e2; } */

/*
date picker column + row
*/

.dropdown:hover .dropdown-menu {
	display: block;
}

.dropdown-submenu {
	position: relative !important;
}

.dropdown-submenu>.dropdown-menu {
	top: 0 !important;
	left: 100% !important;
	margin-top: -6px !important;
	margin-left: -1px !important;
	border-radius: 0 !important;
}

.dropdown-submenu:hover>.dropdown-menu {
	display: block !important;
}

.dropdown-submenu>a:after {
	display: block;
	content: "\f105";
	margin-top: -18px;
	right: 15px;
	position: absolute;
}

.ui-datepicker-calendar .ui-datepicker-week-col {
	background-color: #404552;
	color: #d3dae3;
	border: 1px solid #404552;
}

.ui-datepicker-calendar th {
	background-color: #404552;
	color: #d3dae3;
	border: 1px solid #404552;
}

.ui-priority-secondary, .ui-widget-content .ui-priority-secondary, .ui-widget-header .ui-priority-secondary {
	background: #2e333e;
	background-color: #2e333e;
	color: #d3dae3;
	opacity: .7;
	filter: Alpha(Opacity=70);
	border: 1px solid #2e333e;
}

.ui-datepicker td a.ui-priority-secondary {
	background: #2e333e;
}

.ui-datepicker td a.ui-state-default {
	background: #383a4c;
	color: #d3dae3;
}

#datepicker .ui-datepicker-activerange a {
	color: #404552;
	background: #84afe3;
}

.ui-datepicker td a.ui-state-active {
	background: #5294e2 !important;
	border-color: #5294e2 !important;
}
}
