style.css:
@-moz-document domain("pillowfort.social") {
/*************************
darkcyan              - accent
#222                  - darker/background
#333                  - dark
white                 - white
rgba(0,0,0,.05)       - darken
rgba(255,255,255,.05) - lighten
*************************/

body {
  background-color: #222 !important;
  font-size: medium;
  color: white;
  font-family: Consolas, "Andale Mono WT", "Andale Mono", "Lucida Console", "Lucida Sans Typewriter", "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Liberation Mono", "Nimbus Mono L", Monaco, "Courier New", Courier, monospace;
  scrollbar-color: #333 #222;
  scrollbar-width: thin;
}

div.title {
  text-transform: uppercase;
  color: darkcyan;
}

.avatar img {
  padding: 0;
  border: none;
}

.side-info .timestamp {
  background-color: #222;
}

.blog-home-button {
  background-color: darkcyan;
  color: #222 !important;
}

.modal-content,
.modal-header,
.modal-footer {
  background-color: #222;
  border-color: #333;
  border-radius: 0;
}

button.close,
button.close:hover {color: white;}

.user-sidebar,
.user-sidebar .title,
.user-sidebar .util-buttons,
.user-sidebar .util-buttons a,
.user-sidebar #description,
.user-sidebar #description a,
.user-sidebar #metrics a {
  background-color: darkcyan !important;
  color: #222;
}

.user-sidebar .util-buttons a,
.user-sidebar #description a,
.user-sidebar #metrics a {
  font-weight: bold;
}

#userTopNavContainer #user-sidebar-toggle a#sidebar-expand-down,
#userTopNavContainer #user-sidebar-toggle a#sidebar-close {
  border-bottom: 1px solid;
}

.userNav,
#userTopNavContainer #user-sidebar-toggle a#sidebar-expand-down,
#userTopNavContainer #user-sidebar-toggle a#sidebar-close,
.separator,
input[type="text"],
select,
.btn-info {
  background-color: #222 !important;
  color: white !important;
  border-color: #333 !important;
}

.post .post-nav .nav-tab,
.post .post-nav .post-nav-right .tab-leftmost,
.nav-tab .ng-binding {
  border: none;
  color: white;
  background-color: #333;
}

.nav-tab:hover, .nav-tab:active {
  background-color: #333 !important;
  color: darkcyan !important;
  text-decoration: none;
}

.main,
.main .content,
.main .header,
.post .media,
.post-nav,
.post-nav-left,
.post-nav-right,
.tags-container,
.tags {
  border-color: #333 !important;
}

.main,
.post-type-container,
.post-type,
.post-type div,
#media-preview,
.main .content,
.main .content .ng-binding,
.main .tags-container div,
.post-nav,
.post-nav-left,
.post-nav-right {
  background-color: #333 !important;
  border-color: #222 !important;
}

.btn-primary {
  border-color: #222;
  background-color: darkcyan;
}

.btn-default:hover,
.btn-default:active,
.btn-default {
  border-color: #222;
  background-color: #333 !important;
  color: white !important;
}

.main .header,
.main .header div {
  background-color: darkcyan;
  color: #222;
}

.main .header .post-perma-link a:hover {
  text-decoration: none;
  color: #222;
}

.main .header div a {
  color: #222;
  font-weight: bold;
}

.comm-item .header,
.comms_list .header  {
  background-color: transparent !important;
  color: white;
}

.tags span {display: none;}
.tags span.ng-scope {
  display: inline;
}


.btn-primary,
.btn-success {
    color: #fff;
background-color: darkcyan;
border-color: black;
}

.btn-primary:hover,
.btn-success:hover {
  background-color: darkcyan;
  border-color: black;
}

.sidebar,
.sidebar-header,
.sidebar-expanded,
.sidebar-collapsed,
.sidebar-toggle-indicator, 
.sidebar-row,
.sidebar-boxes .first-row,
.sidebar-boxes .second-row,
.sidebar-boxes .sidebar-box {
  background-color: #222;
  color: white !important;
  border: none;
}

.box-metric, .box-text,
.sidebar-toggle-indicator .glyphicon {background-color: transparent}


.sidebar-row:hover,
.sidebar-row:active,
.sidebar-boxes .sidebar-box:hover,
.sidebar-boxes .sidebar-box:active,
.sidebar-toggle-indicator:hover,
.sidebar-toggle-indicator:active {
  background-color: #222;
  color: darkcyan !important;
text-decoration: none !important;
}

.sidebar {
  box-shadow: 0 0 5px black;
}

.sidebar-icon {
  background-color: #222;
  color: white !important;
}

.sidebar-icon:hover,
.sidebar-icon:active {
  color: darkcyan !important;
}

.user-sidebar #metrics .metric {
  color: darkcyan;
  background-color: #222;
}

.post-nav-right span button {border: none;}


.post .post-nav .nav-tab:hover {
  background-color: #222;
    color: darkcyan;
}

.follow-header {background-color: #222}
.follow-buttons, .follow-info {background-color: #333;}

.community .forum .preview {border: none;}
.community .forum .preview .title,
.community .forum .preview .title a {
  background-color: #333;
color: white !important;
}

.community .forum .preview,
.community .forum .preview .content,
.community .forum .preview .comment-div,
.community .forum .preview .ng-binding {
  background-color: #333 !important;
  color: white !important;
  border: none;
}

a,
a:link,
a:visited {color: darkcyan;}

a:hover,a:active {text-decoration: underline;}

blockquote {
  background-color: rgba(255,255,255,.05);
  color: white !important;
  font-style: italic;
  border-left: 1px solid darkcyan !important;
}

textarea {
  background-color: white;
  color: black;
}


div#navbar-bumper {
  margin-bottom: 53px !important;
}
}
