style.css:
@-moz-document domain(hdrezka.me), domain(hdrezka.cm), domain(hdrezka.ag), domain(rezka.ag)
{
/*rebuild*/
.b-post, .b-wrapper, #main, #wrapper, body {
    margin: 0 auto !important;
	padding: 0 !important;
    width: 100% !important;
	box-sizing: border-box !important;
}

.b-content__description, .b-content__inline_items, #topnav-menu {
	padding-left: 10px !important;
	width: 100% !important;
	box-sizing: border-box !important;
}

.b-content__columns {
	padding-top: 0px !important;
	padding-bottom: 0px !important;
    padding: 10px !important;
	width: 100% !important;
	box-sizing: border-box !important;
}

.b-content__main {
    display: table !important;
}

.b-translators__block + div,  .b-post__lastepisodeout + div {
    display: table-caption !important;
}

#cdnplayer, #cdnplayer-container  {
    width: 1000px !important;
    height: 562px !important;
	margin: 0 auto !important;
}

.b-tophead {
    height: 20px !important;
	line-height: 5px !important;
}

.fixed-header .b-topnav_wrapper  {
    height: 28px !important;
}

.b-footer__inner {
    padding: 60px 0 0 0 !important;
}

.b-post__support_holder_report .append, div.b-post__mixedtext + div,  .b-post__mixedtext, .b-search__submit, .b-content__htitle, .b-tophead__subscribe-dropdown-inner:before, .b-post__support_holder, .b-tg__wrapper, .b-tracker__main__announce, .b-post__status_wrapper, .b-post__mixedtext, .b-post__lastepisodeout, .b-content__main > style + div, .b-post__status_wrapper, div.b-content__main + div {
	display :none !important;
}


/*recolor background*/
.b-newest_slider__inner, #hd-comments-list>.comments-tree-list .comments-tree-list, .b-search__live, .tooltipster-default, .b-tophead-dropdown ul, .b-topnav__sub_inner, body {
    background: #000 !important;
	color: #aaa !important;
}

.b-person__popup_photo, .b-post__qa_list_link, .b-post__schedule_block_title, .comments-form, .b-search__live_all, .b-sidecover, .b-post__social_holder, .b-person__popup_holder_photo .overlay, .b-person__popup_content .stats a, .b-tophead-dropdown.hovered, .b-tophead_wrapper, .b-translators__block, .b-videosaves__list_item .new-episode.own, .b-seriesupdate__block_date.collapsible.expandable, .b-seriesupdate__block_list_item, .b-seriesupdate__block_date, .b-newest_slider, .b-newest_slider__list, .b-collections__newest, .b-newest_slider_wrapper, .b-content__inline_item-cover, .b-wrapper, #top-nav, body.has-brand #wrapper, #wrapper {
    background-color: rgba(0,0,0,0) !important;
}

.b-post__schedule_table tr:hover td, .b-post__partcontent_item:hover .td, .b-post__partcontent_item.current:hover .td, .b-search__section_list li.hovered, .b-tophead-dropdown a:hover, .b-videosaves__list_item:hover .td, .b-topnav__sub_inner a:hover, .b-seriesupdate__block_list_item:hover {
    background-color: rgba(255,255,255,0.2) !important;
	color: #aaa !important;
}

.btn-helper, .btn-action, .title_spoiler a, #hd-comments-list>.comments-tree-list>li:nth-child(odd) .comments-tree-item, #dlemasscomments #dle-comments-list>div:nth-child(odd) .comments-tree-item, #hd-comments-list>.comments-tree-list>li:nth-child(odd), #dlemasscomments #dle-comments-list>div:nth-child(odd), .b-search__form, .b-search__field {
	background-color: #111 !important;
}

.btn-helper:hover, .btn-action:hover, #hd-comments-list>.comments-tree-list>li:nth-child(even) .comments-tree-item, #dlemasscomments #dle-comments-list>div:nth-child(even) .comments-tree-item, #hd-comments-list>.comments-tree-list>li:nth-child(even), #dlemasscomments #dle-comments-list>div:nth-child(even), .b-comment__admin  {
	background-color: #222 !important;
}

/*recolor text*/
.b-post__info_rates, .b-comment, .b-post__qa_list_item .title, .b-post__schedule_table, .b-search__field, .bold, .b-videosaves__list_item .td, .b-seriesupdate__block_list_item_inner .cell-2, .b-newest_slider__title {
	color: #aaa !important;
}

.btn-helper, .b-comment__like_it i, .b-comment__quoteuser, .title_spoiler a, .b-post__qa_list_item .title:hover, .b-comment .name, .b-search__live_all, .b-post__info .l, .b-person__popup_content .stats a, .b-tophead-dropdown, .persons-list-holder .person-name-item a, .b-videosaves__list_item:hover .td, .user-things a .count, .b-newest_slider__title span, .b-topnav__item-link, a {
	color: #bbb !important;
}

.b-user__settings .sep-title, .btn-helper:hover, .b-comment__like_it i:hover, .b-comment__quoteuser:hover, .b-post__rating .num, .b-post__partcontent_item.current .title, .b-search__section_list .enty, .b-search__live_all:hover, .b-person__popup_content .stats a:hover, .b-tophead-dropdown.hovered, .persons-list-holder .person-name-item a:hover, .b-videosaves__list_item:hover .td a, .user-things a:hover .count, .b-seriesupdate__block_date.collapsible.expandable:hover .act, .b-seriesupdate__block_date.collapsible.expandable, .b-seriesupdate__block_date.collapsible, .b-seriesupdate__block_date:hover .act, .b-newest_slider__title span:hover, .b-topnav__item-link:hover, .b-topnav__item-link.active, .b-topnav__item-link.active_section, a:hover, a:active {
	color: #fff !important;
}

.b-comment .date, .b-post__social_holder .share-label, .persons-list-holder .item, .b-post__description_text, .b-videosaves__list_item .new-episode, .b-seriesupdate__block_date.collapsible.expandable .act, .b-seriesupdate__block_date .act,  .b-seriesupdate__block_list_item_inner .cell-1  {
	color: #777 !important;
}


/*hide borders*/
.user-things a:hover .count, .b-seriesupdate__block_date:hover .act, .b-topnav__item-link:hover, .b-topnav__item-link.active, .b-topnav__item-link.active_section, .b-topnav__item-link, a, a:hover, a:active {
	text-decoration: none !important;
}

.b-person__popup_photo, .btn-helper, .btn-action, .b-comment__like_it i, .b-comment__quoteuser, .title_spoiler a, .b-post__qa_list_item .inner, .b-post__schedule_table td, .fixed-header .b-topnav_wrapper.shi, .fixed-header .b-topnav_wrapper.shm, .b-search__section_list a, .b-sidecover, .b-person__popup_holder_photo .overlay, .tooltipster-default, .b-tophead-dropdown, .b-tophead-dropdown ul, .b-tophead-dropdown.hovered, .persons-list-holder .person-name-item a, .td, .th, .b-topnav__sub_inner a, .b-topnav__sub_inner, .b-seriesupdate__block_list_item, .b-navigation, .b-newest_slider__title span, .b-content__inline_item-cover, #top-nav {
	border: none !important;
	box-shadow: none !important;
	border-radius: 0 !important;
}

/*filter*/
.b-rating, .ya-share2__list_direction_horizontal > .ya-share2__item, .b-post__rating_layer{
	filter: grayscale(100%);
}

/*other*/
.b-videosaves__list_item .td.controls .view:hover {
    filter: alpha(opacity=100) !important;
    opacity: 1 !important;
}

.b-videosaves__list_item .td.controls .view {
    filter: alpha(opacity=60) !important;
    opacity: .6 !important;
}

.persons-list-holder .person-name-item a {
	-webkit-transition: none !important;
    -moz-transition: none !important;
    -ms-transition: none !important;
    transition: none !important;
}
}
