style.css:
/* i really want this to be global */
@-moz-document domain("stepik.org") {
.lesson.lesson-modern a {
    color: #6c6 !important;
}
.page-footer-modern {
    background: #333 !important;
    color: #fff !important;
}
.lesson-modern__top-panel {
    background: #333 !important;
    color: #fff !important;
}
body {
    background: #333;
}
body * {
    color: #fff;
}
body a {
    color: #6c6 !important;
}
.toggle__title {
    background: #333 !important;
}
.toggle__title__btn {
    color: #fff !important;
}
.st-tabs, .leaders-list * {
    background: #333 !important;
    color: #fff !important;
}
.lesson.lesson-modern *:not(.vjs-text-track-display) {
    background: #333;
    color: #fff !important;
}
.course-preview__wrapper * {
    color: #333 !important;
}
.vjs-text-track-display * {
    background: none !important;
}
.course-join-button a {
	color: #333 !important;
}
.cert-widget * {
	color: #fff !important;
}
.progress-circle * {
	color: #6c6 !important;
}
.down-arrow_icon * {
	color: #333;
}
}
