/* ==UserStyle==
@name           Black Wikipedia
@namespace      github.com/martin-juul/userstyles
@version        1.0.0
@description    Black theme for wikipedia
@author         Martin Juul
==/UserStyle== */

@-moz-document domain("wikipedia.org") {
    * {
        /* Set to subpixel-antialiased on crap displays */
        -webkit-font-smoothing: antialiased;
    }

    body.mediawiki, #mw-page-base, #mw-head,
    #mw-head-base, #content, #content .gallerybox div.thumb,
    div.thumbinner {
        background-color: #000;
    }

    li.gallerybox div.thumb, div.thumbinner {
        border-color: #292828;
        border-radius: 6px;
    }

    #siteSub, .hatnote, li, dl, dd, #searchInput {
        color: #ccc;
    }

    /* Search */

    #searchInput {
        background-color: #000;
        border-radius: 12px;
        border-color: #161616;
    }

    .suggestions-result, .suggestions-results, .suggestions-special {
        background-color: #000;
        color: #fff;
        border-radius: 6px;
        border-color: #222;
    }

    .suggestions-special {
        border-radius: unset;
    }

    /* Links */
    a, .vectorTabs li a, #mw-panel .portal .body li a:visited, label.toctogglelabel {
        color: #f59f00;
    }

    a:visited, #mw-panel .portal .body li a:visited {
        color: #ffcd5a;
    }

    /* Top buttons (article, talk, read, edit, view history) */
    .vectorTabs {
        background-image: unset !important;
    }

    .vectorTabs li {
        background-image: unset !important;
        background-color: #0b0b0b !important;
    }

    .vectorTabs li.selected {
        background-color: #222 !important;
    }

    .vectorTabs .selected a, .vectorTabs .selected a:visited, .mw-content-ltr .thumbcaption {
        color: #fff;
    }

    /* Left Nav */
    .mw-interlanguage-selector {
        border-color: #4d4c4c;
        background-color: #000;
        color: #fff;
        border-radius: 15px;
    }

    /* Article */
    h1, h2, h3, h4, h5, h6, p {
        color: #fff;
    }

    table {
        background-color: #000 !important;
        color: #fff !important;
        border-color: #4d4c4c !important;
        border-radius: 6px;
        padding: 12px;
        border-width: 1.33px !important;
    }

    th {
        background-color: #000 !important;
    }

    /* ToC */

    #toc {
        background-color: #000;
        border-color: #4d4c4c !important;
        border-radius: 6px;
        border-width: 1.33px !important;
    }

    .tocnumber {
        color: #ccc;
        font-weight: 600;
    }
}
