style.css:
@-moz-document url-prefix("https://swing.service-now.com") {
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - MAIN VARIABLES */
:root
{
    /* Color variables */
    --bg1: #182838;
    --bg2: #323f50;
    --bg3: #4d5867;
    --bg4: #405264;
    --bg5: #2c3746;
    --txt1: #d5d5d5;
    --txt2: #008aff;
    --txt3: #DE4333;
    --txt4: #71E279;
    --t: yellow;

    /* Variable used for border radius */
    --brad: 5px;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - ALL */
/* Root background for things like refreshing the page */
html,
iframe
{
    background-color: var(--bg1) !important;
}

/* Make all placeholders the light text color */
*::placeholder
{
    color: var(--txt1) !important;
}

/* Remove all weird shaows, because that was f*ugly */
*
{
    -webkit-box-shadow: none !important;
    -moz-box-shadow: none !important;
    box-shadow: none !important;
}

/* Set bright white color to lesser white color*/
.color-white
{
    color: var(--txt1) !important;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - NOTIFICATION BANNER */
/* Banner body */
.outputmsg,
.fieldmsg
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border: none !important;
    margin-bottom: 0px !important;
}

/* Message link */
.web
{
    color: var(--txt2) !important;
}

/* Close button */
#close-messages-btn
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    margin: 0px !important;
    margin-right: 5px !important;
    margin-top: 1px !important;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - TOP NAVBAR */
/* Set background of top bar */
.navbar.navbar-default.sn-frameset-header,
.navpage-header
{
    background-color: var(--bg1) !important;
}

/* Remove empty space which will allign the buttons in the center */
.navbar.navbar-default.sn-frameset-header > div > .navbar-header,
.navbar-divider
{
    display: none !important;
}

/* Remove padding from main navbar */
.sn-frameset-header
{
    padding: 0px !important;
    margin-top: 3px !important;
}

/* Buttons except search */
.navpage-header-content > button,
.navpage-header-content > div > button
{
    margin-top: 3px !important;
    background-color: var(--bg2) !important;
    border-radius: var(--brad) !important;
    color: var(--txt1) !important;
}

.navpage-header-content > button:hover,
.navpage-header-content > div > button:hover
{
    background-color: var(--bg4) !important;
    color: var(--txt1) !important;
}

.navpage-header-content > div > button
{
    margin-right: 3px !important;
    color: var(--txt1) !important;
}

/* Search button */
.sysparm-search-icon
{
    background-color: var(--bg2) !important;
    margin-top: 2px !important;
    color: var(--txt1) !important;
}

.sysparm-search-icon:hover::before,
.sysparm-search-icon:hover
{
    background-color: var(--bg4) !important;
    color: var(--txt1) !important;
}

#user_info_dropdown
{
    border: none !important;
}

/* Search bar */
#sysparm_search
{
    background-color: var(--bg2) !important;
    border: 0px !important;
    border-radius: var(--brad) !important;
    margin-top: 3px !important;
    padding-left: 35px !important;
    color: var(--txt1) !important;
}

/* User profile dropdown */
.navpage-header-content > div > ul
{
    background-color: var(--bg4) !important;
}

.navpage-header-content > div > ul > li > a
{
    color: var(--txt1) !important;
}

.navpage-header-content > div > ul > li > a:hover
{
    background-color: var(--bg2) !important;
}

/* Notification badge */
.sn-badge
{
    background-color: var(--txt2) !important;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - SIDE NAV */
/* Entire navbar */
#nav_west
{
    border: none !important;
}

#nav_west_center
{
    background-color: var(--bg2) !important;
}

/* Filter bar div*/
.navigator-filter-form
{
    border: none !important;
}

/* Filter bar */
#filter
{
    background-color: var(--bg2) !important;
    border: none !important;
    border-radius: var(--brad) !important;
    color: var(--txt1) !important;
}

/* Top navbar div*/
#nav_west_north > form
{
    border: none !important;
    background-color: var(--bg1) !important;
}

/* Filter icon*/
#nav_west_north > form > div > div > label
{
    color: var(--txt1) !important;
}

/* Bottom navbar div*/
#nav_west_south
{
    border: none !important;
    background-color: var(--bg1) !important;
}

/* Filter bar remove search button*/
#nav_filter_controls
{
    color: var(--txt1) !important;
}

/* Navbar buttons */
#history_tab,
#allApps_tab,
#favorites_tab,
#nav_west_north > div
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
    border: none !important;
}

/* Navbar favorites */
.nav-favorites-list > * > * > * > *,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > a,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > ul > li > div > a,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > ul > li > div > div > a,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > ul > li > div > ul > li > div > a
{
    border: none !important;
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border-bottom: 1px solid var(--bg1) !important;
}

/* Navbar favorite text */
.sn-widget-list-content > span,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > a,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > ul > li > div > a,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > ul > li > div > a > span,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > ul > li > div > ul > li > div > a,
.sn-aside-filler.navigator-view.sn-widget-list_indentation.ng-isolate-scope > ul > li > ul > li > div > div > a > div > div > a
{
    color: var(--txt1) !important;
}

/* Remove devider in sidenav filter */
.sn-widget-list-divider
{
    display: none !important;
}

/* Loading spinning animation color */
.sn-widget.sn-widget_padding-top.sn-widget-textblock.sn-widget-textblock_large.sn-widget-textblock_center.navigator-loading-message
{
    color: var(--txt1) !important;
}

/* Favorite tab buttons */
#magellan_tablist > a,
#magellan_tablist > a::before
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
}

/* Active favoite tab*/
#magellan_tablist > a.state-active::before
{
    color: var(--txt2) !important;
}

/* Other nav buttons (minimise, expand, filter, edit) */
.sn-pane-action.icon-edit.nav-settings.default-focus-outline,
.sn-pane-footer-content > .sn-pane-action.icon-arrow-left-rounded,
.sn-pane-footer-content > .sn-pane-action.icon-arrow-right-rounded,
.sn-live-search-flex > div > span.input-group-addon-transparent.icon-filter.default-focus-outline
{
    color: var(--txt1) !important;
}

.sn-pane-action.icon-edit.nav-settings.default-focus-outline:hover,
.sn-pane-footer-content > .sn-pane-action.icon-arrow-left-rounded:hover,
.sn-pane-footer-content > .sn-pane-action.icon-arrow-right-rounded:hover,
.sn-live-search-flex > div > span.input-group-addon-transparent.icon-filter.default-focus-outline:hover
{
    background-color: var(--bg2) !important;
}


/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - TOP TASK NAV */
/* General navbar background */
[id^='list_nav']
{
    background-color: var(--bg1) !important;
    padding: 0px !important;
    border: none !important;
}

/* Left search label */
.list_search_title,
.list_row_number_input,
.navbar-title.list_title
{
    color: var(--txt1) !important;
    font-weight: normal !important;
}

/* Left nav title */
.navbar-title
{
    display: none !important;
}

/* Page indicator */
.list_row_number_input.form-control,
.task_hide_search > div > div > *
{
    background-color: var(--bg2) !important;
    border: none !important;
}

/* Left search bars */
#task_hide_search > div > div > input,
#task_hide_search > div > div > span > span > select
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border: none !important;
}

/* Activity stream button */
.navbar-btn.list_stream_button
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
}

.navbar-btn.list_stream_button:hover
{
    background-color: var(--bg4) !important;
    color: var(--txt1) !important;
}

/* Navigation arrow buttons */
.list_nav.btn
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    margin-left: 3px !important;
}

.list_nav.btn:hover
{
    background-color: var(--bg4) !important;
    color: var(--txt1) !important;
}

/* Select arrow */
.select-indicator.icon-vcr-down
{
    color: var(--txt1) !important;
}

/* Left menu button */
.btn.btn-default.icon-menu.navbar-btn
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border: none !important;
}

.btn.btn-default.icon-menu.navbar-btn:hover
{
    background-color: var(--bg4) !important;
}

/* Breadcrumb menu */
.breadcrumb_container > *,
.breadcrumb_container
{
    background-color: var(--bg1) !important;
    color: var(--txt2) !important;
}

/* Hide breadcrumb menu content, since i can always click the button for the filter info */
.breadcrumb_container > span
{
    display: none !important;
}

/* Filter button */
.breadcrumb_container > div > a
{
    color: var(--txt2) !important;
    margin: 0px !important;
    padding: 4px !important;
    border: none !important;
}

.breadcrumb_container > div > a:hover
{
    background-color: var(--bg2) !important;
}

/* Table headers */
tr[id^='hdr'] > * > * > *,
tr[id^='hdr'] > * > *,
tr[id^='hdr'] > *,
tr[id^='hdr']
{
    background-color: var(--bg1) !important;
    color: var(--txt2) !important;
    border: none !important;
    font-weight: normal !important;
}

/*Hide search header in ci items for example*/
tr[class^='list_header'] > * > * > *,
tr[class^='list_header'] > * > *,
tr[class^='list_header'] > *,
tr[class^='list_header']
{
    display: none !important;
}

div[class=' ']
{
    background-color: var(--bg1) !important;
}

/* Table header bar */
.list_header_cell.list_hdr,
.col-control.list-decoration-table,
.col-control,
#clone_table
{
    border: none !important;
}

/* Gear icon in table headers */
.icon-cog-changes.btn.btn-icon.table-btn-lg
{
    margin-left: 6px !important;
    padding: 4px !important;
}

.icon-cog-changes.btn.btn-icon.table-btn-lg:hover
{
    background-color: var(--bg2) !important;
}

/* Activity loading */
.doctype-stream.list-stream.ng-scope
{
    background-color: var(--bg5) !important;
}


/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -COLLUMN MENU */
/* Menu background */
.modal-content,
.modal-header
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
    border: none !important;
}

/* Menu close button */
.modal-header > button,
.modal-header > div > button
{
    background-color: var(--bg1) !important;
}

/* Menu central buttons */
.modal-header > button::before,
.modal-header > div > button::before
{
    background-color: transparent !important;
    color: #FFFFFF !important;
}

.modal-header > button:hover,
.modal-header > div > button:hover
{
    background-color: var(--bg2) !important;
}

/* Menu footer buttons */
div.button-column > a,
.modal-footer > span > button
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border: none !important;
}

div.button-column > a:hover,
.modal-footer > span > button:hover
{
    background-color: var(--bg3) !important;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - TASK LIST */
/* Task list background */
.windows.chrome
{
    background-color: var(--bg1) !important;
}

/* Empty record notice */
.list2_no_records,
.list2_no_records > td
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
    border: none !important;
    font-weight: normal !important;
}

/* List row */
.list_row > * > *,
.list_row > *
{
    color: var(--txt1) !important;
    background-color: var(--bg2) !important;
}

.list_row > *
{
    border: none !important;
    border-bottom: 1px solid var(--bg1) !important;
}

.list_row.hover > * > *,
.list_row.hover > *
{
    background-color: var(--bg4) !important;
}

.list_decoration_cell.col-control.col-small.col-center
{
    border-left: 1px solid var(--bg1) !important;
}

/* Bottom task actions */
#task_expanded > div,
.list_nav.list_nav_bottom > * > *
{
    background-color: var(--bg1) !important;
}

/* Action menu */
.list_action_option.form-control
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border: none !important;
    border-radius: var(--brad) !important;
}

/* Task expand button */
.list_popup
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
    border: none !important;
    border-radius: var(--brad) !important;
}

.list_popup:hover
{
    background-color: var(--bg2) !important;
}

/* Text of request stage */
span[id^='stage'] > li > div
{
    color: var(--txt1) !important;
}

/* Priority indicators */
/* Critical */
.list2_cell_background[style="background-color:tomato"]
{
    background-color: tomato !important;
}

/* High */
.list2_cell_background[style="background-color:orange"]
{
    background-color: orange !important;
}



/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - FORM HEADER */
/* Form header */
#incident\.form_header > * > * > *,
.navbar.navbar-default.section_zero.ng-scope
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
    border: none !important;
}

/* Header buttons */
.additional-actions-context-menu-button,
.btn.btn-default.icon-chevron-left.navbar-btn,
#connectFollowWidgetAction,
.btn.btn-default.default-focus-outline.dropdown-toggle,
#sysverb_update,
#sysverb_update_bottom,
#incident_update_and_stay,
#header_add_attachment,
#form_stream,
#toggleMoreOptions,
.btn.btn-icon.icon-arrow-up,
.btn.btn-icon.icon-arrow-down,
.form_action_button.header.action_context.btn.btn-default
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border: none !important;
    height: 25px !important;
}

.additional-actions-context-menu-button:hover,
.btn.btn-default.icon-chevron-left.navbar-btn:hover,
#connectFollowWidgetAction:hover,
.btn.btn-default.default-focus-outline.dropdown-toggle:hover,
#sysverb_update:hover,
#sysverb_update_bottom:hover,
#incident_update_and_stay:hover,
#header_add_attachment:hover,
#form_stream:hover,
#toggleMoreOptions:hover,
.btn.btn-icon.icon-arrow-up:hover,
.btn.btn-icon.icon-arrow-down:hover,
.form_action_button.header.action_context.btn.btn-default:HOVER
{
    background-color: var(--bg4) !important;
    color: var(--txt1) !important;
    border: none !important;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - FORM */
/* Labels */
.label-text
{
    color: var(--txt1) !important;
}

/* Required marker */
.required-marker::before
{
    animation: blinker 2s linear infinite;
}

/* Make required marker blink grey, so that if it's already filled out it won't appear to blink*/
@keyframes blinker
{
    50%
    {
        color: #303a46;
    }
}

/* Text fields */
.form-control
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border: none !important;
}

/* Form buttons */
button[id^='lookup'],
a[id^='lookup'],
button[id^='viewr'],
button[id^='sc_req_item'],
span[id^='show_map'],
a[id^='show_incidents'],
a[id^='ni'],
.btn.btn-default.icon-book.knowledge,
.icon-stream-one-input.btn-default.btn,
#activity_field_filter_button,
button[id^='add_me_locked']
{
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
    border: none !important;
    height: 25px !important;
    width: 25px !important;
}

button[id^='lookup']:hover,
a[id^='lookup']:hover,
button[id^='viewr']:hover,
button[id^='sc_req_item']:hover,
span[id^='show_map']:hover,
a[id^='show_incidents']:hover,
a[id^='ni']:hover,
.btn.btn-default.icon-book.knowledge:hover,
.icon-stream-one-input.btn-default.btn:hover,
#activity_field_filter_button:hover,
button[id^='add_me_locked']:hover
{
    background-color: var(--bg4) !important;
    color: var(--txt1) !important;
    border: none !important;
}

/* Disabled fields */
.form-control.disabled
{
    background-color: var(--bg3) !important;
    border: none !important;
    color: var(--txt1) !important;
    cursor: default;
}

/* Notes/Comments */
.comment-box
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
    border: none !important;
}

/* Header attachment list */
#header_attachment_list > * > * > *,
#header_attachment_list > * > *
{
    color: var(--txt2) !important;
}

/* Header attachment line */
#header_attachment_line
{
    display: none !important;
}

/* Remove vip icon */
[style="background-image: url(\"images/icons/vip.gif\"); background-repeat: no-repeat; background-position: 5% 45%; padding-left: 30px;"]
{
    background-image: none !important;
}

/* Highlighted field */
.form-control[style="color: red;"]
{
    color: var(--bg1) !important;
    background: rgb(111, 209, 90);
    background: linear-gradient(90deg, rgba(111, 209, 90, 1) 0%, rgba(50, 63, 80, 1) 90%);
}

/* Post coment button */
.btn.btn-default.pull-right.activity-submit
{
    background-color: var(--bg2) !important;
}

.btn.btn-default.pull-right.activity-submit:hover
{
    background-color: var(--bg4) !important;
}

/* Item list (Comments/Vars/ETC) buttons*/
.tabs2_tab
{
    background-color: var(--bg2) !important;
    border: none !important;
    border-radius: 3px !important;
    margin-right: 5px !important;
    padding: 2px !important;
}

.tabs2_tab > *
{
    color: var(--txt1) !important;
}

.tabs2_tab:hover
{
    background-color: var(--bg4) !important;
}

/* Comment list */
.sn-form-stream-init
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
}

/* Comment list box border */
.section
{
    border: none !important;
}

/* Comment card*/
.h-card.h-card_md.h-card_comments,
.sn-card-component-createdby,
.sn-card-component,
.date-timeago,
.sn-card-component.sn-card-component_records > * > * > * > *,
.sn-widget-textblock-body.sn-widget-textblock-body_formatted
{
    border: none !important;
    background-color: var(--bg2) !important;
    color: var(--txt1) !important;
}

.stream-action
{
    color: var(--txt2) !important;
}

/* Variables header */
.veditor_header
{
    background-color: var(--bg1) !important;
    color: var(--txt1) !important;
    margin: 0px !important;
}

/* Variables body */
.iotd,
