style.css:
@-moz-document domain("forreadingaddicts.co.uk") {
html {
    --color-black: #000;
    --color-dark-grey: #121212;
    --color-medium-grey: #212121;
    --color-light-grey: #bfbfbf;
    --color-white: #FFF;
    --color-red: #d74b1f;
    --color-dark-red: #b33d19;
    --color-green: #008000;
    --color-blue: #7f7fff;
}

/* Header */
body #header-outer,
body[data-hhun="1"]:not(.no-scroll) #header-outer:not([data-transparent-header="true"]):not(.detached),
body[data-hhun="1"]:not(.no-scroll) #header-outer[data-permanent-transparent="false"][data-using-secondary="1"]:not([data-transparent-header="true"]),
.content-block,
.product-recommendations,
.full-width--return-link,
.sticky-display,
#bundle-sticky_add_to_cart,
.site-header .meganav.meganav--active,
.site-nav__dropdown--third-level.meganav--active {
    background-color: var(--color-dark-grey) !important;
}

/* Body */
.container-wrap,
.post-area.masonry.classic_enhanced .post.regular .article-content-wrap,
.post-area.masonry.classic_enhanced .post .post-meta,
.page-header-no-bg,
.home-wrap,
.comment-list .comment >div, .comment-list .pingback >div,
.is-moved-by-drawer,
.loox-pn-review,
.loox-pn-product,
.product-card,
.product-card__info,
.vitals-nice-select.right,
.site-nav__dropdown-container.meganav--active {
    background-color: var(--color-medium-grey) !important;
}

.modal--is-active .modal__inner {
    background: transparent !important;
}

/* Menu icon */
.slide-out-widget-area-toggle i,
/* Divider line between posts */
.divider-border {
    background-color: var(--color-light-grey) !important;
}

.snax a.snax-voting-upvote:hover {
    background-color: var(--color-green) !important;
}

.snax a.snax-voting-downvote:hover {
    background-color: var(--color-red) !important;
}

.main-content tr {
    color: var(--color-black) !important;
}

.snax-quiz-answer-label-text,
.snax-quiz-answer-label:before {
    color: var(--color-dark-grey) !important;
}

/* Select option*/
option,
.vitals-nice-select .option {
    background-color: var(--color-black) !important;
    color: var(--color-white) !important;
}

/* Search icon */
.mobile-search .icon-salient-search,
#sidebar h4,
span,
form,
.masonry.classic_enhanced .post .excerpt,
.masonry.classic_enhanced .post .post-meta a:hover,
body .nectar-love-wrap:hover .total_loves span,
#header-outer #top nav > ul > li > a,
.single .blog-title #single-meta div i.icon-default-style,
.single #single-meta .meta-comment-count a,
.single #single-meta div:hover > a,
.single #single-meta div a:hover i,
.single .blog-title #single-meta div i,
.nectar-fancy-ul ul li .icon-default-style[class^="icon-"],
.comment-list .comment-meta a,
.snax a.snax-voting-upvote:hover span,
.product-form__item label,
.modal__close .icon {
    color: var(--color-light-grey) !important;
}

body a,
.collection-sort__label {
    color: var(--color-light-grey) !important;
    font-weight: bold;
}

.single.single-post,
.single.single-post .heading-title[data-header-style="default"] h1,
body.single .post .wpb_text_column h2,
.sharing-default-minimal .nectar-love i,
.sharing-default-minimal .nectar-social .love-text,
.masonry.classic_enhanced .post .post-meta a,
body .nectar-love-wrap .total_loves span,
div,
body h1,
body h2,
body h3,
body h4,
body a,
body a:hover,
body p,
.wpcf7-form p span,
.row .col h3,
#respond h3,
.nectar-button span,
.collection-sort__input,
.product-form__item span,
.product-card__overlay-btn,
.meganav__link,
._5v3q ._5pbx .text_exposed_root>p {
    color: var(--color-white) !important;
}

.masonry.classic_enhanced .post .post-meta a:hover i,
.home .blog-recent .col .post-header h3 a {
    color: var(--color-blue) !important;
}

.single .blog-title #single-meta > div > a,
.blog-title #single-meta .nectar-social.hover > div a,
.blog-title #single-meta .nectar-social.hover .share-btn {
    border: 2px solid var(--color-light-grey);
}

.product-card {
    border: none;
}

.vitals-nice-select.right  {
    border: none;
}

/* Little articles on bottom */
body:not(.ascend) .masonry.classic_enhanced .post .content-inner,
.post-area.masonry.classic_enhanced .post .post-meta {
    border-left: 1px solid var(--color-light-grey);
    border-right: 1px solid var(--color-light-grey);
}

#sidebar .widget.widget_categories li a,
body:not(.ascend) .masonry.classic_enhanced .masonry-blog-item .inner-wrap {
    border-bottom: 1px solid var(--color-light-grey);
}

.post-area.masonry.classic_enhanced .post .post-meta {
    border-top: 1px solid var(--color-light-grey);
}

.blog-title #single-meta .nectar-social.hover > div a:hover,
.single #single-meta div a:hover,
meta-comment-count:hover {
    border-color: var(--color-blue) !important;
}

.table-responsive a,
.meganav__link:hover {
    color: var(--color-red) !important;
}

.table-responsive a:hover {
    color: var(--color-dark-red) !important;
}
}
