style.css:
@-moz-document domain("rumble.com") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #74a441 #1c1c1c;
}
*{
    box-shadow: none !important;
}
input,textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border-color: #74a441 !important;
}
/*HEADER*/
.header-search-select, .header-search-field{
    background: none !important
}
/*HEADER*/
/*MAIN-PAGE*/
.module-title{
    color: #74a441;
}
.container{
    background: #1c1c1c;
    border-color: #74a441;
}
.mediaList-link-more a,.promo-widget-link{
    background: #1c1c1c;
    border-color: #74a441;
    color: #74a441;
}
.mediaList-link-more a:hover{
    color: #1c1c1c;
}
.mediaList-by-heading,.forum-post-meta,.promo-widget-link:hover,small{
    color: #fff;
    text-decoration: none;
}
/*MAIN-PAGE*/
/*ARTICLE-VIDEO*/
.rumbles-vote,.comment-count{
    color: #74a441;
}
.rumbles-vote-up{
    background: green;
}
.rumbles-vote-down{
    background: #a80202;
}
.media-video-action{
    background: #1c1c1c;
    border: 1px solid #74a441;
}
.media-icon-license, .media-icon-embed,svg.user-image--icon{
    stroke: #74a441;
}
svg.user-image--icon{
    background: #1c1c1c;
    border-color: #74a441;
}
.round-button{
    background: #1c1c1c;
    border: 1px solid #74a441;
    color: #74a441;
}
.round-button:hover{
    background: #74a441;
    color: #1c1c1c;
}
/*ARTICLE-VIDEO*/
/*SUBPAGES*/
/*HELP*/
.book-body,#book-search-input{
    background: #1c1c1c;
    color: #74a441;
}
.markdown-section h1,.markdown-section h2,.book-summary ul.summary li a,#book-search-results .search-results .has-results .search-results-item a{
    color: #74a441;
}
.markdown-section,.book-summary ul.summary li ul li a,#book-search-results .search-results .has-results .search-results-item{
    color: #fff;
}
.book-summary{
    background: #1c1c1c;
    border-right: 1px solid #74a441;
}
.markdown-section table thead tr,.markdown-section table tbody td{
    background: #1c1c1c;
}
.markdown-section table thead th{
    color: #74a441;
}
.markdown-section code{
    background: #1c1c1c;
    color: purple
}
.markdown-section pre{
    background: #1c1c1c;
    border: 1px solid #74a441;
}
/*HELP*/
/*CORPORATE*/
.header{
    background: #1c1c1c;
    border-bottom: 1px solid #74a441;
}
.header .rumble-logo{
    background: #fff;
    border: 2px solid #74a441;
}
.header .main-nav a,.corp-section .section-titles h3,.solution-lineup .third-section h4,.brand-box .stats .stats-wrapin .big{
    color: #74a441;
}
.header .main-nav a:hover,.brand-box .content p,.brand-box .stats .stats-wrapin .small{
    color: #fff;
}
.solution-lineup,.solution-lineup .check-list .checkmark,.brand-box{
    background: #1c1c1c;
}
.solution-lineup .check-list .container:hover input ~ .checkmark{
    background: #74a441;
}
.timeliny .timeliny-dot::after{
    background: #1c1c1c;
    border: 1px solid #74a441;
    color: #fff;
}
/*CORPORATE*/
/*VIDEO*/
.listing-header--white-bg{
    background: #1c1c1c;
}
.video-item,.tuner-box--section,.paginator--link{
    background: #1c1c1c;
    border: 1px solid #74a441 !important;
}
.video-item:hover{
    background: #74a441;
}
.tuner-box--section-header{
    color: #74a441;
}
.tuner-box--link--current,a.tuner-box--link:hover,.paginator--link--current,.paginator--link:hover{
    background: #74a441;
    color: #1c1c1c !important;
}
.tuner-box--link{
    color: #fff;
}
.paginator--ul{
    color: #fff;
    border-radius: 0;
}
/*VIDEO*/
/*TERMS*/
.legal-nav--a{
    background: #1c1c1c;
    border: 1px solid #74a441;
}
.legal-nav--a:hover{
    color: #1c1c1c;
}
/*TERMS*/
/*SUBPAGES*/
/*FOOTER*/
.footer-terms:after{
    content: " Dark theme by Maciejozaman";
    margin-left: 1rem;
    color: #74a441;
    cursor: none;
}
/*FOOTER*/
}
