style.css:
@-moz-document url-prefix("https://vk.com") {
#page_header_cont .back {
        background: #ffffff;
    border-bottom: 1px solid #ffffff;
    height: 56px;
    box-shadow: 0 1px 4px 0 rgba(0,0,0,.05), 0 0 4px 0 rgba(0,0,0,.05);
}
.page_header_wrap, #page_header, #page_header_cont, .head_nav_item, .head_nav_item > * {
    height: 56px!important;
}
input.text.ts_input {
    margin: 14px 0;
}
.top_nav_btn .top_nav_btn_icon {
    margin: 14px auto;
}
.top_notify_count {
   border: none!important;
    font-style: normal;
    padding: 5px 0px;
    background: #aeb7c2;
    border-radius: 13px;
    color: #fff;
    min-width: 3px;
    text-align: center;
    font-size: 14px;
    font-weight: 500;
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: auto;
    position: absolute;
    right: 5px;
    height: 10px;

}
.top_nav_link {
    color: #000;
}
.top_home_link .top_home_logo {
    background: transparent;
}
.top_home_link .top_home_logo:after {
    content: '';
    background-size: 284px 248px;
    background-image: url('https://m.vk.com/images/mobile/icons/sprites/base-13e72b6a.svg');
    color: #000;
    background-position: 4px;
    padding: 0 0;
    
}
.top_home_link .top_home_logo:after {
    right: auto;
    left: 3px;
}
.top_home_link .top_home_logo:after {
    background-position: -108px -4px;
    background-repeat: no-repeat;
    width: 32px;
    height: 32px;
    content: "";
    position: absolute;
    top: 11px;
    margin: auto;
    left: -24px;
}
.top_audio_player .top_audio_player_title {
    color: #232324;
    font-size: 15px;
    font-weight: 500;
}

.top_nav_link:hover,
.top_audio_player:hover{
	background-color: transparent;
}
input.text.ts_input {
    font-size: 16px;
    -webkit-border-radius: 10px;
    -moz-border-radius: 10px;
    border-radius: 10px;
    background-color: rgba(0,29,61,.08);
    color: #000;
    padding: 9px 18px 9px 34px;
    line-height: 18px;
}

.svgIcon,
.top_nav_btn#top_notify_btn .top_nav_btn_icon{
    position: absolute;
    right: 0;
    left: 0;
    top: 0;
    bottom: 0;
    margin: auto;
}
.input_back {
    margin: 12px 0px 14px 8px!important;
}
#top_audio_btn_group .top_nav_btn_icon {
        background-position: -40px -112px;
    background-repeat: no-repeat;
    width: 28px;
    height: 28px;
}

.top_audio_player .top_audio_player_play .top_audio_player_btn_icon {
	    background-image: url('https://m.vk.com/images/mobile/icons/audio_play.png');
    background-repeat: no-repeat;
    -webkit-background-size: 100% 100%;
    -moz-background-size: 100%;
    background-size: 100%;
    opacity: 1;
    background-position: 0px 0px;
}

.top_audio_player.top_audio_player_playing .top_audio_player_play .top_audio_player_btn_icon {
    background-image: url('https://m.vk.com/images/mobile/icons/audio_play.png');
    background-repeat: no-repeat;
    -webkit-background-size: 100% 100%;
    -moz-background-size: 100%;
    background-size: 100%;
    opacity: 1;
    background-position: 0px -28px;
}

#top_notify_btn .top_nav_btn_icon {
background-position: -184px -4px;
    background-repeat: no-repeat;
}
.top_nav_btn:hover,
.top_nav_link.active{
    background: transparent;
}
.top_audio_player {
    height: 56px;
}
.top_audio_player .top_audio_player_title_wrap {
    margin: 20px 0 0;
}
.top_audio_player .top_audio_player_btn .top_audio_player_btn_icon {
     width: 28px;
    height: 28px;
}
.top_audio_player .top_audio_player_btn {
        padding: 14px 0;
}
.top_audio_play {
display: none;
}
.top_nav_btn#top_notify_btn.active .top_nav_btn_icon, .top_nav_btn#top_notify_btn.has_notify .top_nav_btn_icon,
.top_nav_btn#top_notify_btn .top_nav_btn_icon{
    background-position: -184px -4px;
    background-repeat: no-repeat;
    width: 22px;
    height: 22px;
}
.top_nav_link {
 line-height: 56px;
}
.top_profile_name {
    padding-right: 10px;
    display: inline-block;
    vertical-align: top;
    color: #232324;
    font-size: 15px;
    font-weight: 500;
    -webkit-font-smoothing: subpixel-antialiased;
}
.top_profile_img {
    width: 36px;
    height: 36px;
    border-radius: 19px;
    margin: 10px 0;
}
#page_body,.side_bar_inner {
    margin-top: 56px;
}
.top_profile_arrow {
background-position: -256px -154px;
    background-repeat: no-repeat;
    width: 16px;
    height: 12px;
    content: '';
    display: inline-block;
    vertical-align: middle;
    margin: 0 0 0 7px;
    opacity: 1;
}
.top_nav_btn .top_nav_btn_icon {
	width: 28px;
    height: 28px;
}
#top_profile_menu {
	height: auto!important;
}
.top_nav_btn#top_notify_btn.active .top_nav_btn_icon, .top_nav_btn#top_notify_btn.has_notify .top_nav_btn_icon,
.top_nav_btn#top_notify_btn:hover .top_nav_btn_icon,
.top_nav_btn .top_nav_btn_icon {
    -webkit-background-size: 210px 180px;
    -moz-background-size: 210px 180px;
    background-size: 210px 180px;
    background-image: url('https://m.vk.com/images/mobile/icons/sprites/menu-7a14d06d.svg')!important;
}

.top_profile_arrow {
    -webkit-background-size: 284px 248px;
    -moz-background-size: 284px 248px;
    background-size: 284px 248px;
    background-image: url('https://m.vk.com/images/mobile/icons/sprites/base-13e72b6a.svg');
}


#top_profile_menu {
	background: #fff;
    overflow: hidden;
    -webkit-border-radius: 8px;
    -moz-border-radius: 8px;
    border-radius: 8px;
        box-shadow: 0 0 64px 0 rgba(0,0,0,.24), 0 0 4px 0 rgba(0,0,0,.24);
    color: #000;
    font-size: 16px;
    line-height: 20px;
    font-weight: 400;
    right: auto;
    -webkit-font-smoothing: subpixel-antialiased;
    padding: 0;
    width: 200px;
}
.top_profile_mrow {
    font-size: 16px;
    color: #000;
    font-weight: 400;
    -webkit-font-smoothing: subpixel-antialiased;
        padding: 14px 60px;
    line-height: 20px;
    height: 20px;
    
}
#top_profile_menu.shown {
	top: 56px;
}

#top_profile_menu .top_profile_sep {
	display:none;
}
.top_profile_mrow:before {
    background-position: -148px -76px;
    background-repeat: no-repeat;
    width: 28px;
    height: 28px;
    position: absolute;
    right: 0;
    left: 0;
    top: 0;
    bottom: 0;
    margin: auto 16px;
    content: '';
    z-index: 10;
    background-image: url('https://m.vk.com/images/mobile/icons/sprites/menu-7a14d06d.svg');
    background-size: 210px 180px;
    border: none;
}
#side_bar .left_icon {
    background-image: url('https://m.vk.com/images/mobile/icons/sprites/menu-7a14d06d.svg');
    background-size: 210px 180px;
    width: 28px;
    height: 28px;
    margin: 0 16px 0 0;
    opacity: 1;
}


#side_bar #l_nwsf .left_icon {
	background-position: -137px -146px;
    
}

#side_bar #l_msg .left_icon {
    background-position: -112px -76px;
}

#side_bar #l_fr .left_icon {
    background-position: -148px -112px;
}

#side_bar #l_gr .left_icon {
	background-position: -4px -148px;
}

#side_bar #l_ph .left_icon {
	background-position: -40px -4px;
}

#side_bar #l_aud .left_icon {
    background-position: -40px -112px;
}

#side_bar #l_vid .left_icon {
	background-position: -40px -148px;
}

#side_bar #l_ap .left_icon {
	background-position: -76px -40px;
}
    
#side_bar #l_vkp .left_icon {

}

#side_bar #l_mk .left_icon {

}

#side_bar #l_fav .left_icon {
background-position: -76px -4px;
}


#side_bar #l_doc .left_icon {
background-position: -40px -40px;
}


#side_bar #l_apm .left_icon {
background-position: -148px -40px;
}

#side_bar .l_comm .left_icon{
	background-position: -4px -148px;
}

#side_bar .genre_app .left_icon{
    background-position: -148px -40px;
}


#top_myprofile_link:before,
#side_bar #l_pr .left_icon{
	background-position: -149px -111px;
}

#top_settings_link:before {
    background-position: -149px -76px;
}
#top_logout_link:before {
     background-position: -113px -42px;
}

#top_support_link:before {
	background-position: -41px -76px;
}

#top_edit_link:before {
	background-position: -114px -144px;
    background-image: url('https://m.vk.com/images/mobile/icons/sprites/base-13e72b6a.svg');
    background-size: 284px 248px;
}
    
#side_bar_inner li a {
	padding: 6px 12px 6px 10px;
    font-weight: 400;
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: auto;
    color: #000;
    border-radius: 4px;
    font-size: 15px;
    line-height: 18px;
    display: block;
    white-space: nowrap;
}
#side_bar .left_label {
	color: #000;
    font-size: 15px;
    font-weight: 400;
}

.side_bar,
.side_bar_inner {
	width: 200px;
}

#page_layout .side_bar {
	margin-left: -42px;
}

.body_im #page_layout .side_bar {
    padding-left: 110px;
    margin-left: -149px;
}
.page_square_photo,
.page_avatar_img,
.page_block {
	border-radius: 6px;
}

.button_blue button, .button_gray button, .button_light_gray button, .flat_button {
	border-radius: 10px;
}
.left_count_wrap {
        font-style: normal;
    padding: 4px 8px;
    background: #aeb7c2;
    -webkit-border-radius: 13px;
    -moz-border-radius: 13px;
    border-radius: 13px;
    color: #fff;
    min-width: 10px;
    text-align: center;
    font-size: 15px;
    font-weight: 400;
    -webkit-font-smoothing: subpixel-antialiased;
    -moz-osx-font-smoothing: auto;
    height: 18px;
    line-height: 18px;
    margin: 0;
}
#side_bar ol li .left_row:hover .left_count_wrap {
 padding: 4px 8px;
    margin: 0;
}


}
