@-moz-document domain("rcn.net") {


/* ============================================================= */
/* BUTTONS */
/* ============================================================= */

/* standard large buttons */
input[type=button], button, .button {
	padding:6px 20px;
	border-radius: 8px;
	margin-right: 10px;
	box-shadow: 1px 1px 1px #adadad;
	font-family: 'Open Sans', Verdana, Arial, Helvetica, sans-serif;
	font-size: 1.000em;
	font-weight: 700;
	color: #ff7979;
}

input[type=button]:focus, button:focus, .button:focus {  
	outline:none;
	border: 1px solid #3c78b5;
	box-shadow: 0px 0px 5px #3c78b5;
}

/* add to the class name to make the button less tall */
input.small-button, .small-button {
	padding: 1px 10px 3px 10px;
	border-radius: 4px;
	box-shadow: 1px 1px 1px #adadad;
	margin-right: 8px;
	font-size: 0.85em;
	font-weight: 600;
}

/* change the class name to disabled for disabled buttons */
input.disabled {
	border: 1px solid #bbb;
	color: #aaa;
	background: #ccc;
	box-shadow: none;
}


input.button-primary-action, button.button-primary-action, .primary-action {
	/* Green Button Default */
	border: 1px solid #0b6d37;
	text-shadow: -1px -1px #006838;
	background: #66ad3d; 
	background: -moz-linear-gradient(top,  #8dc63f 0%, #3b8e3a 50%, #006838 51%, #006838 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#8dc63f), color-stop(50%,#3b8e3a), color-stop(51%,#006838), color-stop(100%,#006838)); 
	background: -webkit-linear-gradient(top,  #8dc63f 0%,#3b8e3a 50%,#006838 51%,#006838 100%); 
	background: -o-linear-gradient(top,  #8dc63f 0%,#3b8e3a 50%,#006838 51%,#006838 100%); 
	background: -ms-linear-gradient(top,  #8dc63f 0%,#3b8e3a 50%,#006838 51%,#006838 100%); 
	background: linear-gradient(to bottom,  #8dc63f 0%,#3b8e3a 50%,#006838 51%,#006838 100%); 
	
}


input.button-primary-action:hover, button.button-primary-action:hover, .primary-action:hover, .primary-action-hover {
	/* Green Button Hover */
	cursor:pointer;
	border: 1px solid #0b6d37;
	text-shadow: -1px -1px #0b6d37;
	/* background: #3d903a; */
	background: -moz-linear-gradient(top,  #8dc63f 0%,#006838 50%,#006838 51%,#006838 100%);
	/* background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#8dc63f), color-stop(50%,#006838), color-stop(100%,#006838)); */
	/* background: -webkit-linear-gradient(top,  #8dc63f 0%,#006838 51%,#006838 100%); */
	background: -o-linear-gradient(top,  #8dc63f 0%,#006838 51%,#006838 100%);
	background: -ms-linear-gradient(top,  #8dc63f 0%,#006838 51%,#006838 100%);
	/* background: linear-gradient(to bottom,  #8dc63f 0%,#006838 51%,#006838 100%); */

}


input.button-primary-action:active, button.button-primary-action:active, .primary-action:active, .primary-action-active {
	/* Green Button Active */
	cursor:pointer;
	border: 1px solid #006838;
	text-shadow: 1px 1px #006838;
	background: -moz-linear-gradient(top,  #006838 0%,#006838 51%,#8dc63f 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#006838), color-stop(51%,#0b6d37), color-stop(100%,#8dc63f)); 
	background: -webkit-linear-gradient(top,  #006838 0%,#006838 51%,#8dc63f 100%); 
	background: -o-linear-gradient(top,  #006838 0%,#006838 51%,#8dc63f 100%); 
	background: -ms-linear-gradient(top,  #006838 0%,#006838 51%,#8dc63f 100%); 
	background: linear-gradient(to bottom,  #006838 0%,#006838 51%,#8dc63f 100%); 
 
}

input.button-primary-action:disabled, button.button-primary-action:disabled, .primary-action:disabled, .primary-action-disabled {
	/* Green Button Disabled */
	cursor: default;
	border: 1px solid #b2d69e;
	text-shadow: -1px -1px #85b69b;
	background: #85b69b; 
	background: -moz-linear-gradient(top,  #b2d69e 0%, #85b69b 50%, #85b69b 51%, #85b69b 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#b2d69e), color-stop(50%,#85b69b), color-stop(51%,#85b69b), color-stop(100%,#85b69b)); 
	background: -webkit-linear-gradient(top,  #b2d69e 0%, #85b69b 50%, #85b69b 51%, #85b69b 100%); 
	background: -o-linear-gradient(top,  #b2d69e 0%, #85b69b 50%, #85b69b 51%, #85b69b 100%); 
	background: -ms-linear-gradient(top,  #b2d69e 0%, #85b69b 50%, #85b69b 51%, #85b69b 100%); 
	background: linear-gradient(to bottom,  #b2d69e 0%, #85b69b 50%, #85b69b 51%, #85b69b 100%); 

}

input.button-local-action, button.button-local-action, .local-action {
	/* Blue Button */
	border: 1px solid #3c78b5;
	text-shadow: -1px -1px #114070;
