.segmented-button input[type="radio"] {
  width: 0px;
  height: 20px;
  display: none;
}

.segmented-button label {
	display: -moz-inline-box;
	-moz-box-orient: vertical;
	display: inline-block;
	vertical-align: middle;
	text-shadow: white;
	background: #c6c6c6; 
	background: -moz-linear-gradient(top,  #e8e8e8 0%, #d8d8d8 50%, #c6c6c6 51%, #c6c6c6 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e8e8e8), color-stop(50%,#d8d8d8), color-stop(51%,#c6c6c6), 	color-stop(100%,#c6c6c6)); 
	background: -webkit-linear-gradient(top,  #e8e8e8 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: -o-linear-gradient(top,  #e8e8e8 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: -ms-linear-gradient(top,  #e8e8e8 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: linear-gradient(to bottom,  #e8e8e8 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%); 
	border: 1px solid #7c7c7c;
	color: #303030;
	padding: 3px 12px 5px 12px;
	margin: 0;
	font-size: 0.85em;
	cursor: pointer;
	-moz-border-radius: 0px;
	-webkit-border-radius: 0px;
	-o-border-radius: 0px;
	-ms-border-radius: 0px;
	border-radius: 0px;
	margin-right: -5px;
}

.segmented-button label {
	*display: inline;
}

.segmented-button label:hover {
	background: -moz-linear-gradient(top,  #e8e8e8 0%, #c6c6c6 50%, #c6c6c6 51%, #c6c6c6 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#e8e8e8), color-stop(50%,#c6c6c6), color-stop(51%,#c6c6c6), 	color-stop(100%,#c6c6c6)); 
	background: -webkit-linear-gradient(top,  #e8e8e8 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: -o-linear-gradient(top,  #e8e8e8 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: -ms-linear-gradient(top,  #e8e8e8 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%); 
	background: linear-gradient(to bottom,  #e8e8e8 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%); 
}

.segmented-button label:active, 
.segmented-button label.active {
	background: -moz-linear-gradient(top,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#c6c6c6), color-stop(50%,#c6c6c6), color-stop(51%,#c6c6c6), 	color-stop(100%,#e8e8e8)); 
	background: -webkit-linear-gradient(top,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
	background: -o-linear-gradient(top,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
