	background: linear-gradient(to bottom,  #c6c6c6 0%,#c6c6c6 50%,#c6c6c6 51%,#e8e8e8 100%); 
}

.segmented-button label:disabled, 
.segmented-button label.disabled {
	/* Disabled, white to light gray */
	background: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, #ffffff), color-stop(100%, #efefef));
	background: -webkit-linear-gradient(#ffffff, #efefef);
	background: -moz-linear-gradient(#ffffff, #efefef);
	background: -o-linear-gradient(#ffffff, #efefef);
	background: -ms-linear-gradient(#ffffff, #efefef);
	background: linear-gradient(#ffffff, #efefef);
	cursor: default;
	color: #b2b2b2;
	border-color: #7c7c7c;
	-moz-box-shadow: none;
	-webkit-box-shadow: none;
	-o-box-shadow: none;
	box-shadow: none;
}

/* Set first button rounded corners on left */
.segmented-button label:first-of-type {
	-moz-border-radius-topleft: 4px;
	-webkit-border-top-left-radius: 4px;
	-o-border-top-left-radius: 4px;
	-ms-border-top-left-radius: 4px;
	border-top-left-radius: 4px;
	-moz-border-radius-bottomleft: 4px;
	-webkit-border-bottom-left-radius: 4px;
	-o-border-bottom-left-radius: 4px;
	-ms-border-bottom-left-radius: 4px;
	border-bottom-left-radius: 4px;
}

/* set last button rounded corners on right */
.segmented-button label:last-of-type {
	-moz-border-radius-topright: 4px;
	-webkit-border-top-right-radius: 4px;
	-o-border-top-right-radius: 4px;
	-ms-border-top-right-radius: 4px;
	border-top-right-radius: 4px;
	-moz-border-radius-bottomright: 4px;
	-webkit-border-bottom-right-radius: 4px;
	-o-border-bottom-right-radius: 4px;
	-ms-border-bottom-right-radius: 4px;
	border-bottom-right-radius: 4px;
}

.segmented-button input:checked + label, 
.segmented-button label.selected {
  color: #ffffff;
}

