@-moz-document domain("rcn.net") {
/* TOP Nav Stuff */
#top-navigation-container {
	display: -webkit-box;
	display: -moz-box;
	display: -ms-flexbox;
	display: flexbox;
	-moz-box-orient: horizontal;
	-moz-box-flex: 1;
	-ms-flex: 1;
	flex: 1;

	background: #e82626;
	background: -moz-linear-gradient(top, #e13636 0%, #454545 50%, #303030 51%, #303030 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#585858), color-stop(50%,#454545), color-stop(51%,#303030), color-stop(100%,#303030));
	background: -webkit-linear-gradient(top, #585858 0%,#b92525 50%,#303030 51%,#303030 100%);
	background: -o-linear-gradient(top, #585858 0%,#454545 50%,#303030 51%,#303030 100%);
	background: -ms-linear-gradient(top, #585858 0%,#f92a2a 50%,#303030 51%,#303030 100%);
	background: linear-gradient(to bottom, #585858 0%,#454545 50%,#303030 51%,#303030 100%);


	width: 100%;
}

/* Top Nav/Menu bar */
nav { 
	width: 100%; 
}

nav a {
	text-decoration: none;
	cursor: pointer;
}

nav ul#top-navigation-container-nav-items { 
	margin: 0px; 
	padding: 0px; /* Cancels webkits has default padding for ULs. */ 
	height: 40px; 
	width: 100%; 
} 

nav ul li { 
	display:inline-block; 
	position: relative;
	margin-top: 0px;
	color:#000; 
     text-shadow: 2px 2px 4px #fff;
	height:100%; 
	
	padding: 0px 10px; 
}

/* Notifications menu in top left.*/
nav ul li#top-navigation-notification-container {
  z-index: 100;
}

/* Job Status menu in top left.*/
nav ul li#top-navigation-technician-status-menu-container {
    
      position: absolute;
  top: 0;
    
    color: #fff;
    width: 120px;
	/* set height to fix scroll issue on some browers */
	height: 10px;
    padding: 0px;
   margin-left: 120px;
  background: none;
    
    
    
  z-index: 100;
}

/* Gear/Settings menu in top right.*/
nav ul li#gear-menu {
	position: absolute;
	top: 0px;
	right: 0px;
	
	height: inherit;
    z-index: 200;
}

nav ul li#gear-menu ul {
	left: auto;
	right: 0px;

	z-index: 100;
}

/* Label positioning */
nav ul li .sprite {
	margin-top: 3px;
}

/* Top nav mouse overs*/
nav ul li.hover { 
	background: #c3c3c3;
}

/* Flyout positioning */
nav ul li ul { 
	display:none; 
	position:absolute; 
	left: 0px; 
	top: 37px;

	-webkit-border-bottom-right-radius: 6px;
	-webkit-border-bottom-left-radius: 6px;
	-moz-border-radius-bottomright: 6px;
	-moz-border-radius-bottomleft: 6px;
	border-bottom-right-radius: 6px;
	border-bottom-left-radius: 6px;
	background-color:#2f434f6b;   /* DOES BACK COLOR FOR TOP GEAR DROP DOWN */
	
	margin-top:3px; 
	padding-bottom: 6px;

	overflow: hidden;

	z-index: 100;
}

/* Nav flyout item styling*/
nav ul li ul li { 
	margin:0; 
	padding:0; 
	width:100%;
}
