.css-icons {
	position: relative;
	overflow: hidden;
	list-style: none;
	padding: 0;
	margin: 0 0 0.25em;
	display: inline-block;
}

	.css-icons a:link,
	.css-icons a:visited {
		display: block;
		border: 0;
		padding-left: 28px;
		color: #3c78b5;
	}

	.css-icons a:hover,
	.css-icons a:focus,
	.css-icons a:active {
		color: #114070;
		background: transparent;
	}

	.css-icons:before,
	.css-icons:after,
	.css-icons a:before,
	.css-icons a:after {
		content: "";
		position: absolute;
		top: 50%;
		left: 0;
	}

	.css-icons a:before,
	.css-icons a:after {
		margin: -8px 0 0;
		background: #3c78b5;
	}

	.css-icons a:hover:before,
	.css-icons a:focus:before,
	.css-icons a:active:before {
		background: #114070;
	}


