	background: -ms-linear-gradient(top, #cd0000 0%,#d87569 100%);
	background: linear-gradient(to bottom, #cd0000 0%,#d87569 100%);

}

.modal-footer {
	text-align: right;
	padding: 7px 14px;
	background-color: #e8e8e8;
	border-top: 1px solid #bfbfbf;
	border-bottom-left-radius: 8px;
	border-bottom-right-radius: 8px;
	position: absolute;
	bottom: 0px;
	right: 0px;
	left: 0px;
}

.modal-body {
	margin-left: 8px;
	margin-right: 8px;
}

/* Override left/right margins for modal footer small buttons */
.modal-footer .small-button, .modal-footer input.small-button {
	margin-left: 8px;
	margin-right: 0px !important;
}

/*loading*/
#field-progress {
	position: absolute;
	top: 0;
	left: 0;
	bottom: 0;
	right: 0;
	background-color: transparent;
	z-index: 99;
	text-align:center;
}
/* Spinner */

#field-load {
	background-color: #606060;
	border: 2px solid #ffffff;
	padding: 8px 4px 8px 4px;
	border-radius: 8px;
	vertical-align: middle;
	min-height: 32px;
	box-shadow: 0px 0px 3px 3px rgba(0, 0, 0, 0.3);
	position: relative;
	min-width: 150px;
	max-width: 550px;
	opacity: 1;
	transition-property: opacity;
	transition-duration: 0.3s;
	transition-timing-function: ease;
	margin:315px auto;
	display:inline-block;
}

#field-load span {
	display: block;
	color: #e8e8e8;
	font-weight: 600;
	text-transform: uppercase;
	font-size: 0.85em;
	height: 30px;
	line-height: 30px;
	padding-left: 50px;
	padding-right: 20px;
}
/* Construct the animated circles, usable in IE11 */
#circle {
	width: 30px;
	height: 30px;
	position: relative;
	float: left;
	margin: 0 8px 0 8px;
}

#circle div {
	width: 100%;
	height: 100%;
	position: absolute;
	left: 0;
	top: 0;
}

#circle div:before {
	content: "";
	display: block;
	margin: 0 auto;
	width: 20%;
	height: 20%;
	background-color: #3ecff4;
	border-radius: 100%;
	animation: spin-circleBounceDelay 1.3s infinite ease-in-out both;
}

#circle div:nth-child(2) {
	transform: rotate(30deg);
}

#circle div:nth-child(3) {
	transform: rotate(60deg);
}

#circle div:nth-child(4) {
	transform: rotate(90deg);
}

#circle div:nth-child(5) {
	transform: rotate(120deg);
}

#circle div:nth-child(6) {
	transform: rotate(150deg);
}

#circle div:nth-child(7) {
	transform: rotate(180deg);
}

#circle div:nth-child(8) {
	transform: rotate(210deg);
}

#circle div:nth-child(9) {
	transform: rotate(240deg);
}

#circle div:nth-child(10) {
	transform: rotate(270deg);
}

#circle div:nth-child(11) {
	transform: rotate(300deg);
}

#circle div:nth-child(12) {
	transform: rotate(330deg);
}

#circle div:nth-child(2):before {
	animation-delay: -1.1s;
}

#circle div:nth-child(3):before {
	animation-delay: -1s;
}

#circle div:nth-child(4):before {
	animation-delay: -0.9s;
}

#circle div:nth-child(5):before {
	animation-delay: -0.8s;
}

#circle div:nth-child(6):before {
	animation-delay: -0.7s;
}

#circle div:nth-child(7):before {
	animation-delay: -0.6s;
}

#circle div:nth-child(8):before {
	animation-delay: -0.5s;
}

#circle div:nth-child(9):before {
	animation-delay: -0.4s;
}

#circle div:nth-child(10):before {
	animation-delay: -0.3s;
}

#circle div:nth-child(11):before {
	animation-delay: -0.2s;
}

#circle div:nth-child(12):before {
	animation-delay: -0.1s;
}

@-webkit-keyframes spin-circleBounceDelay {
	0%, 80%, 100% {
		transform: scale(0.5);
	}

	40% {
		transform: scale(1);
	}
}

@keyframes spin-circleBounceDelay {
	0%, 80%, 100% {
		transform: scale(0.5);
	}

	40% {
		transform: scale(1);
	}
}
.hidden{
	display:none;
}

/* ============================================================= */
/* SELECTS - PULL DOWN MENUS                                     */
/* ============================================================= */

/* Full size one-item dropdown menu */
select[size="1"], select:not(size) {
	border: 1px solid #7c7c7c;
	padding: 5px 10px;
	background: #c6c6c6;
	background: -moz-linear-gradient(top, #f0f0f0 0%, #d8d8d8 50%, #c6c6c6 51%, #c6c6c6 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f0f0f0), color-stop(50%,#d8d8d8), color-stop(51%,#c6c6c6), color-stop(100%,#c6c6c6));
	background: -webkit-linear-gradient(top, #f0f0f0 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%);
	background: -o-linear-gradient(top, #f0f0f0 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%);
	background: -ms-linear-gradient(top, #f0f0f0 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%);
	background: linear-gradient(to bottom, #f0f0f0 0%,#d8d8d8 50%,#c6c6c6 51%,#c6c6c6 100%);
	border-radius: 5px;
	font-family: 'Open Sans', Verdana, Arial, Helvetica, sans-serif;
	font-size: 1.000em;
	font-weight: 400;
	color: #303030;
	box-shadow: inset 1px 1px 1px #c6c6c6;
}

/* Full size expanded select item with size greater than 1 */
select[size]:not([size="1"]) {
	padding: 5px 10px !important;
	background: #e8e8e8 !important;
	border-top-left-radius: 5px !important;
	border-bottom-left-radius: 5px !important;
	border-bottom-right-radius: 5px !important;
	border-top-right-radius: 5px !important;
	font-family: 'Open Sans', Verdana, Arial, Helvetica, sans-serif;
	font-size: 1.000em;
	font-weight: 400;
	color: #303030 !important;
	box-shadow: inset 1px 1px 1px #c6c6c6;
}

/* Expanded select hover over option items */
select[size]:not([size="1"]) option:hover {
	background-color: #fef596;
	color: #303030;
}

/* Small select dropdown */
select.select-compact {
	height: 1.75em;
	font-size: 0.750em;
	font-weight: 600;
	padding: 1px 4px;
	border-radius: 0.4em;
	margin-top: -6px;
}

select:hover {
	background: -moz-linear-gradient(top, #f0f0f0 0%, #c6c6c6 50%, #c6c6c6 51%, #c6c6c6 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f0f0f0), color-stop(50%,#c6c6c6), color-stop(51%,#c6c6c6), color-stop(100%,#c6c6c6));
	background: -webkit-linear-gradient(top, #f0f0f0 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%);
	background: -o-linear-gradient(top, #f0f0f0 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%);
	background: -ms-linear-gradient(top, #f0f0f0 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%);
	background: linear-gradient(to bottom, #f0f0f0 0%,#c6c6c6 50%,#c6c6c6 51%,#c6c6c6 100%);
	cursor: pointer;
}

select:disabled,
select:hover:disabled {
	border: 1px solid #bfbfbf;
	background: #e2e2e2;
	color: #909090;
	background: -moz-linear-gradient(top, #f6f6f6 0%,#e2e2e2 50%,#e2e2e2 51%,#e2e2e2 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#f6f6f6), color-stop(50%,#d8d8d8), color-stop(51%,#bfbfbf), color-stop(100%,#bfbfbf));
	background: -webkit-linear-gradient(top, #f6f6f6 0%,#e2e2e2 50%,#e2e2e2 51%,#e2e2e2 100%);
	background: -o-linear-gradient(top, #f6f6f6 0%,#e2e2e2 50%,#e2e2e2 51%,#e2e2e2 100%);
	background: -ms-linear-gradient(top, #f6f6f6 0%,#e2e2e2 50%,#e2e2e2 51%,#e2e2e2 100%);
	background: linear-gradient(to bottom, #f6f6f6 0%,#e2e2e2 50%,#e2e2e2 51%,#e2e2e2 100%);

}

select.error {
	border: 1px solid #cd0000;
}


/* GENERIC CUSTOM SCROLLBAR CODE (not tied to container) */
/* Set custom inner container scrollbar attributes */
select::-webkit-scrollbar {
	border-bottom-right-radius: 7px;
	border-top-right-radius: 7px;
	background-color: #cde3fa;
	width: 11px;
	height: 11px;
	/* border-right: 1px solid #9d9d9d; */
}

select::-webkit-scrollbar-thumb {
	background-color: #78b2ec;
	border: 1px solid #3c78b5;
	width: 7px;
	
	/* border-bottom-right-radius: 7px;
	border-top-right-radius: 7px; */
	border-radius: 6px;
	-webkit-box-shadow: inset -1px 1px 1px #cde3fa;
	-moz-box-shadow: inset -1px 1px 1px #cde3fa;
	box-shadow: inset -1px 1px 1px #cde3fa;
}

select::-webkit-scrollbar-button {
	height: 0;
}

/* IE10 and 11-only media query to change scrollbar */

/* End IE-specific media query */
/* END SCROLLBAR */
}

