/* ==UserStyle==
@name           YNAB Dark Theme
@namespace      github.com/Boren/dark-ynab
@homepageURL    https://github.com/Boren/dark-ynab
@updateURL      https://raw.githubusercontent.com/Boren/dark-ynab/master/ynab-dark.user.css
@version        1.6.0
@author         Fredrik Bore
==/UserStyle== */
@-moz-document domain("app.youneedabudget.com") {
    body,
    .ynab-grid-container {
        background-color: var(--default_background);
    }

    * {
        --budget_balance_zero_background: #222222;
        --default_background: #121212;
        --default_text: #FFFFFF;
        --default_text_dark: #FFFFFF;
        --header_background: #222222;
        --header_negative_text: #CF6679;
        --inspector_background: #1E1E1E;
        --inspector_default_text: #FFFFFF;
        --inspector_section_heading_background: #282828;
        --inspector_warning_background: #CF6679;
        --loading_background: #121212;
        --login_background_default: #121212;
        --modal_background: #1E1E1E;
        --modal_row_background_active: #282828;
        --primary_action: #00beed;
        --primary_button_text: #FFFFFF;
        --register_status_scheduled_background: #363636;
        --sidebar_account_negative_balance_background: #1E1E1E;
        --sidebar_background: #282828;
        --sidebar_button_secondary_background: #265862;
        --status_negative: #CF6679;
        --table_header_background_default: #1E1E1E;
        --table_header_text_default: #FFFFFF;
        --table_row_background_default: #121212;
        --table_row_text_default: #FFFFFF;
        --textinput_background: #282828;
        --toolbar_background: #1E1E1E;
        --tbb_default_text: #000000;
    }
    
    optgroup {
        background-color: #1E1E1E;
    }

    .accounts-toolbar {
        background-color: var(--inspector_background);
    }
    
    .account-widget .button-disabled {
        background-color: var(--primary_action_disabled);
    }

    .ynab-new-currency-input.is-editing {
        background-color: var(--default_background);
    }

    .income-expense-row:hover,
    .income-expense-row.income-expense-totals:hover .income-expense-column,
    .income-expense-row:hover .income-expense-first-column {
        background: #282828 !important;
    }

    .sidebar-nav-menu {
        margin-bottom: 10px;
    }

    .sidebar-nav-menu-budget {
        background-color: #303030;
    }

    .category-name {
        color: var(--default_text);
    }

    .modal-popup input:focus {
        background-color: #282828;
        background-image: none;
    }
    
    .modal-budget-activity .budget-activity-debt-info {
        background-color: var(--header_background);
    }

    .percent-sign,
    .percent,
    .percent-label,
    .chart,
    .highcharts-title {
        fill: var(--default-text) !important;
    }

    text > tspan {
        color: var(--default_text);
        fill: var(--default_text);
    }
    
    .ynab-grid .ynab-grid-body-row:nth-of-type(even):not(.is-scheduled):not(.is-checked):not(.is-editing):not(.ynab-grid-body-empty):not(.ynab-grid-actions) {
        background-color: var(--default_background);
    }

    .ynab-grid .ynab-grid-body-row:nth-of-type(odd):not(.is-scheduled):not(.is-checked):not(.is-editing):not(.ynab-grid-body-empty):not(.ynab-grid-actions) {
        background-color: var(--default_background);
    }

    
    /* YNAB Toolkit Specific */

    .highcharts-background {
        fill: var(--default_background);
    }

    .highcharts-grid-line {

        opacity: 0.2;
    }

    .highcharts-point {

        stroke: var(--sidebar_background);
    }

    .highcharts-text-outline {
        display: none;
    }

    .toolkit-is-reconciled,
    .income-expense-number > span.user-data,
    span.user-entered-text {
        color: var(--default-text);
    }

    .tk-totals-table__child-row,
    .tk-totals-table__child-summary-row,
    .tk-totals-table__title-row--collapsed {
        background-color: #282828;
    }

    .tk-totals-table__header-row--income,
    .tk-totals-table__footer-row--income {
        background-color: #00c853;
    }

    .tk-totals-table__header-row--expense,
    .tk-totals-table__footer-row--expense {
        background-color: #CF6679;
    }

    .tk-ive__net-income {
        background-color: #121212;
        border-top: 2px solid #FFF;
        border-bottom: 2px solid #FFF;
    }

    .tk-modal-content {
        background-color: #282828;
    }

    .tk-series-legend {
        color: var(--default-text);
        background-color: var(--sidebar-background);
    }
}
