style.css:
@-moz-document domain("youtube.com") {
#container.ytd-searchbox {
    position: relative;
    align-items: center;
    background-color: transparent;
    border: 1px solid var(--ytd-searchbox-legacy-border-color);
    border: none;
    border-radius: 2px;
    box-shadow: inset 0 1px 2px var(--ytd-searchbox-legacy-border-shadow-color);
    color: var(--yt-searchbox-text-color);
    padding: 2px 6px;
    -ms-flex: 1 1 1e-9px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: 1e-9px;
    flex-basis: 1e-9px;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -ms-flex-direction: row;
    -webkit-flex-direction: row;
    flex-direction: row;
    text-align: center;
}

#search-input.ytd-searchbox-spt input {
    -webkit-appearance: none;
    -webkit-font-smoothing: antialiased;
    background-color: transparent;
    border: none;
    box-shadow: none;
    color: inherit;
    font-family: 'Roboto', 'Noto', sans-serif;
    font-size: 16px;
    font-weight: 400;
    line-height: 24px;
    margin-left: 4px;
    max-width: 100%;
    outline: none;
    text-align: inherit;
    width: 75%;
    -ms-flex: 1 1 .000000001px;
    -webkit-flex: 1;
    flex: 1;
    -webkit-flex-basis: .000000001px;
    flex-basis: .000000001px;
    text-transform: lowercase;
}

html {
    cursor: default;
}

/* links */
a.yt-simple-endpoint.yt-formatted-string {
    color: #617E9A;
    transition: color 0.3s ease;
}

/* hover over links */
a.yt-simple-endpoint.yt-formatted-string:hover {
    color: #F5F0F6;
}

.count-text.ytd-comments-header-renderer {
    text-transform: lowercase;
}

/* width */
::-webkit-scrollbar {
    display: none;
}

/*the youtube video badge*/
ytd-author-comment-badge-renderer {
    border-radius: 0.75rem;
}

/* Track */
::-webkit-scrollbar-track {
    display: none;
}

/* Handle */
::-webkit-scrollbar-thumb {
    display: none;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    display: none;
}

.sbsb_b li {
    text-align: center !important;
    padding: 2px 1px;
}

/*when searchbox is focused*/
ytd-searchbox[has-focus] #container.ytd-searchbox {
    border: none;
    box-shadow: none;
}

/*bar*/
.masthead-finish {
    background-color: #202020 !important;
}

ytd-simple-menu-header-renderer {
    background-color: #202020 !important;
}

ytd-app[is-watch-page] {
    background-color: #202020 !important;
}

ytd-multi-page-menu-renderer {
    background-color: #202020 !important;
}

ytd-watch-flexy[theater] #player-theater-container.ytd-watch-flexy, ytd-watch-flexy[fullscreen] #player-theater-container.ytd-watch-flexy {
    background: #202020 !important;
}

#search-icon-legacy.ytd-searchbox {
    cursor: pointer;
    width: 65px;
    border: none;
    opacity: 0.1;
    background-color: rgba(1, 1, 1, 0);
    --yt-icon-button-icon-width: 24px;
    --yt-icon-button-icon-height: 24px;
    transform: translateX(-5.25em);
    transition: opacity 0.3s ease;
}

#search-icon-legacy.ytd-searchbox:hover {
    background-color: rgba(1, 1, 1, 0);
    opacity: 1;
}

paper-button.ytd-subscribe-button-renderer {
    border-radius: 2em;
    background-color: red;
    transition: background-color 0.3s ease;
}

paper-button.ytd-subscribe-button-renderer:hover {
    background-color: rgba(40, 40, 40, 1);
}

paper-dialog {
    border-radius: 1.25rem;
    border: none;
}

ytd-add-to-playlist-renderer[dialog] {
    outline: none;
}

img.yt-img-shadow {
    border-radius: 0.25rem;
}

paper-button.yt-next-continuation {
    border: none;
    color: #F5F0F6;
    border-radius: 0.75rem;
    background-color: rgba(40, 40, 40, 0);
    padding: 0 2px 2px 0px;
    transition: color 0.3s ease;
}

paper-button.yt-next-continuation:hover {
    color: #617E9A;
}

#contentWrapper.iron-dropdown > * {
    border-radius: 0rem 0rem 1rem 1rem;
    border: none;
}

