style.css:
@-moz-document domain("text.npr.org") {
/* Prettify text.npr.org */

html {
	background: #f9f9f9;
	font-family: Georgia, serif;
	font-size: 17px;
	line-height: 1.7em;
}

body {
	background: white;
	margin: 1rem auto;
	max-width: 40rem;
	padding: 1rem 3rem;
	border: 1px solid #DFDFDF;
	border-radius: 3px;
}

/* Links */

a {
	color: #5076b8;
	text-decoration: none;
}

a:hover,
a:focus {
	color: #bccae5
}

/* Header */

p:nth-of-type(2):before {
	content: '';
	background: linear-gradient(90deg, rgba(214,32,33,1) 0%, rgba(214,32,33,1) 33%, rgba(0,0,5,1) 33%, rgba(0,0,5,1) 66%, rgba(33,116,178,1) 66%, rgba(35,123,189,1) 100%);
	display: inline-block;
	width: 2.25em;
	height: .75rem;
	margin-right: .5em;
}

p:nth-of-type(3) {
	font-family: sans-serif;
	font-size: 1.5rem;
}

/* Lists */

ul {
	list-style: none;
	padding: 0;
}

ul li {
	border-bottom: 1px solid #DFDFDF;
	padding: .25em 0;
}

ul li:last-child {
	border: none;
}

/* Footer */

p:first-of-type,
p:last-of-type,
ul:last-of-type,
ul:last-of-type li,
ul:last-of-type li a {
	font-family: sans-serif;
	font-size: .8rem;
	color: #919191;
	display: inline;
	list-style: none;
	padding: 0;
	border: 0;
}

ul:last-of-type li {
	margin-right: 1em;
}
}
