style.css:
@-moz-document domain("school.mosreg.ru") {

 .adfox-banner-background {
    display: none
}
.bannerBackground {
    display: none;
    margin-bottom:200px;
}
body {
    background-color: #333;
    background-position: center top;
    background-repeat: no-repeat;
    font: 70%helvetica;
    line-height: 13pt;
}
.user-profile-box__avatar {
    display: none;
    margin-right: 4px;
}
.page-footer__mobile-app-links {
    float: left;
    display: none;
    text-align: center;
}
.page-footer__age-category {
    position: relative;
    float: right;
    font-size: 20px;
    width: 68px !important;
    height: 68px !important;
    color: #fff0 !important;
    border-radius: 18px;
    background-image: url(http://vivg.cz/mosreg/vivg_slogo.png);
    line-height: 32px;
    background-size: cover;
    text-align: center;
    margin-right: 10px;
    cursor: pointer;
}
label {
    color: #949494;
}
textarea, input.t, select, input[type='text'], input[type='password'], select.t, div.textarea {
    border: none;
    background-color: #6b6b6b;
    cursor: text;
    font-size: 120%;
    font-family:helvetica, helvetica, sans-serif;
    color: gainsboro;
}
form input.button {
    background: #eb8602;
    border: none;
    border-right-color: none;
    border-bottom-color: none;
    color: #fff;
    font-size: 1.4em;
    overflow: visible;
    padding: 2px 8px;
    width: auto;
    border-radius: 5px;
    box-shadow: 0px 0px 7px 0px #eb8602;
}
.header-submenu__item_active .header-submenu__link:after {
    position: absolute;
    content:'';
    bottom: 0;
    left: 0;
    width: 100%;
    height: 2px;
    background: #adadad;
}
.header-menu__item_current .header-menu__link:after {
    border-left: none;
    border-right: none;
    border-bottom: none;
}
.page-wrapper {
    background-color: #333;
}
.dynamic_banner1 {
    display: none;
}
.analytics-popup-hint {
    display: none !important;
}
.page-header {
    margin-top: 100px;
    background-color: #333;
    font-family:helvetica, Helvetica, Arial, sans-serif;
}
.header-menu__link {
    font-family:'Arimo', Helvetica, Arial, sans-serif;
    font-weight: 600;
}
.header-submenu {
    background-color: #333;
    box-shadow: none !important;
    color: white;
}
.header-submenu__item .header-submenu__link {
    text-decoration: none;
    color: #bfbfbf;
    padding: 11px 0;
}
.header-menu {
    background-color: #4c4c4c;
    border-radius: 5px;
}
header-menu__item_current .header-menu__link:after {
    border-color: black;
}
.header-menu__link {
    background-color: #4c4c4c;
}
.header-menu__item_active .header-menu__link {
    background-color: #6b6b6b;
}
.header-menu__item:nth-of-type(1) .header-menu__link {
    border-radius: 5px;
}
.user-profile-box__info {
    background-color: #333;
}
.user-profile-box__info:hover, .user-profile-box__info_active {
    margin: 0;
    background-color: #333333;
    border: 1px solid transparent;
    border-radius: 3px;
}
.h5, h5 {
    font-size: 1.171875rem;
    color: #d8d8d8;
}
.dropdown-container {
    display: none;
    position: absolute;
    z-index: 90;
    right: 0;
    margin: 5px 0 0 -2px;
    border-radius: 3px;
    text-align: left;
    background-color: #848484;
    -webkit-box-shadow: 0 0 3px rgba(120, 121, 141, .26);
    -moz-box-shadow: 0 0 3px rgba(120, 121, 141, .26);
    box-shadow: 0 0 3px rgba(120, 121, 141, .26);
    color: #fff;
}
.dropdown-container:after {
    border-bottom-color: #848484;
    border-width: 4px;
    margin-left: 56px;
}
.dropdown-container :last-child {
    padding: 7px 0 7px 11px;
    background-color: #848484;
    border: none;
    border-radius: 0 0 3px 3px;
    height: 18px;
    color: #4a4a4a;
    font-family:"Open Sans";
    font-size: 13px;
    font-weight: 400;
    letter-spacing: -.43px;
    line-height: 18px;
}
.header-links__link {
    color: #929292;
    border-bottom: 1px solid #4e4e4e;
}
.button-with-counter__counter {
    display: none !important;
}
.header-logotype_mosreg {
    width: 143px;
    height: 50px;
    background: url(https://vivg.cz/mosreg/p_logo.png) no-repeat center;
    min-width: 300px;
    min-height: 61px;
    position: relative;
    top: 6px;
    background-size: contain;
}
ul.crumbs {
    display: none;
}
h2 {
    color: #adadad;
    font-size: 2.2em;
    padding-bottom: 20px;
    font-weight: 600;
    margin-top: 20px;
}
h2, h3, h4 {
    font-family:helvetica;
}
.filter li.active a {
    background-color: #4a4a4a;
    color: #b3b3b3;
    border-radius: 5px;
}
h3 {
    font-size: 1.6em;
    color: #757575;
    font-weight: 600;
    margin-bottom: 5px !important;
}
.actions {
    display: none;
}
span.u {
    color: #7d7d7d;
    font-weight: normal;
    margin-bottom: 10px;
}
.tabs ul {
    border: none !important;
    color: #666;
    height: 26px;
    position: relative;
}
.tc1 {
    display: none;
}
.tc2 {
    display: none;
}
.tabs li a, .tabs li span {
    background-image: none !important;
    background-color: #333;
    border: 0;
    border-right: solid 1px #585858;
    display: block;
    font-size: 110%;
    line-height: 24px;
    padding: 0 12px;
    color: #b3b3b3;
}
.tabs li a.TabRating_orange {
    border: none !important;
    color: #b3b3b3;
    text-shadow: none;
}
#TabRating {
    padding: 0 0 0 10px;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    text-decoration: none;
    font-family:"Open Sans";
    font-size: 15px;
}
.tabs li.active {
    border-bottom: none;
}
.tabs li.active a, .tabs li.active span {
    background-color: #333;
    border-bottom: none;
    color: #eb8601;
    font-weight: bold;
}
#content .blue2 .rounded, .blue2 .rounded2 {
    background-color: #333333;
}
.rounded {
    border: solid 1px #5f5f5f;
    border-radius: 5px;
    -moz-border-radius: 10px;
}
.gray .cc .grid td, .blue2 .cc .grid td, form .cc .grid td {
    background: #333;
}
.grid td {
    border-top: none;
    border-bottom: solid 1px #5f5f5f;
    padding: 4px 8px;
}
.marks .mG {
    background-color: #7c8a01;
    color: #fff !important;
    border-radius: 5px;
    padding: 0 4px 0 4px;
    box-shadow: 0px 0px 9px 0px rgba(124, 138, 1, 0.77);
}
.marks .mY {
    background-color: #585858;
    color: #fff !important;
    border-radius: 5px;
    padding: 0 4px 0 4px;
}
.marks .mR {
    background-color: #585858;
    color: #fff !important;
    border-radius: 5px;
    padding: 0 4px 0 4px;
}
gray .cc .grid tr:hover td, .blue2 .cc .grid tr:hover td, form .cc .grid tr:hover td {
    background-color: #3c3c3c;
}
a {
    text-decoration: none;
    color: #d8d8d8;
    text-shadow: #5a5a5a 0px 0px 7px;
}
.page-footer__content {
    background-color: #333;
}
.page-footer_auth {
    background: #333;
}
.page-footer__banner {
    margin: 0 auto;
    padding-top: 20px;
    width: 960px;
    text-align: center;
    background: #333;
    min-height: 1px;
}
.page-footer__statistics {
    margin-left: 3px;
    position: absolute;
    height: 35px;
    background: #333;
    width: 968px;
    left: -7px;
    padding: 5px;
    box-sizing: border-box;
    bottom: -21px;
}
.page-footer__menu-link {
    color: #bdbdbd;
    text-decoration: underline;
    font-size: 13px;
}
.page-footer__copyright-layout-agreement {
    display: block;
    color: #bdbdbd;
    text-align: right;
    text-decoration: underline;
}
.page-footer__age-category {
    position: relative;
    float: right;
    font-size: 20px;
    width: 32px;
    height: 32px;
    color: #fff;
    border-radius: 18px;
    background-color: #4c4c4c;
    line-height: 32px;
    text-align: center;
    margin-right: 10px;
    cursor: pointer;
}
.play a, .play div {
    background: url(http://vivg.cz/mosreg/global.png) no-repeat;
    display: block;
    height: 24px;
    width: 24px;
}
a.day_calendar {
    margin-right: 5px;
    padding-left: 10px;
    cursor: pointer;
    display: none;
}
a:hover {
    color: #c7ddff;
}
