@-moz-document domain("drive.google.com") {
:root {
--color-black: #000000;
--color-background: #111111;
--color-flyout: #171717;
--color-dropdowns: #222222;
--color-checkboxes: #222222;    
--color-button: #444444;
--color-icons: #5F6368;
--color-buttontext: #A4A4A4;
--color-button-hover: #666666;
--color-borders: #777777;
--color-text-inactive: #222222;    
--color-text: #666666;
--color-text-active: #A4A4A4;
--color-red-highlight: #DD4B39;
--color-white: #FFFFFF;    
--color-id: chartreuse;    
}

/* var(--color-id) !important; */


/* :not([class=""]) */

/* main background accept recently edited item images*/
body div :not([class="l-u-Ab-zb ta-gc-np-Nd"]):not([class="l-u-Ab-zb-Pn-ve"]):not([class="g-Ca-pt"]):not([type="button"]):not([class="uw8t2"]):not([class="wO3c5d"]) {
    background-color: var(--color-background);
    color: var(--color-text) !important;
}

/* overrides */
#aso_search_form_anchor td, #aso_search_form_anchor button, #aso_search_form_anchor svg, .gb_Oe, .gb_de div {
    background-color: var(--color-dropdowns) !important;
}

/* grid view & details buttons */
.a-d-c .a-s-fa-Ha-pa
{
    fill: var(--color-icons) !important;
}

/* search bar container, search input field, search dropdown list, search dropdown list current item hover */
/* .gb_De form, .gb_De div, .gssb_e, .gssb_m, .gssb_i, */
/* .gb_He div, .gb_He form */
.gb_Ue, .gb_Ue div  {
    background-color: var(--color-checkboxes) !important;
    color: var(--color-text) !important;
}

/* search input text */
.gb_Qe.gb_Re .gb_bf, #gs_lc50 > input:nth-child(1) {
    color: var(--color-text-active) !important;
}

/* hide Google colored circle segments around user picture */
#gb > div > div > div > div > div > a > div > svg > path
{
  	display: none !important;  
}

/* hide right side addon bar */
#drive_main_page > div.a-qc-La.sd-ph > div.a-Kk7lMc-lihzJ-j, #drive_main_page > div.a-qc-La.sd-ph > div.a-Kk7lMc-ae3xF-j {
    display: none;
}

/* hide little white dots on right side of file list table */
.aabwZd .WYuW0e {
    border-bottom: 1px solid var(--color-background);
}  

/* borders */
.a-D-B-WErN3d-j, .a-D-gc-wKOTTc .a-s-Ba-Ak, .a-Ra-Qc .aabwZd .N7iPof, .a-U-xc>.a-da-fc-Ug::after {
    border-bottom: 1px solid var(--color-borders);
}

.a-d-vc {
    border-right: 1px solid var(--color-background);
}

/* hide left side menu seaparator borders */
.a-U-xc>.a-da-Ul::before, .ub-ml-Pr.USrqEe-ml-Pr-qc-j::before {
    border-bottom: 1px solid var(--color-background);
}

/* new item button */
button.RTMQvb.Kzazxf.fCmhtc.hc0pBf.x6jRSb.a-qb-d.h-sb-Ic.sXaDqb {
  background-color: var(--color-checkboxes) !important;
  color: var(--color-text-active) !important; 
}

/* docolorize Googlish plus sign on new item button */
.a-ec-Gd .Kzazxf.x6jRSb::before {
  filter: contrast(0);
}
}

@-moz-document domain("docs.google.com") {
:root {
--color-black: #000000;
--color-background: #111111;
--color-flyout: #171717;
--color-scrollbars: #191919;
--color-dropdowns: #222222;
--color-checkboxes: #222222;  
--color-toolbars: #222222;
--color-tabs: #171717;
--color-active-tab: #222222;
--color-button: #444444;
--color-handles: #4C4C4C;
--color-icons: #5F6368;
--color-buttontext: #A4A4A4;
--color-button-hover: #666666;
--color-borders: #777777;
--color-text-inactive: #222222;    
--color-text: #666666;
--color-text-active: #A4A4A4;
--color-red-highlight: #DD4B39;
--color-white: #FFFFFF;    
--color-id: chartreuse;    
}

/* var(--color-id) !important; */


/* header, formula bar, bottom tab bar */
#docs-chrome, .docs-material #docs-header .docs-titlebar-buttons,
#formula-bar, #t-formula-bar-input > div,
.docs-gm .grid-bottom-bar
{
    background-color: var(--color-background);
}

/* chat button */
.docs-gm .docs-material .docs-chat.jfk-button {
    background-color: var(--color-active-tab);
    border: 2px solid var(--color-active-tab);
    box-shadow: 0 0 0 2px var(--color-active-tab);
}


/* toolbar */
.docs-material #docs-toolbar-wrapper
{
    background-color: var(--color-toolbars);
    border-top: 1px solid var(--color-background);
    border-bottom: 1px solid var(--color-background);
}

/* toolbar buttons */
.docs-material #docs-toolbar-wrapper,
.docs-material .goog-toolbar-button, .docs-material .goog-toolbar-combo-button, .docs-material .goog-toolbar-menu-button
{
    background-color: var(--color-background);
    color: var(--color-icons);
    border-top: 1px solid var(--color-background);
    border-bottom: 1px solid var(--color-background);
}

/* toolbar separators */
.docs-gm .goog-toolbar-separator.goog-inline-block {
    border-left: 1px solid var(--color-icons);
}

/* formula-bar separator */
.formula-bar-separator>div {
    background-color: var(--color-icons);
}

/* menu */
#docs-menubar div
{
    color: var(--color-text-active);
}

/* document title */
.docs-title-input, .docs-title-input-label
{
    background-color: var(--color-background);
    color: var(--color-white);
}

/* bottom tab bar border */
.docs-gm .grid-bottom-bar, #formula-bar, #formula-bar-dragger {
  border-top: 1px solid var(--color-background);
  border-bottom: 1px solid var(--color-background);
}


/* document tabs */
.docs-gm .docs-sheet-tab {
    background-color: var(--color-tabs);
    border-left: 1px solid var(--color-background);
    border-right: 1px solid var(--color-background);
    border-top: 1px solid var(--color-background);
}

/* document active tab */
.docs-gm .docs-sheet-active-tab {
    background-color: var(--color-active-tab);
    border-top-color: var(--color-background);
}

/* document active tab */
.docs-gm .docs-sheet-active-tab .docs-sheet-tab-name {
    color: var(--color-white);
}

/* document active tab dropdown icon */
.docs-icon-img-container .docs-icon-img .goog-inline-block .docs-icon-arrow-dropdown {
    content: none;
}

/* scrollbars */
.grid-shim-end-ltr, .grid-shim-bottom {
    background: var(--color-scrollbars);
    border: 1px solid var(--color-scrollbars);
}

/* scrollbars */
.native-scrollbar {
    -webkit-filter: invert(90%);
}

/* freezebar handles */
.freezebar-horizontal-handle, .freezebar-vertical-handle {
    background: none;
}

/* explore button */
.waffle-assistant-entry-expanded {
    background-color: var(--color-active-tab);
    border-left: 1px solid var(--color-active-tab);
}

/* explore button */
.waffle-assistant-entry-expanded.companion-collapsed {
    border-right: 1px solid var(--color-active-tab);
}








/* sheets canvas */
@media screen {
/* Canvas */
  /* See http://www.w3.org/TR/selectors/#lex */
  div[id*='grid-table-container'] > canvas {
/*     filter: invert(90%) hue-rotate(180deg);
    -webkit-filter: invert(90%) hue-rotate(180deg); */
		
    filter: invert(90%);
    -webkit-filter: invert(90%);
  }

div[id*='grid-table-container'] > canvas.noinvert {
  filter: unset;
  -webkit-filter: unset;
}

  .grid-container {
    background-color: #111 !important;
  }

  .input-box {
    background-color: #333 !important;
  }
  
}

