style.css:
@-moz-document url-prefix("https://www.multitran.com") {

a{
    color: #1063a2;
}

body{
    background: #fff7e6 !important;
}

img[src*='logo']{
    position: relative;
    z-index: 2;
}

body,td{
	font-family: 'Roboto', sans-serif;
}

div.offset1{
	text-align: right;
    margin-bottom: 0px;
    position: relative;
    top: -60px;
    left: 0;
}

div.offset1 a{
	color: gray;
    text-transform: uppercase;
    font-size: 8pt;
    padding: 0 10px;
}
div.offset1 a:hover{
	color: black;
}
div.offset1 span {
    color: #e8e8e8 !important;
}

.container{
    width: 100%;
    max-width: 1100px;
    margin: 0 auto;
    background: white;
    min-height: 100vh;
    padding: 0 50px;
    padding-bottom: 50px;
}

left_banner, right_banner, #google_ads_frame1, #yandex_ad_R-57304-13,.adsbygoogle{display: none !important;}

.middle_col{
	margin: 0;
    padding: 0;
    overflow: visible;
}

table{
    width: 100%;
}

#start > div:nth-child(5){
	text-align: right;
    position: relative;
    top: -40px;
    margin-bottom: -40px;
}

a.menu:link, a.menu:visited, a.menu1:link, a.menu1:visited{
    background: none;
    color: black;
    font-weight: 300;
    font-size: 10pt;
    border-radius: 0;
    border-bottom: 1px solid #eee;
    margin: 0;
    padding: 0;
    margin-top: 10px;
    margin-right: 5px;
    width: auto;
    padding: 7px 15px;
    float: none;
    display: inline-block;
}

a.menu:hover, a.menu1:hover{
    color: #016fc0;
    border-bottom: 1px solid #016fc0;
}

.empty5{
    margin-top: 30px;
}

.input-trans{
	border: none;
    box-shadow: 0 10px 20px rgba(0,0,0,0.1);
    border-radius: 0;
    border: 1px solid #eee;
    padding: 5px 8px;
    position: relative;
    outline: none;
}

.button4{
    margin-right: 20px;
    background: url(data:image/png;base64,);
	background-size: 15px;
    background-repeat: no-repeat;
    background-position: center center;
    border: none;
    font-size: 0;
    height: 20px;
    width: 30px;
    margin-left: 0px;
    position: relative;
    top: -6px;
    opacity: 0.2;
    cursor: pointer;
}
.button4:hover{
	opacity: 1;
}

#translation{
	font-size: 9pt;
    text-transform: uppercase;
    margin-bottom: 30px;
}
#translation select{
	font-size: 9pt;
    text-transform: uppercase;
}
#translation > a{
	color: rgba(0,0,0,0);
    background: url(data:image/png;base64,);
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center center;
    padding: 0 5px;
    opacity: 0.3;
}
#translation > a:hover{
	opacity: 1;
}

a[href*="MessHeader"]{
	float: right;
    border: 1px solid;
    padding: 5px 10px;
    border-radius: 4px;
    margin-top: -22px;
    font-size: 10pt;
    text-transform: uppercase;	
}

.grayline {
	padding: 10px 10px;
    font-size: 10pt;
    background: #ececec;
    margin-top: 30px;
}

.grayline a{
	margin: 5px;
    text-transform: uppercase;
    font-size: 8pt;
    padding: 0 10px;
}
.grayline a[href*="act1=26"]{
	color: #a90000;
}

td.gray{
	padding-top: 20px;
    font-size: 15pt;
    background: white;
    border-bottom: 3px solid #f1f1f1;
    padding-left: 0;
    border-radius: 0;
    padding-bottom: 5px;
}

}
