style.css:
@-moz-document url-prefix("http://public.avast.com/~cimbal/bi-soj/") {
/* latin-ext */
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans Regular'), local('OpenSans-Regular'), url(https://fonts.gstatic.com/s/opensans/v17/mem8YaGs126MiZpBA-UFW50bbck.woff2) format('woff2');
  unicode-range: U+0100-024F, U+0259, U+1E00-1EFF, U+2020, U+20A0-20AB, U+20AD-20CF, U+2113, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */
@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans Regular'), local('OpenSans-Regular'), url(https://fonts.gstatic.com/s/opensans/v17/mem8YaGs126MiZpBA-UFVZ0b.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}

html {
 background-color:#fefefe
}
body {
 font-family: Open Sans;
 color:#454545;
 font-size:16px;
 margin:2em auto;
 max-width:1000px;
 padding:1em;
 line-height:1.4;
 text-align:justify
}

h1 a font, h1 a {
	font-style: normal;
    text-decoration: none;
    text-align: center;
    display: block;
    font-size: 38px;    
    color: black;
    margin-bottom: 20px;
}

a {
 color:#07a;
 text-decoration:none;
}
a:visited {
 color:#941352;
}

h3 font u {
	color: black;
    text-align: center;
	text-decoration: none;
    display: block;
    font-weight: bold;
}

h3 font {
    font-style: normal;
    font-weight: 400;
	margin: 10px 0;
    font-size: 28px;
   	color: black;
}

h3 {
	font-size: 25px;
}

img {
	width: 90%;
    margin: 2em auto;
    display: block;
}

font {
    font-style: italic;
}

h5 font {
    font-weight: normal;
    font-size: 17px;
    text-align: justify;
}

h5 br {
	display: none;
}

td:first-child {
    display: none;
}

hr {
	border: 1px dashed #ddd;
    margin: 30px auto;
    width: 100%;
    padding: 0px;
}

td center {
	text-align: left;
     line-height:2;
}

td center a + br {
	display: none;
}

tbody:after {
   content: url('http://public.avast.com/~cimbal/bi-soj/demotivational-posters-machine-code.jpg');
   display: block;
   text-align: center;
    margin: 2em auto;
}
}
