style.css:
@-moz-document domain("antyweb.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #fa4b2a #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #fa4b2a !important;
}
a:hover,p,span,small{
    color: #fff !important;
}
*{
    box-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #fa4b2a !important;
}
input[type="submit"]:hover{
    background: #fa4b2a !important;
    color: #fff !important;
}
/*HEADER*/
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-1,header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-3,header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(1),header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(2),header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(3),header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(4),header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-2 .dl-box:nth-child(5),.top-menu .sub-menu{
    background: #1c1c1c;
}
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-3 a p{
    color: #fa4b2a !important;
}
header[role="banner"] nav[role="navigation"] .dropdown-list.d-l-3 strong{
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.art-list .item .inside .box-text,.bg-white{
    background: #1c1c1c;
    border: 1px solid #fa4b2a;
}
.box-tags .tag{
    color: #1c1c1c !important;
}
.box-tags .tag:hover{
    color: #fa4b2a !important;
    background: #1c1c1c !important;
}
.item .inside:hover .strong,.zeropln-article:hover h3 a{
    color: #fff !important;
}
.bg-grey-light{
    background: #fa4b2a;
}
.links-list .item:hover p{
    color: #1c1c1c !important;
}
.grid-box-color,#zero-pln{
    background: #1c1c1c !important;
    border: 1px solid #fa4b2a;
}
.bg-orange{
    border: 2px solid #fa4b2a;
}
.bg-orange span{
    color: #1c1c1c !important;
}
.box-buttons .bg-orange:hover{
    background: #1c1c1c;
    border: 2px solid #fa4b2a;
}
.bg-orange:hover span{
    color: #fa4b2a !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
section .art-lead,.block-more__title{
    color: #fff !important;
}
.b-more-list-item__title{
    color: #fa4b2a;
}
.b-more-list-item__title:hover{
    color: #fff;
}
.image-link img{
    max-width: 50%;
    margin: 0 auto;
}
.box-tags-default .tag{
    background: #1c1c1c;
    border: 1px solid #fa4b2a;
}
.box-tags-default .tag:hover{
    background: #fa4b2a;
    color: #1c1c1c !important;
}
/*KOMENTARZE*/
.btn.black{
    background: #1c1c1c;
    border: 2px solid #fa4b2a;
}
.btn.black:hover{
    background: #fa4b2a;
    color: #1c1c1c !important;
}
#disqus_thread{
    border: 2px solid #fa4b2a;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
.grid-box-review.category .btn.small.active{
    background: #fa4b2a;
    color: #1c1c1c !important;
}
.grid-box-review.category .btn.small.active:hover{
    background: #fff;
}
.grid-box-review.category .btn.small:hover{
    background: #fa4b2a;
}
.reviews-list{
    background: #0000006e;
}
/*OKAZJE*/
.flickity-slider header h3 a{
    color: #1c1c1c !important;
}
.flickity-slider header h3 a:hover{
    color: #fff !important;
}
.flickity-slider ul li a span:hover{
    color: #1c1c1c !important;
}
.category-link:hover{
    background: #1c1c1c;
}
.button,.label{
    background: #1c1c1c;
    border: 2px solid #fa4b2a;
}
.button:hover{
    background: #fa4b2a;
    color: #1c1c1c !important;
    border: 2px solid #1c1c1c;
}
article header{
    background: #1c1c1c !important;
    border: 2px solid #fa4b2a;
}
.mr-20{
    margin-right: 4rem;
}
/*OKAZJE*/
/*POLITYKA-PRYWATNOŚCI*/
.career-wrapper ul li{
    color: #fff;
}
/*POLITYKA-PRYWATNOŚCI*/
/*PODSTRONY*/
/*FOOTER*/
.special-footer,.box-team{
    background: #1c1c1c;
}
footer .columns-wrapper .left span:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #fa4b2a;
    cursor: none;
}
/*FOOTER*/
}
