@-moz-document url-prefix("https://jira.com") {
:root {
    --text-1: #ddd;
    --text-2: #bbb;
    --text-3: #555;
    --text-blue: #59d;
    --text-orange: #b50;
    --text-red: #a33;
    --link-blue: #36e;    
    --bg-body: #21252D;
    --bg-dark-grey: #2C3037;
    --bg-grey: #434953;
    --bg-light-grey: #666;
    --bg-very-light-grey: #999;    
    --bg-blue: #159;
    --bg-dark-blue: #137;
    --bg-orange: #3c1b04;    
    --bg-yellow: #972;
    --bg-dark-yellow: #221;    
    --bg-bright-yellow: #db3;
    --bg-green: #3A3;
	--bg-dark-green: #151;
    --bg-red: #733;
    --bg-dark-red: #422;    
    --border-colour: #555;
    --border: 1px solid var(--border-colour);
}
/* Nav Menu */
.aui-dropdown2 li *:hover
{
    background: var(--bg-dark-grey) !important;
}
/* Weird Arrorw */
.aui-theme-default .aui-button.aui-button-subtle svg g path,
.ghx-backlog div div div div button svg g path
{
    fill: var(--text-1);
}
.aui-button.aui-button-subtle.ghx-expander.ghx-heading-expander.js-version-details-toggle
{
    padding:0px;
}
.aui-header
{
	background-color: var(--bg-blue);
}

/* Checkbox */
form.aui:not(.aui-legacy-forms) .checkbox input:not(:checked)+.aui-form-glyph:before
{
    border: var(--border);
    background-color: var(--bg-very-light-grey);
}

.ghx-parent-group.ghx-away 
{
    background-color: var(--bg-grey);
}

.aui-header .aui-button.aui-button-primary.aui-style:focus,
.aui-button.aui-button-primary.aui-style.create-issue:before
{
    background-color: transparent;
    box-shadow: none;
    
}
.layout .column .gadget
{
	background: transparent;
    border: none;
    border-left: 0;
    border-right: 0;
    padding: 0 1px 0 1px;
}

.aui-dropdown2.aui-layer.aui-style-default[x-placement*=bottom],
.aui-inline-dialog,
.check-list-select .aui-list-scroll .no-suggestions,
.quicksearch-dropdown[data-v-62ffbf75],
.aui-dialog2,
div.aui-inline-dialog-contents
{
    border: var(--border);
}

/* change the color of the gadgets */
/* Teal */
.gadget.color5 .dashboard-item-header, .gadget-colors .color5>a
{
    background-color: #09a;
}
/* Blue */
.gadget.color1 .dashboard-item-header, .gadget-colors .color1>a 
{
    background-color: var(--bg-blue);
}
/* Green */
.gadget.color4 .dashboard-item-header, .gadget-colors .color4>a
{
    background-color: var(--bg-dark-green);
}
/* Orange */
.gadget.color3 .dashboard-item-header, .gadget-colors .color3>a 
{
    background-color: #c80;
}
/* Red */
.gadget.color2 .dashboard-item-header, .gadget-colors .color2>a 
{
	background-color: #C21;
}
/* Purple */
.gadget.color6 .dashboard-item-header, .gadget-colors .color6>a
{
 	background-color: #54b;   
}
/* Pink */
.gadget.color7 .dashboard-item-header, .gadget-colors .color7>a
{
 	background-color: #C0A;    
}
/* END OF GADGET COLORS */

.filter-details-section.full-section {
    background-color:transparent!important;
}
.aui-header a,
.issue-list>li .splitview-issue-link .issue-link-summary,
.aui-message:after,
.icon:after
{
    color: var(--text-1) !important;
}
rect[fill="#dfe1e5"]
{
    fill: var(--bg-dark-blue) !important;
}
rect[fill="#deebff"]
{
    fill: var(--bg-bright-yellow) !important;
}

rect[fill="#e3fcef"]
{
    fill: var(--bg-green) !important;
}
rect[fill="#c0c0c0"]
{
	fill: var(--bg-dark-grey) !important;

}

form.aui:not(.aui-legacy-forms) select:not([size])
{
	background-blend-mode: lighten  luminosity;
}
