style.css:
@-moz-document domain("www.vidlii.com") {
.s_search input[type=submit] {
    padding: 4px 10px;
    border: 5px solid #dfdfdf;
    font-size: 12px;
    background: #fff;
    background: -moz-linear-gradient(top,#fff 0,#efefef 100%);
    background: -webkit-linear-gradient(top,#fff 0,#efefef 100%);
    background: linear-gradient(to bottom,#fff 0,#efefef 100%);
    background: url(https://i.imgur.com/VDT5Nqc.png);
    font-weight: bold;
    color: #039;
    border-top-right-radius: 3.5px;
    border-bottom-right-radius: 3.5px;
    cursor: pointer;
    outline: 0;
}

.s_search input[type=search] {
    border: 5px solid #dfdfdf;
    border-right: 0;
    outline: 0;
    padding: 4px 7px;
    font-size: 12px;
    width: 400px;
}

#s_toggle2 {
    display: block !important;
    width: 222px;
    background: rgba(0,0,0,0) !important;
    box-shadow: unset !important;
    border: 0;
    right: 630px;
    top: 40px;
     
}

.s_head {
    margin-top: 6px;
    margin-bottom: 15px;
}
#s_toggle a, #s_toggle2 a {

    color: #03c;
    display: inline-block;
    padding-left: 0;
    font-size: 13px;
    font-weight: 700;
 
}

#s_toggle2 a:after {
    content: "⠀";
    font-weight: bold;
}

.s_head img {
    height: 75px !important;
    width: 0px !important;
    padding-left: 131px !important;
    padding-top: 0px !important;
    background: url(https://i.imgur.com/QypbrXh.png) no-repeat !important;
    background-size: contain !important;
}

.s_a {
    float: left;
    top: 18px;
    margin-left: 760px;
    border-left: 1px solid #ccc;
    
    background: #fffbc7;
    background: -moz-linear-gradient(top,#fffbc7 7%,#f2d70c 100%);
    background: -webkit-gradient(linear,left top,left bottom,color-stop(7%,#fffbc7),color-stop(100%,#f2d70c));
    background: -webkit-linear-gradient(top,#fffbc7 7%,#f2d70c 100%);
    background: -o-linear-gradient(top,#fffbc7 7%,#f2d70c 100%);
    background: -ms-linear-gradient(top,#fffbc7 7%,#f2d70c 100%);
    background: linear-gradient(to bottom,#fffbc7 7%,#f2d70c 100%);
    user-select: none;
    color: #8a4100!important;
    font-size: 13px;
    font-weight: 700;
    padding: 2px 15px;
    border: 1px solid #f4c800;
    border-radius: 5px;
}
#s_username {
    margin-right: 13px;
    padding: 1px 13px;
    border-right: 0px solid #039;
    cursor: pointer;
        color: #039;
    user-select: none;
    border-top: 1px solid transparent;
    border-bottom: 1px solid transparent;
    -moz-user-select: none;
}

}

@-moz-document url-prefix("http://example.com") {
.s_a {
    float: left;
    top: 18px;
    margin-left: 13px;
    border-left: 1px solid #ccc;
    padding: 1px 0 0 790px;
}
}
