  .ArticleHeaderComponent, .ArticleBodyComponent, #ArticleHeaderComponent, .ArticleBodyComponent .text_editor_paragraph,
  .SocialNetworkSharer,
  .YnetHeader .headerTop,
  .WeatherViewInHeader .weatherCitieSelect, .menuMore.open,
  .FlashPageComponenta,
  .MultiImages1280Componenta .slotsContent .slotView,
  .MultiImagesYplusComponenta .slotsContent .slotView,
  .grid1280_2 .TickerComponenta1280 .slotListRow .slotView .tooltip,
  .pplus #main {
    background-color: #222;
    color: #fafafa;
  }

  .logo img {
    filter: drop-shadow(0 0.5px 2px #aaa);
  }

  .article_title,
  .art_header_title,
  .art_tkb_talkback_title,
  .art_tkb_talkback_index,
  .art_headlines a,
  #SiteArticleComments .topView .commentTitle,
  #SiteArticleComments .topView .commentText,
  #SiteArticleComments .topView .details,
  .ArticleCommentsTopTabUI1280 .tabTitle span:not(.tbIcon),
  A.tickerTextA:link, A.tickerTextA:visited,
  .TickerComponenta1280 .slotListRow .slotView .moreDetails a,
  .multiarticles .mta_pic_text, .multiarticles .mta_title, .multiarticles .mta_title:link, .multiarticles .mta_title:visited, .multiarticles .mta_title:active, .multiarticles .mta_title:hover,
  .bigdealhomepage .bdl_items .bdl_item .item_title,
  .WinWinHP1024 .item_container .item_title a,
  .rpphp_main_header .pphp_main .rpphp_main_title span,
  .multiimagesnews_main .multiimagesnews_main_title span,
  .pphp_main .pphp_main_title span,
  .MultiImagesLeft_main .MultiImagesLeft_main_title span,
  .ArticleBodyComponent .ArticleImageComponenta .ImageDetails > *,
  .ArticleImageCaption,
  .YnetHeader .headerTop .rightSideLinks > *,
  .YnetHeader .headerTop .YnetPremiumHeaderLogin .loginBtn,
  .FlashPageComponenta .Accordion .AccordionSection .titleRow .date,
  .FlashPageComponenta .Accordion .radioViewsPreference label,
  .FlashPageComponenta .Accordion .AccordionSection .titleRow.opened .date,
  .FlashPageComponenta .Accordion .AccordionSection .titleRow.opened .title,
  .blog-auto-feed .blogs-auto-feed-items-container .blogs-auto-feed-item.responsive .item-info .title-row .title-section,
  .BlogsPageTitleComponenta .BlogsPageTitle,
  .BlogsPageTitleComponenta .BlogsPageSubTitle {
    color: #fafafa;
  }

  .trj_subnav a, .categoryBreadcrumbs a,
  ul.trj_trajectory a,
  a.title,
  .ArticleBodyComponent .pHeader,
  .TopStory1280Componenta *,
  .categoryBreadcrumbs1280_2 ul li:first-child a, .categoryBreadcrumbs1280_2 ul li:first-child span,
  .TickerComponenta1280 .slotListRow .slotView .moreDetails > a,
  .TickerComponenta1280 .slotListRow .slotView .moreDetails a:hover,
  .AddCommentForm .AddCommentFormContainer .bottomPanel .termsOfService,
  .MultiImages1280Componenta .slotsContent .slotView a div, .MultiImages1280Componenta .slotsContent .slotView a:hover div,
  .MultiImagesYplusComponenta .slotsContent .slotView a div, .MultiImagesYplusComponenta .slotsContent .slotView a:hover div,
  .ynet .MultiArticleAutoComponenta1280 .slotsContent .withImagePreview .slotView .MultiImagesTopItemCol .slotTitle,
  .ynet .MultiArticleAutoComponenta1280 .slotsContent .slotList .slotView .MultiImagesTopItemCol .slotTitle,
  .slotTitle, .slotTitle span, .slotTitle a,
  .slotSubTitle, .slotSubTitle span, .slotSubTitle a,
  a[style="color:#000000"],
  .BlogsHotTitlesComponenta .headline {
    color: #fafafa !important;
  }

  .art_header_sub_title,
  .ArticleHeaderComponent .subTitleWrapper .subTitle,
  a.sub-title,
  .grid1280_2 .TickerComponenta1280 .slotListRow .slotView:hover > .slotTitle,
  .grid1280_2 .TickerComponenta1280 .slotListRow .slotView:hover > .moreDetails > a > div {
    color: #ddd !important;
  }

  .art_tkb_talkback_content.art_tkb_talkback_content_visible,
  .ArticleComment .expandedView .commentText {
    background-color: #222;
  }

  .art_tkb_talkback.art_tkb_talkback_open, .ArticleComment.expanded,
  .MSAmiddleContent,
  .ticker_comp .news_ticker_iframe {
    background-color: #333;
  }

  .AddCommentFormUI1280 .AddCommentForm .AddCommentFormContainer .userDetailsInputs .userDetailsInput,
  .AddCommentFormUI1280 .AddCommentForm .AddCommentFormContainer .inputStyle {
    background-color: #333;
    color: #fafafa;
  }

  #aTicker table, #bTicker table {
    background-color: #333 !important;
  }

  .moreTickersLink, .TickerComponenta1280 {
    background-color: #333 !important;
    border-color: #333 !important;
  }

  .MSAmiddleContent li p, a, .mainVerticalArticleSharingLinks a.ASLtextIconLink,
  #SiteArticleComments .topView .topLeftView .details .commentToComent {
    color: #88A6E5;
  }

  .DateCaption {
    color: #d80e11;
  }

  .main_search {
    background: #7b7676;
  }

  h2.trj_image, .category-image img,
  .YnetHeader .headerTop .rightSideLinks > * .headerIcon,
  .AccordionSocialsShareComponenta .shareIcon1280,
  .blog-auto-feed .authorAndSocial {
    filter: invert(90%);
  }

  .FlashPageComponenta .Accordion .AccordionSection .titleRow .itemToggler {
    filter: invert(0);
  }

  .iframeComponenta, .iframeComponenta img {
    filter: invert(1);
  }

  .tbl-rtl, .tbl-feed-container,
  #blanket/* Remove loading "blanket" that delays the loading */
  {
    display: none;
  }

  #SiteArticleComments .topView .commentTitle.redTitleText {
    color: #de1a1a;
    font-weight: 600;
  }

  .ArticleComment1280 .topView .topLeftView .details .voteButtonsWrapper svg.likeSvg:hover g,
  .ArticleComment1280 .topView .topLeftView .details .voteButtonsWrapper svg.likeSvg.likePressed g,
  .ArticleComment1280 .topView .topLeftView .details .voteButtonsWrapper svg.dislikeSvg:hover g,
  .ArticleComment1280 .topView .topLeftView .details .voteButtonsWrapper svg.dislikeSvg.dislikePressed g {
    fill: #5456b2;
  }

  .spotimWrapper.popUp {
    background-color: #111;
  }

  .blog-auto-feed .blogs-auto-feed-items-container .blogs-auto-feed-item.responsive,
  .blog-auto-feed .blogs-auto-feed-items-container .blogs-auto-feed-item.responsive.isPinned,
  .blog-auto-feed .blogs-auto-feed-items-container .blogs-item-wrapper.isPinned,
  .grid1280_2 .VideoComponenta .videoInfo, .grid1280_2 .VideoLiveComponenta .videoInfo,
  .VideoComponenta {
    background-color: #222;
  }

  .blog-auto-feed .blogs-auto-feed-items-container .blogs-auto-feed-item.responsive .item-info .title-row .title-row__separator {
    background-color: #fafafa;
  }

  .blog-auto-feed .blogs-auto-feed-items-container .blogs-item-wrapper {
    box-shadow: 1px 1px 7px 0 rgba(255, 255, 255, .18);
  }

  .articlePromoSlot {
    background-color: #222;
    border-color: #666;
  }
}

@-moz-document url-prefix("https://www.ynet.co.il/Ext/Comp/CdaNewsFlash") {
  html {
    zoom: 1.5;
  }

  body, table {
    background-color: #222;
  }

  .text12, .text20b, .text16 {
    color: #fafafa;
  }
}

@-moz-document domain("infogram.com") {
  .InfographicEditor-Contents-Block {
    background-color: #222 !important;
  }

  .ItemContent-text [data-text="true"] {
    color: #fff;
  }

  g.text_tick text, g.tick text, g.igc-label text, g.igc-x-values text {
    fill: #ddd !important;
  }

  g.tick rect {
    fill: rgba(255, 255, 255, .2) !important;
  }

  g.igc-labels text, text.igc-graph-pie-label, g.igc-graph-group text {
    stroke: #fff;
    stroke-width: 3px;
    stroke-linejoin: round;
    paint-order: stroke;
  }

  ._1ReWgYUONQstNoqE4Pn496 ellipse {
    fill: transparent;
  }

  .sheet-player .sheet-player-slider-progress {
    background-color: rgba(255, 255, 255, .4);
  }

  .sheet-player .sheet-player-slider-rail,
  .sheet-player .sheet-player-circle {
    background-color: rgba(255, 255, 255, .2);
  }

  .sheet-player .sheet-player-circle:after {
    border-color: transparent transparent transparent #fff !important;
  }

  table.igc-table tr {
    background-color: #000 !important;
    color: #fff !important;
  }

  table.igc-table tr:nth-child(odd) {
    background-color: #444 !important;
  }
}

@-moz-document url-prefix("https://z.ynet.co.il/fast/content/2020/coronavirus") {
  body {
    background-color: #222;
  }

  .main .flex-box .title,
  .main .flex-box .desc {
    color: #d0d4dc
  }

  .main .flex-box {
    background-color: #313132;
    border-color: #50515a;
  }

  .main .flex-box .number {
    text-shadow: 1px 1px 0 #FFF, 1px -1px 0 #FFF, -1px 1px 0 #FFF, -1px -1px 0 #FFF, 1px 0px 0 #FFF, 0px 1px 0 #FFF, -1px 0px 0 #FFF, 0px -1px 0 #FFF, 0px 0px 1px rgba(255, 255, 255, 0);
  }
}

@-moz-document url-prefix("https://kayma-dashboards.com") {
  .RcRvl, .izOMCT {
    background-color: #333;
    color: #FFF;
  }

  .STxCN {
    color: #FFF;
  }
}
