#login {
  /* display: flex;
  flex-direction: column;
  align-items: center; */
  border-radius: 10px;
  border: transparent;
  padding: 30px !important;
  -webkit-box-shadow: var(--primary-shadow);
          box-shadow: var(--primary-shadow);
  background-color: white;
  width: 50%;
  position: relative;
  margin: 50px;
  opacity: 0;
  -webkit-animation: moveup 0.7s forwards ease-in-out;
          animation: moveup 0.7s forwards ease-in-out;
  -webkit-animation-delay: 0.1s;
          animation-delay: 0.1s;
}

#login::after {
  position: absolute;
  /* position: relative; */
  top: 10%;
  right: -105%;
  content: "";
  display: block;
  z-index: -1;
  background-image: url(https://res.cloudinary.com/dna1lurcx/image/upload/v1584593647/learning_fyniq7.svg);
}

#login::before {
  position: absolute;
  /* position: relative; */
  top: 10%;
  left: -105%;
  content: "";
  display: block;
  z-index: -1;
  background-image: url(https://res.cloudinary.com/dna1lurcx/image/upload/v1584593648/support_qqu5ai.svg);
}

#login::after, #login::before {
  background-repeat: no-repeat;
  background-size: var(--desktop-svg) var(--desktop-svg);
  width: var(--desktop-svg);
  height: var(--desktop-svg);
}

