/* responsive */

@media only screen
and (max-width: 1024px) {
  body {
    background-size: 200% !important;
  }
  #login::after {
    right: -75%;
  }
  #login::before {
    left: -75%;
  }
  #login::after, #login::before {
    background-repeat: no-repeat;
    background-size: var(--mobile-svg) var(--mobile-svg);
    width: var(--mobile-svg);
    height: var(--mobile-svg);
  }
}

