.table>thead>tr>th{border-bottom:2px solid #444;}

.affinity .header .user-vcard{background:#333;}
.header{background:url(https://cdn.lovepedia.cloud/img/background/test.png) rgba(255, 15, 15, 0.5) !important;}

.affinity .test-result .lovemeter .circle{border:1.3em solid #444;}
.affinity .test-result .lovemeter .bar{background:#000;border:1.3em solid #444;border-left:none;}
.affinity .text-result{border-top:1px solid #333;}
.affinity .text-result .match-table .progress .progress-bar{background:#349886;}

.lovepoints-section .info-block .info-box .page-header .info-title{color:#000;}
.lovepoints-section .info-block .info-box{color:#000;}
.list-group .media .list-group-item-text{color:#999;}
.list-group .gone .list-group-item-text{color:#fff;}

.error-page.adb .content{background:#333;border-radius:0px;}
.error-page .content .choice>div:first-child{border-right:1px solid #222;}
.load-more, .prev-elements{background-color:#333;border-bottom:0px;}
.load-more:hover, .prev-elements:hover{background-color:#444;border-bottom:0px;}
.load-more:active{background-color:#222;box-shadow:none;border:none;}
.chat-section .chat-menu:after,.chat-content:before{background:#111;}

.magazine-modal-reply .modal-body .panel.comment .panel-footer .panel.comment .panel-body,.magazine-modal-reply .modal-body .panel.comment.comment-endorse.comment-endorse-warning .panel-heading{background:#333;}
.magazine-modal-reply .modal-footer{background:#444;border-top:1px solid #000;}
.widget.magazine-table-of-content .panel .panel-body, .widget.magazine-table-of-content .panel .panel-heading{background:#222;}
.inbox .message-number{color:#fff;background:#000;border-radius:9999px;padding:0px;margin:5px;font-size:10pt;width:25px !important;display:inline-block;text-align:center;font-weight:bold;}
.settings-section .no-modal .save-settings{border-top:1px solid #333;}
.interest-header .action-bar.action-chat, .profile-header .action-bar.action-chat{background-color:#222;}

section.photo-endorse h2.title{color:#fff;border-bottom:1px solid #000;}
aside .widget.photo-endorse-top-contributors .mini-vcard.active{background:#222;border:1px solid #111;}
.progress{background:#000;}
footer{background-color:#000 !important;}

body.eadv-pseudoskin-box{padding-top:0px !important;}
#htv_div_container{background-color:rgba(0,0,0,1) !important;}
iframe[id*="htv_content_frame"]{display:none;}
}

@-moz-document url-prefix("https://it.lovepedia.net/search.php"), url-prefix("https://en.lovepedia.net/search.php"), url-prefix("https://es.lovepedia.net/search.php") {
/*TEST AVATAR*/
.round-img{height:256px;width:256px;border-radius:0px;border:1px solid #999;}
.round-img .round-span{margin-left:0px;height:256px;width:256px;background:#000;}
.round-img .round-span img{height:256px;width:auto;}
.menu-item .round-img{height:50px;width:50px;border-radius:999px;border:0px;}
