style.css:
@-moz-document domain("changethistoderpibooru.org") {
body {
	color: #cacaca;
}



.block__content,
.block__tab {
	border-left: 0;
	border-right: 0;
	background: #252323;
}
.media-box__header {
	min-width: 100%;
	background: #ffffff1f;
	color: #cecece;
	line-height: 22px;
	font-size: 12px;
	text-align: center;
	white-space: nowrap;
}
.block__header,
.block__header--sub,
.block__header--single-item {
	font-size: 14px;
	line-height: 32px;
	background: #4e4c4c;
	color: #333;
	box-shadow: 0 0 6px #00000033;
}
/* cards */
.column-layout__left .media-box {
	display: inline-block;
	position: relative;
	box-shadow: 2px 3px 4px 2px #00000029;
	background-color: #ffffff21;
}
.media-box {
	display: inline-block;
	position: relative;
	border: 0.6em solid #403e3e;
	margin: 1em;
	box-shadow: 2px 3px 4px 2px #00000029;
	background-color: #ffffff21;
	transition: 0.3s;
	/* 	if is really slow down your pc .turn backdropfilter off */
	backdrop-filter: blur(15px);
}
.media-box:hover {
	/* 	 use scale()  to control how big it shows*/
	transform: scale(1.5);
	background-color: #00000040;
	z-index: 999;
	border: 0.6em solid#0003;
	border-radius: 2em 0;
}
.media-box:active {
	transform: scale(1.3);
	transition-timing-function: cubic-bezier(0, 0.88, 0.71, 0.98);
}
/* toolbar stuff */
a.interaction--fave {
    color: #ffdb00 !important;
}
a.interaction--fave.active {
    background: #e6ff0073;
    color: #fff;
    text-shadow: 0 0 8px #fcff00;
    color: #fff4b4 !important;
}
a.interaction--fave:hover{
	    background: #fffc40;
	color:#000 !important;
}
.block__header a,
.block__header--sub a,
.block__header--single-item a {
	background: #4c4a4a;
	color: #53a0d8;
}
.block__header a:hover,
.block__header--sub a:hover,
.block__header--single-item a:hover {
	background: #4e4c4c;
	color: #0addff;
	box-shadow: 0px -2px 0px #32ffeb;
}
.page-current {
	padding: 8px;
	box-shadow: inset 0 0 12px #272727;
	color: #c3c3c3;
	background-color: #25232357;
}
#container {

	background: #383838;
}
.block__header,
.block__header--sub,
.block__header--single-item {
	font-size: 14px;
	line-height: 32px;
	background: #4e4c4c;
	color: #9c9c9c;
}
/* footer stuff */
#footer {
	background: #383838;
}


#footer .footercol {
	flex: 1 0 auto;
	width: auto;
	padding: 2em;
	margin: 1em 0 3em 0;
	background-color: #2d2d2d;
	box-shadow: inset 0 0 20px 18px #15151547;
	border-radius: 20px;
}

a:hover {
	color: #26fffe;
}
/* header stuff */
.header--secondary {
	background: #333131;
	line-height: 32px;
}
.header--secondary a {
	line-height: 32px;
	color: #a9a9a9;
	background: #333131;
}
.header--secondary a:hover,
.header--secondary .header__dropdown:hover > a {
	background: #4e4c4c;
	background: #4e4c4c;
	color: #0addff;
	box-shadow: 0px -2px 0px #32ffeb;
}
.header--secondary span.header__counter {
	padding: 0 7px;
	margin-left: -2px;
	background: #5a5a5a;
	border-radius: 50%;
	transform: scale(0.8);
}
.flash--site-notice {
	background: #7d1752;
	color: #f170ba;
	box-shadow: 1px 1px 5px black;
}
/* view pic */
.image-show-container {
	color: #333;
	display: inline-block;
	max-width: 100%;
	padding-bottom: 6px;
	align-self: center;
	    border: 0.7em solid #00000033;
	box-shadow: 0 0 9px black;
	    background-color: #1f1f1f;
}
.center--layout--flex {
	padding: 3%;
}
.image-description {
	background: #2f2f2f;
	padding: 1.5em;
	border: 1px solid #101213;
	color: darkgrey;
	box-shadow: inset 0 0 20px 3px #1d1d1d;
	margin-bottom: 2em;
}
/* ADs, i like derpibroou , we should support them */
#imagespns :before {
	content: "hover to see ad    ";
}
#imagespns:hover :before {
	content: " ";
}
#imagespns {
	height: 16px;
	width: 120px;
	border: 1px solid #000000;
	padding: 2px 10px;
	background: #1d1c1c;
	text-align: center;
	color: yellowgreen;
	overflow: hidden;
}
#imagespns:hover {
	height: 100%;
	width: 100%;
}
/* login */
.button,
.toggle-box + label {
	background-color: rgba(0, 0, 0, 0.58);
	border: 1px solid #040404;
	color: #929292;
	border-radius: 5px;
	box-shadow: 0 0 5px black;
	transition: 0.1s;
}
.button:hover {
	background-color: rgba(0, 0, 0, 0.58);
	border: 1px solid #040404;
	color: #929292;
	border-radius: 5px;
}
.button:active {
	transform: scale(0.85);
}

/* comment */
.block__header--js-tabbed a {
	border: none;
}
.block__header--js-tabbed a:first-child {
	border-left: 3px solid #272424;
}
.block__header--js-tabbed a:last-child {
	border: none;
}
.block__header--light a,
.block__header--js-tabbed a {
	color: #6ab6ec;
}
.block__header--js-tabbed {
	border: none;
}
.input {
	border: 2px solid #0a0909;
	border-radius: 5px;
	color: #fbfbfb;
	background: #252525e0;
}
.input:focus {
	outline: none;
	border: 2px solid #0a0909;
	background: #252525;
}

.communication__toolbar__button {
	background-color: #080808;
	border: 1px solid #3e3e3e;
	color: #b5b5b5;
	margin: 2px;
	padding: 5px 10px;
	border-radius: 4px;
	box-shadow: 0 0 3px 0px black;
}
.communication__toolbar__button:hover {
	background-color: #1d1d1d;
}
/* spolier
 */
.spoiler {
	transition: 0.2s;
	background-color: #FF3CAC;
	background-image: linear-gradient(225deg, #FF3CAC 0%, #784BA0 50%, #2B86C5 100%);
	color: transparent;
	border-radius: 4px;
}
.spoiler:hover {
	/*   font-size: 72px; */
	background: linear-gradient(225deg, #FF3CAC 0%, #784BA0 50%, #2B86C5 100%);
	-webkit-background-clip: text;
	-webkit-text-fill-color: transparent;
	text-shadow: 0 0 1px #ffffff75;
	box-shadow: inset 0px 0px 20px 0px black;
}

.spoiler-revealed {
	/*   font-size: 72px; */
	background: linear-gradient(0deg, #08AEEA 0%, #2AF598 100%);
	-webkit-background-clip: text;
	-webkit-text-fill-color: transparent;
	text-shadow: 0 0 1px #ffffff75;
	background-color: #08AEEA;
	background-image: linear-gradient(0deg, #08AEEA 0%, #2AF598 100%);
}

.imgspoiler {
	background: linear-gradient(0deg, #0277a2 0%, #05bf6a 100%);
}


/* chat */
.table {
	padding-left: 10px;
	width: 100%;
	border-collapse: collapse;
	border: 2px solid #0c0c0c;
}
.table > thead > tr {
	background: #0c0c0c;
}
.table > tbody > tr:nth-child(odd) {
	background: #3b3d3e;
}
.table > tbody > tr:nth-child(even) {
	background: #252525;
}
.block__content,
.block__tab {
	background: #252323;
	padding: -1px 3px;
}
td a {
	color: #a6a6c3;
}
td.table--communication-list__name {
	padding: 13px;
}
.small-text {
	margin-top: 7px;
	color: #848484;
}
td time {
	color: #848484;
	text-shadow: 0 0 13px black;
}

td.table--communication-list__stats {
	width: 6%;
	color: #a7a7a7;
}
td a:visited {
	color: #b551c5;
}

.communication {
	box-shadow: 0 0 8px black;
	margin: 22px 0;
	padding: 10px;
	background-color: #252323;
}
.communication .block__content {
	box-shadow: none;
}
.communication__body__text {

	line-height: 140%;
}
.block__content:first-child {
	border-top: none;
}
.block__content:last-child,
.block__tab:not(.hidden) {
	border-bottom: none;
}
.communication__options {

	border: none;
}
/*  avtar*/
.avatar--100px img {
	border-radius: 14px;
	overflow: hidden;
}
.avatar--100px {
	border-radius: 14px;
	box-shadow: 0 0 20px 6px #00000057;
	overflow: hidden;
}
/* lables blocks */
.label--primary {
	background: #061117;
	border: none;
	border-top: 9px solid #252323;
	color: #566982;
}
.block--warning {
	background: #ffe70012;
	border-color: #ffe500;
	color: #c1c1c1;
}
.block--success {
    background: #1a2d0f !important;
    border-color: #bde9a3;
    border: none;
}
.block--fixed {
    padding: 6px 12px;
    background: #292929;
    /* border: 1px solid #d9e7f1; */
    line-height: 1.35;
}
/* streaming */
.label--success {
    background: #335d1a;
    border: none;
    box-shadow: 0 0 3px #0000004d;
}
.label--danger {
    background: #651f17;
    border: none;
    box-shadow: 0 0 6px #00000038;
    color: #ff6161;
}
/* user profile */

.barline__bar {
    fill: #b3b3b3;
}
.sparkline {
    border-bottom: 1px solid #b3b3b373;
}

.alternating-color:nth-child(even) {
    background: #4e4c4c;
}
.alternating-color:nth-child(odd) {
    background: #4e4c4c;
}
.block__header, .block__header--sub, .block__header--single-item {

    border-bottom: 2px solid #3b3d3e;
}


/* tags  */
.tag {
	border:none;
    background: #181f03;
    color: #6f8f0e;
    border-radius: 4px;
}
.tag__count {
    color: #ffffff5c;
}
.tag[data-tag-category="character"] {
    background: #0f2b26;
}
.tag[data-tag-category="content-fanmade"] {
    background: #38172c;

}
.tag[data-tag-category="species"] {
    background: #2f2015;
}
.tag[data-tag-category="error"] {
    background: #440b16;
}
.tag[data-tag-category="origin"] {
    background: #0c0f35;
    color: #6a6fb3;
}
.tag[data-tag-category="content-official"] {
    background: #464210;
}
.tag[data-tag-category="spoiler"] {
    background: #401f16;
}
.tag[data-tag-category="rating"] {
    background: #0e293a;
}
.tag[data-tag-category="oc"] {
    background: #4a2050;

}
/* tag drop down */
.dropdown .dropdown__content>*:not(.hidden) {
    display: block;
    white-space: nowrap;
    background-color: #000000d4;
    border-bottom: 1px solid #ffffff38;
    box-shadow: 6px 3px 6px #00000082;
		backdrop-filter: blur(15px);
}

/* left bar */
.column-layout__left .media-box {
    border: none;
	margin:0;
	transform: translate3d(5px, -4px, 5px);
}

.column-layout__left  .media-box:hover {
    transform:scale(1.3) !important;
}
a.block__header--single-item, .block__header a, .block__header--sub a, .block__header--single-item a {
    color: #73c5ff;
}
a.block__header--single-item:hover, .block__header a:hover, .block__header--sub a:hover, .block__header--single-item a:hover {
    background: #ffffff4a;
    color: #9ddfff;
}
.flex__grow a {
    color: #ffffffba;
}
.flex__grow a:hover{
    color: #ffffff;
}

/* filter */
.filter {
    background: #2e3940;
    border: 1px solid #717171;
    padding-left: 14px;
    margin-bottom: 10px;
}



/* star upvote downvote */
}
