@-moz-document domain("community.claris.com"), domain("partner.claris.com")
{
	html {
		font-size: 20px;
		overflow-x: hidden;
		font-family: SFPro, 'Hiragino Sans', 'BIZ UDPGothic', Sans-Serif;
	}
	a:focus-ring {
		outline: none;
	}
	.cFileMaker_EN_CommunityTheme {
		font: 400 .7rem/1rem SFPro, 'Hiragino Sans', 'BIZ UDPGothic', Sans-Serif;
	}
	.cFileMaker_EN_CommunityTheme .templateHeaderFull .templateHeaderBottom>[data-region-name=templateHeaderBottom]:not(:empty) {
		min-height: 80px;
	}
	.cFileMaker_EN_CommunityTheme .headlineTitleText {
		font: 600 2rem/2.25rem SFPro, 'Hiragino Sans', 'BIZ UDPGothic', Sans-Serif;
	}
	.cFileMaker_EN_CommunityTheme .headlineInfo {
		font: 400 1rem/1.3rem SFPro, 'Hiragino Sans', 'BIZ UDPGothic', Sans-Serif;
		margin-top: .3rem;
	}
	.forceCommunityHeadline .headlineTitle {
		padding: 0;
	}
	.cPeak_TopicsFollowed {
		display: none;
	}
	.cFileMaker_EN_CommunityTheme p,
	.cFileMaker_EN_CommunityTheme table,
