	.cFileMaker_EN_CommunityTheme .h6 {
		font: 600 1.25rem/1.5rem SFPro, 'Hiragino Sans', 'BIZ UDPGothic', Sans-Serif;
	}
	.ql-editor,
	.cFileMaker_EN_CommunityTheme~.comm-panels-container .forceChatterQuestionPost,
	.cFileMaker_EN_CommunityTheme~.comm-panels-container .forceChatterQuestionPost .forceChatterTopicSuggestionAutoComplete .uiInput input,
	.cFileMaker_EN_CommunityTheme~.comm-panels-container .forceChatterQuestionPost input,
