style.css:
@-moz-document domain("grouple.co"), domain("readmanga.live"), domain("mintmanga.live"), domain("doramatv.live"), domain("librebook.me"), domain("findanime.net"), domain("selfmanga.live"), domain("selflib.me"), domain("mose.live") {
/* grouple all sites light theme v.2.0 by krravvi */

/* ============ NONE ============ */

div.co_ba, .co_ba.middle_ba,
div.co_ba.reader-comment-ba,
div.co_ba.top_ba,
div.co_ba.right_ba,
div.co_ba.reader_twitts_ba {
    display: none !important;}

.left-brand, .right-brand div, .pull-left {display: none !important;}

/*============ BODY ============ */

body {
    font-family: "Calibri" !important;
    font-size: 15px;
    line-height: 20px;
    letter-spacing: .4px;
    background: #F6F6F6 !important;
    color: #595959;}

a {
    color: #8592B2;
    border: none !important;}

a:hover, a:focus, .listSelected, .js-link.current {
    transition: all .2s linear !important;
    border: none !important;
    background: transparent !important;
    color: #909090 !important;
	opacity: .85 !important;}

p {text-indent: 0px; text-align: left;}

.fa {text-shadow: none !important; text-align: center;}

/*============ BODY: BOX ============ */

div.container.pageBlock {
    top: 60px;
    box-shadow: none !important;
    border: 1px solid #EEE;
    border-radius: 3px;
    margin: 0 auto 5px auto;}

/*============ BODY: BOX - COMMENTS ============ */

.twitter .cm .mess {
    margin-left: 0; 
    line-height: 18px;}

.twitter .cm .comm {
    background: #FAFAFA;
    border-left: 3px solid #EEE;
	margin: 10px;
    padding: 8px 10px;
	border-radius: 3px;
	font-size: 13px;
    letter-spacing: .8px;
    box-shadow: 1px 0px 1px 1px rgba(0,0,0,.1);}

.twitter .cm .children {
    border-radius: 3px;
    padding: 2px;
    margin: 0 0 0 5px;
    border-left: none;}

.twitter .cm .children > div.comm {
    background: #fff !important;
    border-left: 3px solid #E8E8E8;}

.twitter .cm .comm a {
    color: #9CA6C0;
    margin: 0 2px 0 5px;
    font-size: 11px;
    font-weight: bold;
    text-transform: uppercase;}

.twitter .cm .contr {
    opacity: 1 !important;
    cursor: default;
    font-size: 14px;
    color: #909090;}

.twitter .cm .comment_good .votes, .twitter .cm .comment_vgood .votes {
    color: #97C1A2 !important;}

.twitter .cm .comment_bad .votes, .twitter .cm .comment_vbad .votes {
    color: #E79379 !important;}

span.js-link.fa.fa-lg.fa-caret-up:before {
    content: "\f106";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    font-size: 11px;}

span.js-link.fa.fa-lg.del.fa-caret-down:before {
    content: "\f107";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    font-size: 11px;}

.twitter .cm .contr .fa-caret-up,
.twitter .cm .contr:hover .fa-caret-up,
.twitter .cm .contr .fa-caret-down,
.twitter .cm .contr:hover .fa-caret-down {
    font-size: 11px;
    background: #eee !important;
    border-radius: 2px;
    padding: 3px 0 0 1px;
    height: 15px;
    width: 15px;}

.twitter .cm .contr .fa-caret-up {margin: 0 -1px 0 2px;}
.twitter .cm .contr .fa-caret-down {margin: 0 2px 0 -1px;}

span.js-link.fa.fa-lg.fa-caret-up:hover:before {color: #97C1A2 !important;}
span.js-link.fa.fa-lg.del.fa-caret-down:hover:before {color: #E79379 !important;}

.twitter .cm .comm > span > span.js-link.fa.fa-reply:before {
    content: "\f101";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    font-size: 12px;
    vertical-align: -1px;}

.twitter .cm .comm > span > span.js-link.fa.fa-reply:hover:before {color: #595959;}

#twitts > h5 {padding: 5px 0 5px 0; margin: 0;}

#twitts > h5 > span > i.fa.fa-step-forward.text-success.fa-rotate-90.fa-lg:before {
    content: "\f105";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    font-size: 16px;
    color: #9CA6C0;
    background: #F6F6F6;
    border: 1px solid #EEE;
    border-radius: 2px;
    padding: 3px 0 0 1px;
    margin: 0 -20px;
    height: 22px;
    width: 22px;
    display: inline-block;}

#twitts > h5 > span > i.fa.fa-step-forward.text-success.fa-rotate-90.fa-lg:hover:before {color: #595959;}

/*============ BODY: BOX - COMMENTS SCROLL ============ */

#twitts > div.cm_0.cm {
    max-height: 480px;
    overflow-y: auto;}

#twitts > div.cm_0.cm::-webkit-scrollbar {width: 8px;}
#twitts > div.cm_0.cm::-webkit-scrollbar-track {background: #F2F3F7;}
#twitts > div.cm_0.cm::-webkit-scrollbar-button {background: #F2F3F7; height: 3px;}
#twitts > div.cm_0.cm::-webkit-scrollbar-thumb {
    height: 15%;
    background-color: #D6D9E6;
    border-radius: 4px;
    border: 2px solid #F2F3F7;}

/*============ BODY: BOX - TRANSLATOR ============ */

.translator-control {
    margin: -5px 0 10px 0;
    padding: 0 0 10px 0;
    border-bottom: 1px solid #EEE;
    text-transform: uppercase;
    font-size: 11px;}

div.col-md-4.hidden-xs.hidden-sm > h5 {margin: 0; padding: 0;}

div.col-md-4.hidden-xs.hidden-sm {
    text-align: right;}

div.translator-control.row > div.col-md-8 > div > a > img {
    margin: 10px 0 0 0;
    border-radius: 6px;}

/*============ BODY: BOX - OTHER ============ */

.arrow-left, .arrow-right {background-image: none;}
.arrow-left:hover:before, .arrow-right:hover:before {
    transition: all .3s linear !important; 
    color: #DADADA;}

.arrow-left:before {
    content: "\f053";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    color: #E8E8E8;
    font-size: 50px;}

.arrow-right:before {
    content: "\f054";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    color: #E8E8E8;
    font-size: 50px;}

.b-chapter > p, .b-chapter > div,
.b-chapter .section > div, .b-chapter .section > p {color: #595959;}

.p-control {
    box-shadow: none !important;
    background: #FAFAFA; 
    border: 1px solid #CCC;}

/*============ SIDE TOP: LEFT ============ */

.navbar.navbar-default {
    z-index: 1000;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 60px;
    border-radius: 0px;
    padding: 10px 0 10px 0;
    background: #404758;
    opacity: .95;
    backdrop-filter: blur(1px) !important;}

.navbar-nav > li > a {padding-top: 8px; padding-bottom: 8px;}

.federation-nav {
    position: fixed;
    top: 54px;
    left: 0px;
    background: none;}

.federation-nav .container {
    height: auto;
    background: #404758;
    border-bottom: 3px solid #B9BED4;
	border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
    letter-spacing: 1px;
    opacity: .95;}

.federation-nav a {
    color: #FFF !important;
    font-size: 12px;
    font-weight: normal;
    text-transform: uppercase;
    margin: 0 2px;
    padding: 5px 7px;}

.federation-nav a:hover {
    background-color: #B9BED4 !important;
    color: #FFF !important; 
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    opacity: 1 !important;}

.fa-caret-square-down:before,
.fa-caret-square-up:before,
.fa-caret-square-o-down:before,
.fa-caret-square-o-up:before {
    position: relative; 
    top: 0px; 
    left: 10px;
	color: #B9BED4; 
    font-size: 17px;}

.fa-caret-square-down:before,
.fa-caret-square-o-down:before {
    content:"\f13a"; font-family: "FontAwesome", "Font Awesome 5 Pro" !important;}

.fa-caret-square-up:before,
.fa-caret-square-o-up:before {
    content:"\f139"; font-family: "FontAwesome", "Font Awesome 5 Pro" !important;}

/*============ SIDE TOP: RIGHT ============ */

#accountMenu > li.dropdown > a > span.caret {display: none;}

#accountMenu > li.dropdown > a > span.hidden-xs.visible-xxs-inline:after,
#accountMenu > li.dropdown > a > span.hidden-xs.hidden-sm.hidden-md:after {
    content: "\f107";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    color: #B9BED4;
    padding: 0 0 0 10px;}

#accountMenu > li.dropdown > a > span.hidden-xs.visible-xxs-inline,
#accountMenu > li.dropdown > a > span.hidden-xs.hidden-sm.hidden-md {
    top: 5px;
    position: relative;
    text-transform: uppercase;
    font-size: 12px;
    font-weight: normal;
    letter-spacing: 1px;
    color: #B9BED4 !important;
    padding: 0 0 0 10px;}

#accountMenu > li > a > span.badge {
    background-color: #2a2d33 !important;
    vertical-align: 7px;
    font-size: 12px;
    width: auto;
    height: 18px;
    margin: -2px 0 0 -2px;
    padding: 3px 6px;}

#accountMenu.nav.navbar-nav.navbar-right {margin: -4px 0 0 0 !important;}

.navbar-default .navbar-nav > .open > a, 
.navbar-default .navbar-nav > .open > a:hover, 
.navbar-default .navbar-nav > .open > a:focus {
    background: none !important;
    border: none !important;}

.navbar-default .navbar-nav > li > a {
    color: #B9BED4 !important;}

.navbar-default .navbar-form {margin: 0 !important;}

/*============ SIDE TOP: RIGHT - ICONS ============ */

#accountMenu > li > a > span.fa.fa-lg.fa-edit,
#accountMenu > li > a > span.fa.fa-lg.fa-bookmark,
#accountMenu > li > a > i.fa.fa-lg.fa-comment, 
#accountMenu > li > a > i.fa.fa-lg.fa-comments,
#accountMenu > li > a > span.fa.fa-lg.fa-comment,
#accountMenu > li > a > span.fa.fa-lg.fa-comments,
#accountMenu > li > a > span.fa.fa-lg.fa-bell,
#accountMenu > li > a > i.fa.fa-lg.fa-shopping-cart.gold-status,
#accountMenu > li > a > span.fa.fa-lg.fa-shopping-cart {
    color: #FFF;
    background: #353B49;
    padding: 10px 0 0 1px !important;
    margin: 0 !important;
    border-radius: 3px;
    font-size: 14px;
    width: 30px;
    height: 30px;}

#accountMenu > li > a > i.fa.fa-lg.fa-shopping-cart.gold-status:before,
#accountMenu > li > a > span.fa.fa-lg.fa-shopping-cart:before {
    content: "\f155";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    color: #B9BED4;}

#navbar > ul.nav.navbar-nav.navbar-right.hidden-xs.hidden-sm > li > a > span.fa.fa-question-circle.fa-lg:before {
    content: "\f128";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;
    font-size: 14px;
    color: #81899b;
    left: -10px;
    top: 0;
    position: relative;}

/*============ SIDE TOP: RIGHT - DROP ============ */

.navbar-nav > li > .dropdown-menu {
    box-shadow: 0 5px 10px 0 rgba(0,0,0,.2);
    background: #FFF;
    border: 1px solid #DADADA;
    border-radius: 3px;
    font-size: 11px;
    text-transform: uppercase;
    letter-spacing: 1px;
    margin: 20px 0 0 0;
    padding: 8px;}

.dropdown-menu .divider {
    overflow: hidden;
    height: 1px;
    background-color: #EEE;
    margin: 5px 0;}

.navbar-nav > li > .dropdown-menu a {
    color: #595959 !important;
	opacity: 1 !important;}

.navbar-nav > li > .dropdown-menu a:hover {
    color: #BABABA !important;
	opacity: 1 !important;}

/*============ SIDE TOP: RIGHT - DROP - ICONS ============ */

.navbar-nav > li > .dropdown-menu a > .fa-fw {
    width: 35px; 
    padding: 5px;
    text-align: center;
	color: #9CA6C0 !important;}

#accountMenu > li.dropdown.open > ul > li > a > span {
    background: #F2F2F2;
    padding: 7px 0 0 1px;
    margin: 0 10px 0 0;
    border-radius: 3px;
    font-size: 12px;
    width: 24px;
    height: 24px;
    vertical-align: -1px;}

#accountMenu > li.dropdown.open > ul > li > a > span.fa.fa-lg.fa-fw.fa-heartbeat:before {
    content: "\f274";
    font-family: "FontAwesome", "Font Awesome 5 Pro" !important;}

/*============ SIDE SECOND BAR ============ */

.second-nav {
    position: relative;
    top: 60px;
	background: transparent;
	color: transparent;
    padding: 0;
	margin: 5px 0 -2px 0;}

.nav-main-menu {height: 30px;}

.pull-right a {
    color: #757575 !important;
    text-transform: uppercase; 
    font-weight: bold; 
    font-size: 11px; 
    letter-spacing: 1px;
	margin: 0 -7px;
    padding: 10px;}

.pull-right a:hover {
    color: #909090 !important;
    opacity: 1 !important;
    transition: none !important;
    border-bottom: 1px solid #B9BED4 !important;}

.pull-right a > .manga-updated {
    font-weight: bold;
    background: none !important; 
    border: none !important; 
    color: #9CA6C0 !important;}

body > div.second-nav > div > div.nav-main-menu > div.pull-right > strong > i {
    color: #9CA6C0 !important;
    font-size: 13px;}

body > div.second-nav > div > div.nav-main-menu > div.pull-right > strong > i:hover {
    background: transparent;}

/***/

.nav-sub-menu {
    color: #595959;
    background: #FFF;
    border: 1px solid #EEE;
    text-transform: uppercase;
    font-size: 11px;
    letter-spacing: 1px;
    float: right;
    line-height: 20px;
    height: 100px;
    margin: -5px -10px 5px 0;
    padding: 10px 5px 0 5px;
    border-radius: 3px;}

.nav-sub-menu a {
    color: #595959 !important;}

.nav-sub-menu a:hover {
    color: #BABABA !important;}

.nav-sub-menu div {
    margin: 0;
    padding: 0;
    width: auto;}

/*============ NAVIGATION TABS ============ */

.nav-tabs {
    border-bottom: 1px solid #EEE; 
    margin: -10px 0 0 0;}

.nav.nav-tabs > li > a {
    color: #595959 !important;
    border-top: 1px solid transparent !important;
    text-transform: uppercase; 
    font-weight: bold; 
    font-size: 11px; 
    letter-spacing: 1px;
	padding: 7px 10px;
	margin: 0 4px}

.nav.nav-tabs > li > a:hover {
    border-radius: 0px !important;
    color: #BABABA !important;
	opacity: 1 !important;}

.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover {
    color: #FFF !important;
    background: #B9BED4 !important; 
    border: 1px solid #B9BED4;}

.nav.nav-tabs > li > a .badge {
    margin: -5px 0 0 0 !important;}

/***/

.filter-selector {
    font-size: 0px;
    padding: 4px 0;
	margin: -10px 0 5px 0;}

.filter-selector a {
    padding: 0 5px;
    text-transform: uppercase;
    font-size: 12px;
    letter-spacing: 1px;}

.filter-selector .active, .filter-selector .active:hover {
    background: none !important;
    color: #595959 !important;
    font-style: italic;
	opacity: 1 !important;
    cursor: default;}

/*============ TABLE ============ */

thead {
    font-size: 15px;
    text-transform: uppercase;
    border-top: 1px solid #EEE;
    color: #757575 !important;}

.table > thead > tr > th, .table > thead > tr > td,
.table > tbody > tr > th, .table > tbody > tr > td,
.table > tfoot > tr > th, .table > tfoot > tr > td {
    border-top: 1px solid #EEE;
    border-bottom: 1px solid #EEE;
	background: #FFF !important;
	vertical-align: middle;}

.table > tbody > tr.active > th {
    text-transform: uppercase;
    background: #FAFAFA !important;}

.table > tbody > tr.active > td {
    font-size: 15px;
    text-align: end;
    color: #909090 !important; 
	background: #FAFAFA !important;}

.table > tbody > tr > td a {
    color: #595959 !important;
    font-size: 12px !important;
    font-weight: normal;
    letter-spacing: .8px;
    text-transform: uppercase;}

.table > tbody > tr > td a:hover {color: #9CA6C0 !important;}
.table > tbody > tr > td a:visited {color: #9E9E9E !important;}

/*============ TITLE - H1 ============ */

h1 {
    text-transform: uppercase; 
    font-size: 18px;
    font-weight: bold;
    letter-spacing: .8px; 
    color: #595959; 
    padding: 10px 0 10px 0;
    margin: 0 0 10px 0;
    border-bottom: 1px solid #EEE !important;}

h1.title {font-size: 18px; line-height: 25px; color: #9CA6C0;}
h1.title a {font-size: 18px;}

h1.names {font-size: 13px; line-height: 20px; color: #BABABA;}
h1.names span:first-child {font-size: 18px; color: #595959; padding: 2px 5px;}

h1 > span.eng-name,
h1 > span.original-name {font-size: 15px; color: #757575; padding: 0 5px;}

h1.names .name:hover {text-shadow: none !important;}
h1 > span.eng-name:hover, h1.names .original-name:hover {
    text-shadow: none !important; color: #757575;}

/*============ TITLE - H2 ============ */

h2 {
    text-transform: uppercase;
    font-size: 20px;
    letter-spacing: .8px;}

h2.title {
    font-size: 18px; 
    margin: 0 0 10px 0;}

.table th h2 {
    font-size: 18px; 
    letter-spacing: .8px; 
    padding: 0;
    margin: 0;}

h2.zero-chapter {
    font-size: 15px; 
    font-weight: bold; 
    padding: 5px 0;
	margin: auto;}

/*============ TITLE - H3 ============ */

h3 {
    background: #F6F6F6;
    text-transform: uppercase; 
    font-size: 16px; 
    color: #595959; 
    font-weight: bold; 
    letter-spacing: .8px;
    margin: 10px 0;
    padding: 10px 0 10px 10px;
    border-radius: 2px;}

h3 a {font-weight: bold; margin: 0 0 0 5px; vertical-align: 1px;}

.b-chapter h4, .b-chapter h3, .b-chapter h3 p {
    margin: 0;
    padding: 0;
    background: none;
    text-transform: uppercase; 
    font-size: 18px; 
    color: #595959 !important;}

/* TITLE - H3: TABLE */

#mangaBox > div.leftContent > table > thead > tr > th:nth-child(1) > h3 {
    font-size: 15px;
    background: none;
    margin: 0;
    padding: 0;}

#mangaBox > div.leftContent > table > thead > tr > th:nth-child(1) > h3 > a:nth-child(1) > span {display: none;}

i.fa.fa-calendar-check, i.fa.fa-rss-square, i.fa.fa-clock.text-info {
    background: #FAFAFA;
	width: 22px;
    height: 22px;
    border-radius: 2px;
    border: 1px solid #EEE;
    color: #9CA6C0;
    font-size: 13px;
    padding: 4px 0 0 2px;
    vertical-align: middle;}

i.fa.fa-rss-square, i.fa.fa-clock.text-info {padding: 4px 0 0 1px;}

/*============ TITLE - H4 ============ */

h4 {
    font-size: 16px;
    font-weight: bold;
    letter-spacing: 0.8px;
    text-transform: uppercase;
    color: #595959;
	padding: 5px 0;
    margin: 5px 0;}

h4 a {
    font-size: 12px;
    font-weight: normal;
	margin: 0 3px;}

.chapter .sub-info h4 {
    margin: 10px 0;
    padding: 0;
    font-size: 17px;}

/*============ TITLE - H5 ============ */

h5 {font-size: 14px !important;
    font-weight: bold;
    letter-spacing: .8px;
    text-transform: uppercase;
    margin: 10px 0;
	color: #757575;}

.hide.hidden-xs.hidden-sm > h5,
.hide.hidden-xs.hidden-sm > h5 a {text-transform: uppercase; font-size: 13px !important;}

/*============ COVERS ============ */

.simple-tile img {
    box-shadow: none;
    width: 135px;
    height: auto;
    border: 1px solid #E8E8E8;
    border-radius: 4px;
	margin: 0;
    padding: 0;
	filter: saturate(0.8) opacity(0.8);}

.simple-tile .title .num {
    font-size: 14px;
	text-shadow: 0 0 4px #000;
    font-weight: bold;
    color: #FFF;}

.simple-tile .title {
    border-top: 1px solid rgba(255, 255, 255, .35);
    border-bottom-right-radius: 4px;
    border-bottom-left-radius: 4px;
    text-shadow: 0 0 4px #000;
    font-size: 11px;
    height: 60px;
    max-width: 133px;
    margin: -61px 0 0 1px;
    padding: 2px 6px;
    background-image: none;
    background: linear-gradient(to right, #acb1c4b3, #404758a6, #353b49de) 50% 0%;
    color: #FFF;
    letter-spacing: .4px !important;
    text-transform: uppercase;
    font-weight: normal;
    line-height: 1.2;
    word-spacing: 1px;
    opacity: .8;
	transition: all .3s linear !important;
    backdrop-filter: blur(1px);}

.simple-tile .title:hover {opacity: 1;}

.tiles .tile.for-sale .img, .simple-tile.for-sale img {box-shadow: none;}

.tiles-row .simple-tile .long-description {
    left: 11px;
    top: 1px;
    padding: 3px;}

/***/

.review-tiles .simple-tile .title {width: 128px; opacity: 1;}
.review-tiles .simple-tile .title:hover {opacity: 0.8;}

#mangaBox > div.leftContent > div.review-tiles.row > div:nth-child(even) > div.simple-tile > a:hover,
#mangaBox > div.leftContent > div.review-tiles.row > div:nth-child(odd) > div.simple-tile > a:hover {
    opacity: 1 !important;}

/***/

.tiles .tile .img {
    box-shadow: none;
    background: #FFF;
    border: 1px solid #E8E8E8;
	padding: 5px;
	border-radius: 4px;}

.tiles .tile div.img img {
    box-shadow: none !important;
    filter: opacity(0.8) saturate(0.3) brightness(0.9);
    transition: all .3s linear !important;
	border-radius: 4px;}

.tiles .tile div.img img:hover {filter: opacity(0.8);}

/***/

.vol-img img {
    box-shadow: none !important;
    border: 1px solid #E8E8E8;
    border-radius: 4px;
    transition: all .3s linear !important;
	filter: opacity(0.9) saturate(0.5);}

.vol-img img:hover {filter: opacity(0.8);}

/*============ RIGHT PANELS ============ */

.panel {
    font-size: 12px !important;
    letter-spacing: .8px;
    word-spacing: .8px;
    text-transform: uppercase;
    border-radius: 2px;
