style.css:
@-moz-document domain("golang.org") {
:root {
    --regular-text: #e3e3e3;
    --header-text: #ccd6e1;
    --button-background: #222;
    --button-border: #004b60;
}

html, body {
    background-color: black;
    color: var(--regular-text);
    display: block;
    margin-left: auto;
    margin-right: auto;
    width: 85%;
}

/*Some images*/
/*
List:
.Header-logo
.Hero-gopher
img.gopher
.Footer-gopher
*/
img {
    opacity: 0.8;
}

img:Hover {
    opacity: 1;
}

.HeroDownloadButton-image {
    filter: invert(90%);
    opacity: 1;
}

/*Header Menu*/
.HeaderSearch {
	border: 0.12rem solid #444;
	border-radius: 0.1875rem;
}

.HeaderSearch-input {
	background-color: #000;
    color: var(--regular-text);
}

.HeaderSearch-submit {
	background-color: #000;
	transition: background-color 200ms;
}

.HeaderSearch-icon {
	fill: #c6c6c6;
}

a, .exampleHeading .text, .expandAll {
	color: #009ec6;
}

.Header-menuItem, .Header-menuItem a:link, .Header-menuItem a:visited {
	color: var(--header-text) !important;
	text-align: center;
}

.Blog-when, .Footer-supportedBy, .Hero-description {
	color: #a6a6a6;
}

/*Articles*/
h1, h2, h3, h4 {
	color: var(--header-text) !important;
}

h2 {
	background: #24262b;
}

#sidebar {
	background: #171717;
}

#sidebar h2 {
	padding: 5px;
}

#content .title {
	padding: 5px;
}

a.downloadBox
{
    color: #eee;
    border: 1px solid var(--button-border);
    background: #252728;
}

table.codetable td, table.codetable th
{
    background: #252728;
    color: #eee;
}

table.codetable tr.highlight td
{
    background: #373a3c;
}

/*Code*/
pre {
	background: #171717;
}

pre .comment {
	color: #00a200;
}

pre .ln {
	color: #fff;
	background: #171717;
}

/*Playground embedded*/
div.play .input, div.play .output {
	border: #242424;
}

div.play .input, div.play .input textarea, div.play .output, div.play .output pre {
	background: #1e1e1e;
	color: #d0e0f0;
}

.output .system {
	color: #a4a4a4;
}

/*Try Playground*/
.Playground-selectExample {
	background-color: var(--button-background);
	border: 0.0625rem solid var(--button-border);
	font-size: 16px;
	height: 2.375rem;
	margin: 0 0 0.5rem 0;
	width: 100%;
}

.Playground-inputContainer, .Playground-input, .Playground-outputContainer, .Playground-output {
	background: #1e1e1e;
	color: #d0e0f0;
}

#banner {
	position: absolute;
	left: 0;
	right: 0;
	top: 0;
	height: 50px;
	background-color: #3d3f42;
}

/*Buttons*/
.Button--primary, .Button--primary:link, .Button--primary:visited {
	border: 0.0625rem solid var(--button-border);
}

.Button, .Button:link, .Button:visited {
	background-color: var(--button-background);
	box-shadow: 0 2px 5px rgba(255, 255, 255, .2);
	color: #00a0c8;
}
}

@-moz-document domain("go.dev") {
:root{
    --regular-text: #e3e3e3;
    --h3: #d3d9dd;
    --yellow-bg: #1c1b17;
}

html, body, .Site-content {
    background-color: black;
    color: var(--regular-text);
}

a, a:link, a:visited {
	color: #009ec6;
}

.Banner {
	background-color: #33322c;
}

/*Header*/
.Site-header, .bluebg {
	background: #004151;
}

/*Footer*/
.Footer {
	background-color: #0e131a;
}

.Site-footer {
	border: none;
}

/*Main page*/
.WhyGo, .WhoUses {
	background-color: #262626;
    color: var(--regular-text);
}

.WhoUses-caseStudy a:link:hover, .WhoUses-caseStudy a:visited:hover {
	color: var(--h3);
}

.UseCase-logo img {
    filter: invert(100%);
}

.LearnGo-subHeader h3 {
	color: var(--h3);
}

.LearnGo, .UseCases {
    color: var(--regular-text);
	background-color: #17171a;
}

/*Courses*/
.LearnGo-courseImage img, .Learn-tileThumbnail img {
	max-height: 80%;
	max-width: 80%;
}

.LearnGo-courseImage {
	background: none;
	box-shadow: 0 0.125rem 0.5rem 0 rgba(0, 0, 0, .22);
}

.Learn-tileThumbnail {
	background-color: #aaa;
	box-shadow: 0 0.125rem 0.5rem 0 rgba(255, 255, 255, .22);
}

.LearnGo-courseImage:Hover, .Learn-tileThumbnail:Hover {
	background-color: #eee;
}

/*Events*/
.LearnGo-events {
	background-color: #090909;
	border: 0.0625rem solid rgba(117, 117, 117, .19);
	box-shadow: 0 0.125rem 0.8125rem 0 rgba(0, 0, 0, .26);
}

.Learn-eventThumbnail--noimage, .LearnGo-eventThumbnail--noimage {
	background: none;
}

.LearnGo-eventThumbnail img, .Learn-eventThumbnail img {
    opacity: 0.7;
}

.LearnGo-eventThumbnail:Hover img, .Learn-eventThumbnail:Hover img {
    opacity: 1;
}

.LearnGo-eventDate p, .Learn-eventDate p {
	color: #a2a6aa;
}

.LearnGo-eventItem + .LearnGo-eventItem, .Learn-eventItem + .Learn-eventItem {
	border-top: 0.0625rem solid #333;
}

/*learn.go.dev*/
.Learn-gettingStarted {
	background-color: var(--yellow-bg);
}

.Learn-quickstart a, .Learn-quickstart a:visited, .Learn-quickstart a:hover {
	color: #c6cdd5;
}

.Learn-quickstartThumbnail {
	background: none;
    border: none;
}

.Learn-quickstartThumbnail:Hover {
	background: #eee;
	box-shadow: 0 0.125rem 0.8125rem 0 rgba(0, 0, 0, .26);
}

.Learn-viewMoreButton, .Learn-viewMoreButton:visited, .Learn-viewMoreButton:hover, .Learn-viewMoreButton {
	background-color: #141515;
	border: 0.0625rem solid #00add8;
	box-shadow: 0 0.125rem 0.3125rem 0 rgba(0, 0, 0, 0.2);
}

.Learn-inPerson:nth-child(1), .Learn-inPerson:nth-child(2) {
	border-top: 0.0625rem solid #5b5b5b;
}

.Learn-inPerson {
	border-bottom: 0.0625rem solid #5b5b5b;
}

.Learn-events {
	border-bottom: 0.0625rem solid #5b5b5b;
	border-top: 0.0625rem solid #5b5b5b;
}

/*solutions*/
.Solutions-headline {
	background-color: var(--yellow-bg);
}

.Solutions-caseStudy:hover, .Solutions-useCase:hover {
	box-shadow: 0 0.125rem 0.5rem 0 rgba(0, 0, 0, 0.22);
}

.Solutions-caseStudyTitle, .Solutions-useCaseTitle {
    color: var(--regular-text);
}

.Solutions-caseStudy a:link, .Solutions-caseStudy a:visited, .Solutions-useCase a:link, .Solutions-useCase a:visited {
	color: var(--regular-text);
}

.Solutions-caseStudyLogo {
    border-radius: 0.25rem;
	background-color: #666;
	box-shadow: 0 0 3px #eee;
}

.Solutions-caseStudyLogo:Hover {
	background-color: #eee;
}

/*Article*/
.Article {
	color: #eee;
}

.Article p, .Article ul, .Article ol {
	color: var(--regular-text);
}
}

@-moz-document domain("pkg.go.dev") {
:root {
	--gray-1: #fafafa;
	--gray-2: #f0f1f2;
	--gray-3: #dcdee0;
	--gray-4: #c6c8ca;
	--gray-5: #aaacae;
	--gray-6: #848688;
	--gray-7: #6e7072;
	--gray-8: #555759;
	--gray-9: #3e4042;
	--gray-10: #202224;
	--turq-light: #007d9c;
	--turq-med: #00add8;
	--turq-text: #5dc9e2;
	--blue: #3ba3bb;
	--green: #00bdb6;
	--pink: #e1386c;
	--purple: #8343c4;
	--slate: #5a83ac;
	--white: black;
    --black: white;
	--yellow: #fddd00;
}

/*Header*/
.Site-header--dark input[type="text"].Header-searchFormInput, .Site-header--dark input[type="text"].Header-searchFormInput::placeholder {
	color: var(--black);
}

.Site-header--dark .Header-searchFormSubmitIcon {
	fill: var(--black);
}

.Site-header--dark .Header-menuItem a:link, .Site-header--dark .Header-menuItem a:visited {
	color: var(--black);
}

/*Footer*/
.Footer-listItem a:link, .Footer-listItem a:visited {
	color: var(--black);
}

.SearchForm {
	background-color: black;
}

/*Overview README*/
.Overview-readmeContent pre {
	background-color: #171717;
}

.Overview-readmeContent img {
	max-width: 100%;
	box-sizing: initial;
	background: none;
}

/*Code*/
.Documentation code, .Documentation pre {
	border: 0.0625rem solid #333;
}

.Documentation pre .comment {
	color: #00a200;
}
}

@-moz-document domain("godoc.org") {
:root {
    --regular-text: #e3e3e3;
}

html, body {
    background-color: black;
    color: var(--regular-text);
    display: block;
    margin-left: auto;
    margin-right: auto;
}

img {
    opacity: 0.8;
}

img:Hover {
    opacity: 1;
}

.form-control {
	color: #bbb;
	background-color: #000;
	border: 1px solid #333;
	-webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
	-o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
	transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}

a, .exampleHeading .text, .expandAll {
	color: #009ec6;
}

.navbar-default, #x-footer {
	background-color: hsl(209, 5%, 6%);
	border-color: hsl(209, 5%, 6%);
}

.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus {
	background-color: hsl(209, 5%, 6%);
}

.jumbotron {
	background-color: #222;
}

.btn-default:hover, .btn-default:focus, .btn-default.focus, .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
	background-color: #222 !important;
}

.btn-default {
	color: #eee !important;
	background-color: #000 !important;
	border: 0.5px solid #333 !important;
}

.banner {
	background-color: #2b2b24;
}

code {
	color: #eaeaea;
}

.text-muted {
	color: #999;
}

#x-projnav {
	background-color: #333;
}

.panel-default {
	border-color: #333;
    background-color: #151515;
}

.panel-default > .panel-heading + .panel-collapse > .panel-body {
	border-top-color: #333 !important;
}

.panel-default > .panel-heading {
	background-color: transparent;
}

.panel-default > .panel-heading {
	background-color: #151515;
	border-color: #333 !important;
}

.table > thead > tr > th {
	border-bottom: 2px solid #333;
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
	border-top: 1px solid #333;
}

.highlighted {
    background-color: #202012;
}

/*Code*/
pre {
    color: #eee;
    border-color: #333;
	background: #171717;
}

pre .com {
	color: #00a200;
}

/*Playground embedded*/
div.play .input, div.play .output {
	border: #242424;
}

div.play .input, div.play .input textarea, div.play .output, div.play .output pre {
	background: #1e1e1e;
	color: #d0e0f0;
}

.output .system {
	color: #a4a4a4;
}
}

@-moz-document domain("play.golang.org") {
#wrap, #about {
	background: #1e1e1a;
}

#code {
	color: #fff;
}

input[type="button"], #importsBox {
	box-shadow: 0px 0px 1px #447eee;
	background: #1a2948;
	color: #fff;
}

#output .system, #output .loading {
	color: #a4a4a4;
}

pre {
	background: #171717;
    padding: 5px;
}

#shareURL {
	border: none;
    box-shadow: 0px 0px 3px #447eee;
	background: #171717;
	color: #eee;
    height: 27px;
}
}

@-moz-document url-prefix("https://tour.golang.com"), domain("go-tour-ru-ru.appspot.com"), domain("go-tour-br.appspot.com"), domain("tour.go-zh.org"), domain("go-tour-zh-tw.appspot.com"), domain("go-tour-cz.appspot.com"), domain("go-tour-fr.appspot.com"), domain("go-tour-de.appspot.com"), domain("go-tour-id2.appspot.com"), domain("go-tour-ita.appspot.com"), domain("go-tour-jp.appspot.com"), domain("go-tour-th.appspot.com"), domain("go-tour-turkish.appspot.com"), domain("go-tour-ua.appspot.com"), domain("go-tour-uz.appspot.com") {
html, body {
    background-color: black;
    color: white;
}

.container {
	background: #2b2b2b;
}

.lesson {
	background: #35383c;
}

h1, h2, h3, h4, .module-title {
	color: #ccd6e1;
}

h2 {
    padding: 5px;
}

.container a, a, .exampleHeading .text, .expandAll {
	color: #009ec6;
}

/*left*/
#left-side {
	background-color: #000;
}

pre, code {
	color: #fafafa;
	background-color: #333;
}

.top-bar {
	color: var(--regular-text);
	background: #1e1e1a;
}

.slide-content {
	background: #000;
}

.module-bar {
	background-color: #333;
}

.nav {
	fill: #396fdb;
}

/*right*/
#right-side {
	background: #000;
}

/*Code*/
#file-editor {
	filter: invert(100%);
	background-color: #fafafa;
}

.CodeMirror-scroll, .CodeMirror-sizer, .CodeMirror-gutter, .CodeMirror-gutters, .CodeMirror-linenumber,
#file-editor .CodeMirror-lines, #file-editor .CodeMirror-gutters {
	background-color: #fafafa;
	color: #0d0d0b;
}

.CodeMirror-wrap pre {
	color: #0d0d0b;
}

.CodeMirror-gutters {
	border-right: 1px solid#0d0d0b;
}

.cm-s-default .cm-string {
	color: #db1414;
}

.cm-s-default .cm-keyword {
	color: #b700d0;
}

.cm-s-default .cm-number {
	color: #22d78e;
}

div[horizontal-slide], div[vertical-slide] {
	background: #111213;
}

#explorer {
	background: #333;
	color: #fafafa;
}

.menu-button.active {
	background: #000;
    color: #4baae3;
}

.menu-button:hover:not(.active) {
	border: 1px solid #202020;
	background-color: #333;
    color: #fff;
}

.menu-button {
	background: #000;
    color: #fff;
}

.relative-content {
	background: #000;
}

#file-menu {
	background: #333;
}

#run:not(:active), #kill:not(:active) {
	background-color: #000;
	color: #fff;
}

#run:hover:not(:active), #kill:hover:not(:active) {
	background-color: #396fdb;
	color: #fff;
}

/*Output*/
.output > pre {
	background: #333;
}

.output {
	background-color: #8c8c8c;
}

.output:Hover {
	background-color: #ddd;
}

/*Table of contents*/
.toc-module {
	color: #396fdb;
	background: black;
}

.toc span, .toc a {
	background-color: #333;
    color: #fff;
}

.toc-page {
	background: #333;
	color: #fff;
	padding-left: 4px;
	display: list-item;
}

.toc {
	background: black;
	color: #e0ebf5;
	border-left: 4px solid black;
	border-bottom: 4px solid black;
}
}
