style.css:
::-webkit-scrollbar {
  display: none;
}

ytm-app.sticky-player ytm-pivot-bar-renderer {
    box-shadow: 0px 3.75px 11px rgba(0, 0, 0, 0.19), 0px 0.5px 5px rgba(0, 0, 0, 0.039);
}

.pivot-bar-item-tab {
    transition: color 0.3s;
}

.pivot-bar-item-tab:hover {
    color: red;
    cursor: pointer;
}

@media only screen and (min-width: 700px) {
  div div ytm-section-list-renderer {    
    width: 75%;    
    margin: auto;
  }
}

div.menu-content, dialog.dialog {
    border-color: #ffffff;
    border-width: 4px;
    border-style: solid;
    border-radius: 4px;
    box-shadow: 0px 0.634032px 0.996336px rgba(0, 0, 0, 0.184658), 0px 1.75302px 2.75474px rgba(0, 0, 0, 0.265), 0px 4.22059px 6.63236px rgba(0, 0, 0, 0.345342), 0px 14px 22px rgba(0, 0, 0, 0.53);
}

button.menu-item-button {
    background-color: #ffffff;
    transition: background-color 0.3s;
}

button.menu-item-button:hover,
a.menu-item-button:hover {
    background-color: #f0f0f0;
}

a.menu-item-button {
    background-color: #ffffff;
    transition: background-color 0.3s;
}

button.icon-button.topbar-menu-button-avatar-button c3-icon svg {
    transition: background-color 0.3s;
}

button.icon-button.topbar-menu-button-avatar-button c3-icon svg:hover {
    background-color: rgba(240, 240, 240, 0.49);
    border-style: solid;
    border-color: #f0f0f0;
    border-width: 0;
    border-radius: 300px;
}

/* dark mode */

@media (prefers-color-scheme: dark) {
    
    [aria-pressed="true"]  {
        color: #086bff !important;
    }

    input.searchbox-input.title, 
    a.slim-owner-icon-and-title, 
    textarea.comment-simplebox-reply,
    div.slim-video-metadata-header-content,
    c3-icon.undefined svg path,
    button.icon-button c3-icon svg,
    div.slim-video-metadata-description,
    span.metadata-row-title,
    h4.channel-list-sub-menu-avatar-title.subhead.small-text,
    a.compact-link-endpoint,
    c3-icon.compact-link-icon svg path,
    a.multi-page-menu-system-link, 
    button.multi-page-menu-system-link,
    h1.ytm-simple-menu-header-title.simple-menu-header-Accounts,
    h1.ytm-simple-menu-header-title.simple-menu-header-Account,
    div.compact-link-metadata, 
    ytm-account-item-section-renderer,
    h2.slim-video-metadata-title,
    div.button-renderer-text span,
    div.button-renderer-text,
    h4.compact-media-item-headline span,
    div.subhead,
    h3.ytm-autonav-title,
    div.ytm-autonav-toggle-description,
    p.comment-text.user-text,
    div.comment-details.secondary-text.cbox,
    div.dialog-header h2,
    button.playlist-add-to-option.secondary-text,
    .add-to-playlist-button,
    .comment-section-header-text,
    button.title.search-bar-text,
    span.secondary-text span,
    [aria-selected="false"],
    [aria-pressed="false"],
    [aria-label="Share"],
    [aria-label="Save to playlist"],
    [aria-label="Report"],
    [aria-controls],
    div.large-media-item-info.cbox,
    div.active-account-name.cbox,
    h1.title.mobile-topbar-title,
    h1.c4-tabbed-header-title,
    a.scbrr-tab.center,
    h4.compact-media-item-headline,
    div.user-text,
    ytm-channel-about-metadata-renderer.item div,
    div.ytm-item-section-header-title,
    h3.details span{
        color: white !important;
    }
    
    
    button.menu-item-button, 
    a.menu-item-button, 
    dialog.dialog,
    div.menu-content,
    div.page-container ytm-browse ytm-c4-tabbed-header-renderer,
    div.scbrr-tabs.cbox{
      background-color: #202020;
    }
    
    body, html {
        background-color: #181818 !important;
    }

    a.share-target-link {
        color: #086bff;
    }
    
    div.menu-content, dialog.dialog {
        border-color: #202020;
        border-width: 4px;
        border-style: solid;
        border-radius: 4px;
        box-shadow: 0px 0.634032px 0.996336px rgba(0, 0, 0, 0.184658), 0px 1.75302px 2.75474px rgba(0, 0, 0, 0.265), 0px 4.22059px 6.63236px rgba(0, 0, 0, 0.345342), 0px 14px 22px rgba(0, 0, 0, 0.53);
    }
    
    a.sharing-renderer-url {
        border-radius: 4px;
    }
    
    
    button.menu-item-button,
    a.menu-item-button,
    c3-icon.mobile-topbar-logo.ringo-logo svg,
    ytm-option-selectable-item-renderer c3-material-radio label, 
    div.report-form-footer {
        color: #ffffff;
    }
    
    button.menu-item-button:hover,
    a.menu-item-button:hover {
        background-color: #383838;
    }
    
    #header-bar .cbox,
    ytm-app.sticky-player ytm-pivot-bar-renderer {
        background-color: black;
    }
    
    ytm-multi-page-menu-renderer div ytm-privacy-tos-footer-renderer {
        color: white;
        background-color: black;
    }
    
    div.subscribe-button-count.secondary-text {
        color: #999999;
    }
    
}
