style.css:
@-moz-document domain("192.168.1.88") {

/* header */
#header { background-color: #101010 !important }
#headerlogo { filter:brightness(0.9) }
.x-workspace-navigation-toolbar.x-toolbar-default, .x-panel-header-default, .x-tool-tool-el { background-color: #202020 !important; border-bottom-width: 2px; border-color: #909090 !important }

/* body */
.login-page { background-color: #303030 !important }
.x-window-header-default { background-color: #202020 !important; border-color: #202020 !important }
.x-toolbar-footer { background-color: #404040 !important }
body.x-border-layout-ct, div.x-border-layout-ct { background-color: #404040 !important }
.x-collapse-el, .x-layout-split-left { background-color: #303030 !important }
.x-menu-default, .x-boundlist { background-color: #404040 !important; border-color: #505050 !important }
.x-menu-body-default { background-color: #303030 !important }
.x-window-body-default { background-color: #303030 !important; color: #e0e0e0 !important}

/* buttons */
.x-toolbar-text-default, .x-boundlist-item { color: #d0d0d0 !important }
.x-btn-default-toolbar-small, .x-form-spinner-default, .x-form-trigger-default { background-color: #303030 !important; border-color: #404040 !important }
.x-toolbar-separator-horizontal { border-left-color: #707070 !important; border-right-color: #909090 !important }
.x-btn-inner-default-toolbar-small, .x-tool-tool-el, .x-tool-img, .x-tool-collapse-left, .x-btn-arrow-el  { color: #c0c0c0 !important }
.x-form-trigger-wrap-default { border-color: #606060 !important }
.x-btn-default-small { background-color: #253c5e !important; border-color: #054070 !important }
.x-color-boolean-false, .x-color-grey, .x-form-fieldinfo { color: #808080 !important }
.x-color-boolean-true, .x-color-good { color: #0c8914 !important }
.x-form-cb, .x-form-checkbox-default { filter:brightness(0.8) !important }
.x-btn-icon-el, .x-btn-icon-el-default-toolbar-small, .thumb-icon, .x-menu-item-icon-default { filter:invert(0.8) !important }
.x-menu-item-icon-default { color: #000 !important }
.x-tool-collapse-left::before, .x-btn-split-right::after { color: #c0c0c0 !important}
.x-btn-wrap-default-toolbar-small.x-btn-split-right > .x-btn-button::after { border-right: 1px solid #404040 !important }

/* main panel */
.x-mask{ background-color: #18181833 !important}
.x-toolbar-default { background-color: #353535 !important }
.x-panel-body-default { background-color: #353535 !important; border-color: #303030 !important; color: #c0c0c0 !important }
.x-panel-header-title-default { color: #d0d0d0 !important }
.x-splitter-vertical { background-color: #202020 !important}
.x-splitter-vertical.x-column { background-color: #353535 !important}
.x-tree-icon, .x-tree-icon-custom, .x-tree-elbow-img, .x-tree-elbow-plus, .x-tree-expander { filter:invert(0.9) !important }
.x-item-over { background-color: #505050 !important }

/* grid */
.x-column-header, .x-box-aboutbox, .x-box-aboutbox a { background-color: #202020 !important; color: #c0c0c0 !important }
.x-column-header-over, .x-column-header-trigger, .x-column-header-open { background-color: #303030 !important }
.x-grid-item { background-color: #202020 !important }
.thumb-wrap, .x-grid-group-title::before { color: #d5d5d5 !important }
.x-grid-item, .thumb-wrap { color: #d5d5d5 !important }
.x-grid-item-over, .x-menu-item-text-default { color: #d5d5d5 !important }
.x-grid-item-focused { color: #f5f5f5 !important; border-color: #d5d5d5 !important }
.x-grid-item-alt { background-color: #282828 !important }
.x-grid-cell { border-color: #909090 !important }
.x-grid-group-title, .x-grid-cell-inner { color: #d5d5d5 !important }
.x-grid-item-selected {background-color:#224782 !important}

/* tabbar */
.x-tab-bar-default, .x-grid-header-ct { background-color: #202020 !important }
.x-tab-inner-default { color: #e0e0e0 !important }
.x-tab.x-tab-active.x-tab-default { background-color: #353535 !important }
.x-grid-header-ct { border-color: #909090 !important }
.x-column-header { border-right-color: #909090 !important }

/* text area */
.x-form-text-default { background-color: #404040 !important; color: #c0c0c0 !important }
.x-form-item-label-default, .x-form-cb-label-default, .x-form-display-field-default {color: #d0d0d0 !important }

/* fieldset */
.x-fieldset-header-text { color: #d0d0d0 !important }
.x-fieldset  { background-color: #303030 !important }
.x-fieldset-default { border-color: #454545 !important }

/* group */
.x-grid-group-hd { background-color: #202020 !important; border-color: #909090 !important }

/* progressbar */
.x-progres-bar { border-color: #909090 !important }
.x-progress-default { background-color: #505050 !important; border-color: #909090 !important }
.x-progress-text { color: #c0e0c0 !important }
.x-progress-text-back { color: #d0d0d0 !important }
.x-progress-bar-default { background-color: #0c8914 !important }

/* selected items */
.x-tab-focus { outline-color: #909090 !important }
.x-grid-item-focused .x-grid-cell-inner:before{border:1px solid #909090 !important}
.x-boundlist-selected, .x-window-default  { border-color: #144287 !important }
.x-datepicker-selected, .x-btn-focus, .x-menu-item-focus, .x-boundlist-item-over, .x-boundlist-selected { background-color: #224782 !important }
.x-tagfield-item-selected, .x-menu-item-active {background-color:#224782 !important; border-color:#144287 }

}
